INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6038, 'nvi', 'Josué', 'js', 9, 1, 'E souberam disso todos os reis que viviam a oeste do Jordão, nas montanhas, na Sefelá e em todo o litoral do mar Grande até o Líbano. Eram os reis dos hititas, dos amorreus, dos cananeus, dos ferezeus, dos heveus e dos jebuseus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6039, 'nvi', 'Josué', 'js', 9, 2, 'Eles se ajuntaram para guerrear contra Josué, contra Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6040, 'nvi', 'Josué', 'js', 9, 3, 'Contudo, quando os habitantes de Gibeom souberam o que Josué tinha feito com Jericó e Ai,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6041, 'nvi', 'Josué', 'js', 9, 4, 'recorreram a um ardil. Enviaram uma delegação, com jumentos carregados de sacos gastos e vasilhas de couro velhas, rachadas e remendadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6042, 'nvi', 'Josué', 'js', 9, 5, 'Os homens calçavam sandálias gastas e remendadas e vestiam roupas velhas. Todos os pães do suprimento deles estavam secos e esmigalhados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6043, 'nvi', 'Josué', 'js', 9, 6, 'Foram a Josué, ao acampamento de Gilgal, e disseram a ele e aos homens de Israel: "Viemos de uma terra distante. Queremos que façam um acordo conosco".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6044, 'nvi', 'Josué', 'js', 9, 7, 'Os israelitas disseram aos heveus: "Talvez vocês vivam perto de nós. Como poderemos fazer um acordo com vocês? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6045, 'nvi', 'Josué', 'js', 9, 8, '"Somos seus servos", disseram a Josué. Josué, porém, perguntou: "Quem são vocês? De onde vocês vêm? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6046, 'nvi', 'Josué', 'js', 9, 9, 'Ele responderam: "Seus servos vieram de uma terra muito distante por causa da fama do Senhor, do seu Deus. Pois ouvimos falar dele, de tudo o que fez no Egito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6047, 'nvi', 'Josué', 'js', 9, 10, 'e de tudo o que fez aos dois reis dos amorreus a leste do Jordão: Seom, rei de Hesbom, e Ogue, rei de Basã, que reinava em Asterote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6048, 'nvi', 'Josué', 'js', 9, 11, 'E os nossos líderes e todos os habitantes de nossa terra nos disseram: ‘Juntem provisões para a viagem, vão encontrar-se com eles e digam-lhes: Somos seus servos, façam um acordo conosco’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6049, 'nvi', 'Josué', 'js', 9, 12, 'Este nosso pão estava quente quando o embrulhamos em casa no dia em que saímos de viagem para cá. Mas vejam como agora está seco e esmigalhado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6050, 'nvi', 'Josué', 'js', 9, 13, 'Estas vasilhas de couro que enchemos de vinho eram novas, mas agora estão rachadas. E as nossas roupas e sandálias estão gastas por causa da longa viagem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6051, 'nvi', 'Josué', 'js', 9, 14, 'Os israelitas examinaram as provisões dos heveus, mas não consultaram o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6052, 'nvi', 'Josué', 'js', 9, 15, 'Então Josué fez um acordo de paz com eles, garantindo poupar-lhes a vida, e os líderes da comunidade o ratificaram com juramento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6053, 'nvi', 'Josué', 'js', 9, 16, 'Três dias depois de fazerem o acordo com os gibeonitas, os israelitas souberam que eram vizinhos e que viviam perto deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6054, 'nvi', 'Josué', 'js', 9, 17, 'Por isso partiram de viagem, e três dias depois chegaram às cidades dos heveus, que eram Gibeom, Quefira, Beerote e Quiriate-Jearim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6055, 'nvi', 'Josué', 'js', 9, 18, 'Mas não os atacaram, porque os líderes da comunidade lhes haviam feito um juramento em nome do Senhor, o Deus de Israel. Toda a comunidade, porém, queixou-se contra os líderes,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6056, 'nvi', 'Josué', 'js', 9, 19, 'que lhes responderam: "Fizemos a eles o nosso juramento em nome do Senhor, o Deus de Israel; por isso não podemos tocar neles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6057, 'nvi', 'Josué', 'js', 9, 20, 'Todavia, nós os trataremos assim: Vamos deixá-los viver, para que não caia sobre nós a ira divina por quebrarmos o juramento que lhes fizemos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6058, 'nvi', 'Josué', 'js', 9, 21, 'E acrescentaram: "Eles ficarão vivos, mas serão lenhadores e carregadores de água para toda a comunidade". E assim se manteve a promessa dos líderes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6059, 'nvi', 'Josué', 'js', 9, 22, 'Então Josué convocou os gibeonitas e disse: "Por que vocês nos enganaram dizendo que viviam muito longe de nós, quando na verdade vivem perto?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6060, 'nvi', 'Josué', 'js', 9, 23, 'Agora vocês estão debaixo de maldição: Nunca deixarão de ser escravos, rachando lenha e carregando água para a casa do meu Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6061, 'nvi', 'Josué', 'js', 9, 24, 'Eles responderam a Josué: "Os seus servos ficaram sabendo como o Senhor, o seu Deus, ordenou que o seu servo Moisés lhes desse toda esta terra e que destruísse todos os seus habitantes da presença de vocês. Tivemos medo do que poderia acontecer conosco por causa de vocês. Por isso agimos assim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6062, 'nvi', 'Josué', 'js', 9, 25, 'Estamos agora nas suas mãos. Faça conosco o que lhe parecer bom e justo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6063, 'nvi', 'Josué', 'js', 9, 26, 'Josué então os protegeu e não permitiu que os matassem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6064, 'nvi', 'Josué', 'js', 9, 27, 'Mas naquele dia fez dos gibeonitas lenhadores e carregadores de água para a comunidade e para o altar do Senhor, no local que o Senhor escolhesse. É o que eles são até hoje.');
