INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6510, 'nvi', 'Juízes', 'jz', 1, 1, 'Depois da morte de Josué, os israelitas perguntaram ao Senhor: "Quem de nós será o primeiro a atacar os cananeus? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6511, 'nvi', 'Juízes', 'jz', 1, 2, 'O Senhor respondeu: "Judá será o primeiro; eu entreguei a terra em suas mãos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6512, 'nvi', 'Juízes', 'jz', 1, 3, 'Então os homens de Judá disseram aos seus irmãos de Simeão: "Venham conosco ao território que nos foi designado por sorteio, e lutemos contra os cananeus. Iremos com vocês para o território que lhes foi dado". E os homens de Simeão foram com eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6513, 'nvi', 'Juízes', 'jz', 1, 4, 'Quando os homens de Judá atacaram, o Senhor entregou os cananeus e os ferezeus nas mãos deles, e eles mataram dez mil homens em Bezeque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6514, 'nvi', 'Juízes', 'jz', 1, 5, 'Foi lá que encontraram Adoni-Bezeque, lutaram contra ele e derrotaram os cananeus e os ferezeus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6515, 'nvi', 'Juízes', 'jz', 1, 6, 'Adoni-Bezeque fugiu, mas eles o perseguiram e o prenderam, e lhe cortaram os polegares das mãos e dos pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6516, 'nvi', 'Juízes', 'jz', 1, 7, 'Então Adoni-Bezeque disse: "Setenta reis com os polegares das mãos e dos pés cortados apanhavam migalhas debaixo da minha mesa. Agora Deus me retribuiu por aquilo que lhes fiz". Eles o levaram para Jerusalém, onde morreu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6517, 'nvi', 'Juízes', 'jz', 1, 8, 'Os homens de Judá atacaram também Jerusalém e a conquistaram. Mataram seus habitantes ao fio da espada e a incendiaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6518, 'nvi', 'Juízes', 'jz', 1, 9, 'Depois disso eles desceram para lutar contra os cananeus que viviam na serra, no Neguebe e na Sefelá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6519, 'nvi', 'Juízes', 'jz', 1, 10, 'Avançaram contra os cananeus que viviam em Hebrom, anteriormente chamada Quiriate-Arba, e derrotaram Sesai, Aimã e Talmai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6520, 'nvi', 'Juízes', 'jz', 1, 11, 'Dali avançaram contra o povo que morava em Debir, anteriormente chamada Quiriate-Sefer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6521, 'nvi', 'Juízes', 'jz', 1, 12, 'E disse Calebe: "Darei minha filha Acsa em casamento ao homem que atacar e conquistar Quiriate-Sefer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6522, 'nvi', 'Juízes', 'jz', 1, 13, 'Otoniel, filho de Quenaz, irmão mais novo de Calebe, conquistou a cidade; por isso Calebe lhe deu sua filha Acsa por mulher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6523, 'nvi', 'Juízes', 'jz', 1, 14, 'Um dia, quando já vivia com Otoniel, ela o persuadiu a pedir um campo ao pai dela. Assim que ela desceu do jumento, Calebe lhe perguntou: "O que você quer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6524, 'nvi', 'Juízes', 'jz', 1, 15, 'Ela respondeu: "Dê-me um presente. Já que o senhor me deu terras no Neguebe, dê-me também fontes de água". Assim Calebe lhe deu as fontes superiores e as inferiores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6525, 'nvi', 'Juízes', 'jz', 1, 16, 'Os descendentes do sogro de Moisés, o queneu, saíram da Cidade das Palmeiras com os homens de Judá e passaram a viver entre o povo do deserto de Judá, no Neguebe, perto de Arade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6526, 'nvi', 'Juízes', 'jz', 1, 17, 'Depois os homens de Judá foram com seus irmãos de Simeão e derrotaram os cananeus que viviam em Zefate, e destruíram totalmente a cidade. Por essa razão ela foi chamada Hormá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6527, 'nvi', 'Juízes', 'jz', 1, 18, 'Os homens de Judá também conquistaram Gaza, Ascalom e Ecrom, com os seus territórios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6528, 'nvi', 'Juízes', 'jz', 1, 19, 'O Senhor estava com os homens de Judá. Eles ocuparam a serra central, mas não conseguiram expulsar os habitantes dos vales, pois estes possuíam carros de guerra feitos de ferro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6529, 'nvi', 'Juízes', 'jz', 1, 20, 'Conforme Moisés havia prometido, Hebrom foi dada a Calebe, que expulsou de lá os três filhos de Enaque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6530, 'nvi', 'Juízes', 'jz', 1, 21, 'Já os benjamitas deixaram de expulsar os jebuseus que estavam morando em Jerusalém. Os jebuseus vivem ali com os benjamitas até o dia de hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6531, 'nvi', 'Juízes', 'jz', 1, 22, 'Os homens das tribos de José, por sua vez, atacaram Betel, e o Senhor estava com eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6532, 'nvi', 'Juízes', 'jz', 1, 23, 'Enviaram espias a Betel, anteriormente chamada Luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6533, 'nvi', 'Juízes', 'jz', 1, 24, 'Quando os espias viram um homem saindo da cidade disseram-lhe: "Mostre-nos como entrar na cidade, e nós lhe pouparemos a vida".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6534, 'nvi', 'Juízes', 'jz', 1, 25, 'Ele mostrou como entrar, e eles mataram os habitantes da cidade ao fio da espada, mas pouparam o homem e toda a sua família.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6535, 'nvi', 'Juízes', 'jz', 1, 26, 'Ele foi, então, para a terra dos hititas, onde fundou uma cidade e lhe deu o nome de Luz, que é o seu nome até o dia de hoje.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6536, 'nvi', 'Juízes', 'jz', 1, 27, 'Manassés, porém, não expulsou o povo de Bete-Seã nem o de Taanaque nem o de Dor nem o de Ibleã nem o de Megido, nem tampouco o dos povoados ao redor dessas cidades, pois os cananeus estavam decididos a permanecer naquela terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6537, 'nvi', 'Juízes', 'jz', 1, 28, 'Quando Israel se tornou forte, impôs trabalhos forçados aos cananeus, mas não os expulsou completamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6538, 'nvi', 'Juízes', 'jz', 1, 29, 'Efraim também não expulsou os cananeus que viviam em Gezer, mas os cananeus continuaram a viver entre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6539, 'nvi', 'Juízes', 'jz', 1, 30, 'Nem Zebulom expulsou os cananeus que viviam em Quitrom e em Naalol, mas estes permaneceram entre eles, e foram submetidos a trabalhos forçados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6540, 'nvi', 'Juízes', 'jz', 1, 31, 'Nem Aser expulsou os que viviam em Aco, Sidom, Alabe, Aczibe, Helba, Afeque e Reobe,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6541, 'nvi', 'Juízes', 'jz', 1, 32, 'e, por esse motivo, o povo de Aser vivia entre os cananeus que habitavam naquela terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6542, 'nvi', 'Juízes', 'jz', 1, 33, 'Nem Naftali expulsou os que viviam em Bete-Semes e em Bete-Anate; mas o povo de Naftali também vivia entre os cananeus que habitavam a terra, e aqueles que viviam em Bete-Semes e em Bete-Anate passaram a fazer trabalhos forçados para eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6543, 'nvi', 'Juízes', 'jz', 1, 34, 'Os amorreus confinaram a tribo de Dã à serra central, não permitindo que descessem ao vale.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6544, 'nvi', 'Juízes', 'jz', 1, 35, 'E os amorreus igualmente estavam decididos a resistir no monte Heres, em Aijalom e em Saalbim, mas, quando as tribos de José ficaram mais poderosas, eles também foram submetidos a trabalhos forçados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6545, 'nvi', 'Juízes', 'jz', 1, 36, 'A fronteira dos amorreus ia da subida de Acrabim até Selá, e mais adiante.');
