INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6830, 'nvi', 'Juízes', 'jz', 11, 1, 'Jefté, o gileadita, era um guerreiro valente. Sua mãe era uma prostituta; seu pai foi Gileade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6831, 'nvi', 'Juízes', 'jz', 11, 2, 'A mulher de Gileade também lhe deu filhos, que quando já estavam grandes, expulsaram Jefté, dizendo: "Você não vai receber nenhuma herança de nossa família, pois é filho de outra mulher".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6832, 'nvi', 'Juízes', 'jz', 11, 3, 'Então Jefté fugiu dos seus irmãos e se estabeleceu em Tobe. Ali um bando de vadios uniu-se a ele e o seguia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6833, 'nvi', 'Juízes', 'jz', 11, 4, 'Algum tempo depois, quando os amonitas entraram em guerra contra Israel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6834, 'nvi', 'Juízes', 'jz', 11, 5, 'os líderes de Gileade foram buscar Jefté em Tobe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6835, 'nvi', 'Juízes', 'jz', 11, 6, '"Venha", disseram. "Seja nosso comandante, para que possamos combater os amonitas. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6836, 'nvi', 'Juízes', 'jz', 11, 7, 'Disse-lhes Jefté: "Vocês não me odiavam e não me expulsaram da casa de meu pai? Por que me procuram agora, quando estão em dificuldades? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6837, 'nvi', 'Juízes', 'jz', 11, 8, '"Apesar disso, agora estamos apelando para você", responderam os líderes de Gileade. "Venha combater conosco os amonitas, e você será o chefe de todos os que vivem em Gileade. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6838, 'nvi', 'Juízes', 'jz', 11, 9, 'Jefté respondeu: "Se vocês me levarem de volta para combater os amonitas e o Senhor os entregar a mim, serei o chefe de vocês? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6839, 'nvi', 'Juízes', 'jz', 11, 10, 'Os líderes de Gileade responderam: "O Senhor é nossa testemunha; faremos conforme você diz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6840, 'nvi', 'Juízes', 'jz', 11, 11, 'Assim Jefté foi com os líderes de Gileade, e o povo o fez chefe e comandante sobre todos. E ele repetiu perante o Senhor, em Mispá, todas as palavras que tinha dito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6841, 'nvi', 'Juízes', 'jz', 11, 12, 'Jefté enviou mensageiros ao rei amonita com a seguinte pergunta: "Que é que tens contra nós, para ter atacado a nossa terra? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6842, 'nvi', 'Juízes', 'jz', 11, 13, 'O rei dos amonitas respondeu aos mensageiros de Jefté: "Quando Israel veio do Egito tomou as minhas terras, desde o Arnom até o Jaboque e até o Jordão. Agora, devolvam-me essas terras pacificamente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6843, 'nvi', 'Juízes', 'jz', 11, 14, 'Jefté mandou de novo mensageiros ao rei amonita,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6844, 'nvi', 'Juízes', 'jz', 11, 15, 'dizendo: "Assim diz Jefté: Israel não tomou a terra de Moabe, e tampouco a terra dos amonitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6845, 'nvi', 'Juízes', 'jz', 11, 16, 'Quando veio do Egito, Israel foi pelo deserto até o mar Vermelho e daí para Cades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6846, 'nvi', 'Juízes', 'jz', 11, 17, 'Então Israel enviou mensageiros ao rei de Edom, dizendo: ‘Deixa-nos atravessar a tua terra’, mas o rei de Edom não quis ouvi-lo. Enviou o mesmo pedido ao rei de Moabe, e ele também não consentiu. Assim Israel permaneceu em Cades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6847, 'nvi', 'Juízes', 'jz', 11, 18, '"Em seguida os israelitas viajaram pelo deserto e contornaram Edom e Moabe; passaram a leste de Moabe e acamparam do outro lado do Arnom. Não entraram no território de Moabe, pois o Arnom era a sua fronteira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6848, 'nvi', 'Juízes', 'jz', 11, 19, '"Depois Israel enviou mensageiros a Seom, rei dos amorreus, em Hesbom, e lhe pediu: ‘Deixa-nos atravessar a tua terra para irmos ao lugar que nos pertence! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6849, 'nvi', 'Juízes', 'jz', 11, 20, 'Seom, porém, não acreditou que Israel fosse apenas atravessar o seu território; assim convocou todos os seus homens, acampou em Jaza e lutou contra Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6850, 'nvi', 'Juízes', 'jz', 11, 21, '"Então o Senhor, o Deus de Israel, entregou Seom e todos os seus homens nas mãos de Israel, e este os derrotou. Israel tomou posse de todas as terras dos amorreus que viviam naquela região,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6851, 'nvi', 'Juízes', 'jz', 11, 22, 'conquistando-a por inteiro, desde o Arnom até o Jaboque, e desde o deserto até o Jordão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6852, 'nvi', 'Juízes', 'jz', 11, 23, '"Agora que o Senhor, o Deus de Israel, expulsou os amorreus da presença do seu povo Israel, queres tu tomá-la?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6853, 'nvi', 'Juízes', 'jz', 11, 24, 'Acaso não tomas posse daquilo que o teu deus Camos te dá? Da mesma forma tomaremos posse do que o Senhor nosso Deus nos deu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6854, 'nvi', 'Juízes', 'jz', 11, 25, 'És tu melhor do que Balaque, filho de Zipor, rei de Moabe? Entrou ele alguma vez em conflito com Israel ou lutou com ele?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6855, 'nvi', 'Juízes', 'jz', 11, 26, 'Durante trezentos anos Israel ocupou Hesbom, Aroer, os povoados ao redor e todas as cidades às margens do Arnom. Por que não os reconquistaste todo esse tempo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6856, 'nvi', 'Juízes', 'jz', 11, 27, 'Nada fiz contra ti, mas tu estás cometendo um erro, lutando contra mim. Que o Senhor, o Juiz, julgue hoje a disputa entre os israelitas e os amonitas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6857, 'nvi', 'Juízes', 'jz', 11, 28, 'Entretanto, o rei de Amom não deu atenção à mensagem de Jefté.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6858, 'nvi', 'Juízes', 'jz', 11, 29, 'Então o Espírito do Senhor se apossou de Jefté. Este atravessou Gileade e Manassés, passou por Mispá de Gileade, e daí avançou contra os amonitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6859, 'nvi', 'Juízes', 'jz', 11, 30, 'E Jefté fez este voto ao Senhor: "Se entregares os amonitas nas minhas mãos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6860, 'nvi', 'Juízes', 'jz', 11, 31, 'aquele que vier saindo da porta da minha casa ao meu encontro, quando eu retornar da vitória sobre os amonitas, será do Senhor, e eu o oferecerei em holocausto".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6861, 'nvi', 'Juízes', 'jz', 11, 32, 'Então Jefté foi combater os amonitas, e o Senhor os entregou nas suas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6862, 'nvi', 'Juízes', 'jz', 11, 33, 'Ele conquistou vinte cidades, desde Aroer até as vizinhanças de Minite, chegando a Abel-Queramim. Assim os amonitas foram subjugados pelos Israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6863, 'nvi', 'Juízes', 'jz', 11, 34, 'Quando Jefté chegou à sua casa em Mispá, sua filha saiu ao seu encontro, dançando ao som de tamborins. E ela era filha única. Ele não tinha outro filho ou filha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6864, 'nvi', 'Juízes', 'jz', 11, 35, 'Quando a viu, rasgou suas vestes e gritou: "Ah, minha filha! Estou angustiado e desesperado por tua causa, pois fiz ao Senhor um voto que não posso quebrar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6865, 'nvi', 'Juízes', 'jz', 11, 36, '"Meu pai", respondeu ela, "sua palavra foi dada ao Senhor. Faça comigo o que prometeu, agora que o Senhor o vingou dos seus inimigos, os amonitas. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6866, 'nvi', 'Juízes', 'jz', 11, 37, 'E prosseguiu: "Mas conceda-me dois meses para vagar pelas colinas e chorar com as minhas amigas, porque jamais me casarei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6867, 'nvi', 'Juízes', 'jz', 11, 38, '"Vá! ", disse ele. E deixou que ela fosse por dois meses. Ela e suas amigas foram para as colinas e choraram porque ela jamais se casaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6868, 'nvi', 'Juízes', 'jz', 11, 39, 'Passados os dois meses, ela voltou a seu pai, e ele fez com ela o que tinha prometido no voto. Assim, ela nunca deixou de ser virgem. Daí vem o costume em Israel');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6869, 'nvi', 'Juízes', 'jz', 11, 40, 'de saírem as moças durante quatro dias, todos os anos, para celebrar a memória da filha de Jefté, o gileadita.');
