INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6885, 'nvi', 'Juízes', 'jz', 13, 1, 'Os israelitas voltaram a fazer o que o Senhor reprova, e por isso o Senhor os entregou nas mãos dos filisteus durante quarenta anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6886, 'nvi', 'Juízes', 'jz', 13, 2, 'Certo homem de Zorá, chamado Manoá, do clã da tribo de Dã, tinha mulher estéril.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6887, 'nvi', 'Juízes', 'jz', 13, 3, 'Certo dia o anjo do Senhor apareceu a ela e lhe disse: "Você é estéril, não tem filhos, mas engravidará e dará à luz um filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6888, 'nvi', 'Juízes', 'jz', 13, 4, 'Todavia, tenha cuidado, não beba vinho nem outra bebida fermentada, e não coma nada impuro;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6889, 'nvi', 'Juízes', 'jz', 13, 5, 'e não se passará navalha na cabeça do filho que você vai ter, porque o menino será nazireu, consagrado a Deus desde o nascimento; ele iniciará a libertação de Israel das mãos dos filisteus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6890, 'nvi', 'Juízes', 'jz', 13, 6, 'Então a mulher foi contar tudo ao seu marido: "Um homem de Deus veio falar comigo. Era como um anjo de Deus, de aparência impressionante. Não lhe perguntei de onde tinha vindo, e ele não me disse o seu nome,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6891, 'nvi', 'Juízes', 'jz', 13, 7, 'mas ele me assegurou: ‘Você engravidará e dará à luz um filho. Todavia, não beba vinho nem outra bebida fermentada, e não coma nada impuro, porque o menino será nazireu, consagrado a Deus, desde o nascimento até o dia da sua morte’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6892, 'nvi', 'Juízes', 'jz', 13, 8, 'Então Manoá orou ao Senhor: "Senhor, eu te imploro que o homem de Deus que enviaste volte para nos instruir sobre o que fazer com o menino que vai nascer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6893, 'nvi', 'Juízes', 'jz', 13, 9, 'Deus ouviu a oração de Manoá, e o anjo de Deus veio novamente falar com a mulher quando ela estava sentada no campo; Manoá, seu marido, não estava com ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6894, 'nvi', 'Juízes', 'jz', 13, 10, 'Mas ela foi correndo contar ao marido: "O homem que me apareceu outro dia está aqui! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6895, 'nvi', 'Juízes', 'jz', 13, 11, 'Manoá levantou-se e seguiu a mulher. Quando se aproximou do homem, perguntou: "És tu o que falaste com a minha mulher? " "Sim", disse ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6896, 'nvi', 'Juízes', 'jz', 13, 12, '"Quando as suas palavras se cumprirem", Manoá perguntou, "como devemos criar o menino? O que ele deverá fazer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6897, 'nvi', 'Juízes', 'jz', 13, 13, 'O Anjo do Senhor respondeu: "Sua mulher terá que seguir tudo o que eu lhe ordenei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6898, 'nvi', 'Juízes', 'jz', 13, 14, 'Ela não poderá comer nenhum produto da videira, nem vinho ou bebida fermentada, nem comer nada impuro. Terá que obedecer a tudo o que lhe ordenei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6899, 'nvi', 'Juízes', 'jz', 13, 15, 'Manoá disse ao Anjo do Senhor: "Gostaríamos que ficasses conosco; queremos oferecer-te um cabrito".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6900, 'nvi', 'Juízes', 'jz', 13, 16, 'O anjo do Senhor respondeu: "Se eu ficar, não comerei nada. Mas, se você preparar um holocausto, ofereça-o ao Senhor". Manoá não sabia que ele era o anjo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6901, 'nvi', 'Juízes', 'jz', 13, 17, 'Então Manoá perguntou ao anjo do Senhor: "Qual é o teu nome, para que te prestemos homenagem quando se cumprir a tua palavra? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6902, 'nvi', 'Juízes', 'jz', 13, 18, 'Ele respondeu: "Por que pergunta o meu nome? Meu nome está além do entendimento".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6903, 'nvi', 'Juízes', 'jz', 13, 19, 'Então Manoá apanhou um cabrito e a oferta de cereal, e os ofereceu ao Senhor sobre uma rocha. E o Senhor fez algo estranho enquanto Manoá e sua mulher observavam:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6904, 'nvi', 'Juízes', 'jz', 13, 20, 'Quando a chama do altar subiu ao céu, o Anjo do Senhor subiu na chama. Vendo isso, Manoá e à sua mulher prostraram-se, rosto em terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6905, 'nvi', 'Juízes', 'jz', 13, 21, 'Como o Anjo do Senhor não voltou a manifestar-se a Manoá e à sua mulher, Manoá percebeu que era o Anjo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6906, 'nvi', 'Juízes', 'jz', 13, 22, '"Sem dúvida vamos morrer! " disse ele à mulher, "pois vimos a Deus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6907, 'nvi', 'Juízes', 'jz', 13, 23, 'Mas a sua mulher respondeu: "Se o Senhor tivesse a intenção de nos matar, não teria aceitado o holocausto e a oferta de cereal das nossas mãos, nem nos teria mostrado todas essas coisas, nem nos teria revelado o que agora nos revelou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6908, 'nvi', 'Juízes', 'jz', 13, 24, 'A mulher deu à luz um menino e pôs-lhe o nome de Sansão. Ele cresceu, e o Senhor o abençoou,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6909, 'nvi', 'Juízes', 'jz', 13, 25, 'e o Espírito do Senhor começou a agir nele quando ele se achava em Maané-Dã, entre Zorá e Estaol.');
