INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6910, 'nvi', 'Juízes', 'jz', 14, 1, 'Sansão desceu a Timna e viu ali uma mulher do povo filisteu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6911, 'nvi', 'Juízes', 'jz', 14, 2, 'Quando voltou para casa, disse a seu pai e a sua mãe: "Vi uma mulher filistéia em Timna; consigam essa mulher para ser minha esposa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6912, 'nvi', 'Juízes', 'jz', 14, 3, 'Seu pai e sua mãe lhe perguntaram: "Será que não há mulher entre os seus parentes ou entre todo o seu povo? Você tem que ir aos filisteus incircuncisos para conseguir esposa? " Sansão, porém, disse ao pai: "Consiga-a para mim. É ela que me agrada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6913, 'nvi', 'Juízes', 'jz', 14, 4, 'Seus pais não sabiam que isso vinha do Senhor, que buscava ocasião contra os filisteus; pois naquela época eles dominavam Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6914, 'nvi', 'Juízes', 'jz', 14, 5, 'Sansão foi para Timna com seu pai e sua mãe. Quando se aproximavam das vinhas de Timna, de repente um leão forte veio rugindo na direção dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6915, 'nvi', 'Juízes', 'jz', 14, 6, 'O Espírito do Senhor apossou-se de Sansão, e ele, sem nada nas mãos, rasgou o leão como se fosse um cabrito. Mas não contou nem ao pai nem à mãe o que fizera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6916, 'nvi', 'Juízes', 'jz', 14, 7, 'Então foi conversar com a mulher de quem gostava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6917, 'nvi', 'Juízes', 'jz', 14, 8, 'Algum tempo depois, quando voltou para casar-se com ela, Sansão saiu do caminho para olhar o cadáver do leão, e nele havia um enxame de abelhas e mel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6918, 'nvi', 'Juízes', 'jz', 14, 9, 'Tirou o mel com as mãos e o foi comendo pelo caminho. Quando voltou aos seus pais, repartiu com eles o mel, e eles também comeram. Mas não lhes contou que tinha tirado o mel do cadáver do leão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6919, 'nvi', 'Juízes', 'jz', 14, 10, 'Seu pai desceu à casa da mulher, e Sansão deu ali uma festa, como era costume dos noivos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6920, 'nvi', 'Juízes', 'jz', 14, 11, 'Quando ele chegou, trouxeram-lhe trinta rapazes para o acompanharem na festa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6921, 'nvi', 'Juízes', 'jz', 14, 12, '"Vou propor-lhes um enigma", disse-lhes Sansão. "Se vocês puderem dar-me a resposta certa durante os sete dias da festa, então eu lhes darei trinta vestes de linho e trinta mudas de roupas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6922, 'nvi', 'Juízes', 'jz', 14, 13, 'Se não conseguirem dar-me a resposta, vocês me darão trinta vestes de linho e trinta mudas de roupas. " "Proponha-nos o seu enigma", disseram. "Vamos ouvi-lo. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6923, 'nvi', 'Juízes', 'jz', 14, 14, 'Disse ele então: "Do que come saiu comida; do que é forte saiu doçura". Durante três dias eles não conseguiram dar a resposta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6924, 'nvi', 'Juízes', 'jz', 14, 15, 'No quarto dia disseram à mulher de Sansão: "Convença o seu marido a explicar o enigma. Caso contrário, poremos fogo em você e na família de seu pai, e vocês morrerão. Você nos convidou para nos roubar? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6925, 'nvi', 'Juízes', 'jz', 14, 16, 'Então a mulher de Sansão implorou-lhe aos prantos: "Você me odeia! Você não me ama! Você deu ao meu povo um enigma, mas não me contou a resposta! " "Nem a meu pai e à minha mãe expliquei o enigma", respondeu ele. "Por que deveria explicá-lo a você? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6926, 'nvi', 'Juízes', 'jz', 14, 17, 'Ela chorou durante o restante da semana da festa. Por fim, no sétimo dia, ele lhe contou, pois ela continuava a perturbá-lo. Ela, por sua vez, revelou o enigma ao seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6927, 'nvi', 'Juízes', 'jz', 14, 18, 'Antes do pôr-do-sol do sétimo dia, os homens da cidade vieram lhe dizer: "O que é mais doce que o mel? O que é mais forte que o leão? " Sansão lhes disse:  "Se vocês não tivessem arado com a minha novilha, não teriam solucionado o meu enigma".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6928, 'nvi', 'Juízes', 'jz', 14, 19, 'Então o Espírito do Senhor apossou-se de Sansão. Ele desceu a Ascalom, matou trinta homens, pegou as suas roupas e as deu aos que tinham explicado o enigma. Depois, enfurecido, foi para a casa do seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6929, 'nvi', 'Juízes', 'jz', 14, 20, 'E a mulher de Sansão foi dada ao amigo que tinha sido o acompanhante dele no casamento.');
