INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6950, 'nvi', 'Juízes', 'jz', 16, 1, 'Certa vez Sansão foi a Gaza, viu ali uma prostituta, e passou a noite com ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6951, 'nvi', 'Juízes', 'jz', 16, 2, 'Disseram ao povo de Gaza: "Sansão está aqui! " Então cercaram o local e ficaram à espera dele a noite toda, junto à porta da cidade. Não se moveram a noite inteira, dizendo: "Ao amanhecer o mataremos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6952, 'nvi', 'Juízes', 'jz', 16, 3, 'Sansão, porém, ficou deitado só até à meia-noite. Levantou-se, agarrou firme a porta da cidade, juntamente com os dois batentes, e os arrancou, com tranca e tudo. Pôs tudo nos ombros e levou ao topo da colina que fica defronte de Hebrom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6953, 'nvi', 'Juízes', 'jz', 16, 4, 'Depois dessas coisas, ele se apaixonou por uma mulher do vale de Soreque, chamada Dalila.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6954, 'nvi', 'Juízes', 'jz', 16, 5, 'Os líderes dos filisteus foram dizer a ela: "Veja se você consegue induzi-lo a mostrar-lhe o segredo da sua grande força e como poderemos dominá-lo, para que o amarremos e o subjuguemos. Cada um de nós dará a você treze quilos de prata".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6955, 'nvi', 'Juízes', 'jz', 16, 6, 'Disse, pois, Dalila a Sansão: "Conte-me, por favor, de onde vem a sua grande força e como você pode ser amarrado e subjugado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6956, 'nvi', 'Juízes', 'jz', 16, 7, 'Respondeu-lhe Sansão: "Se alguém me amarrar com sete tiras de couro ainda úmidas, ficarei tão fraco quanto qualquer outro homem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6957, 'nvi', 'Juízes', 'jz', 16, 8, 'Então os líderes dos filisteus trouxeram a ela sete tiras de couro ainda úmidas, e Dalila o amarrou com elas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6958, 'nvi', 'Juízes', 'jz', 16, 9, 'Tendo homens escondidos no quarto, ela o chamou: "Sansão, os filisteus o estão atacando! " Mas ele arrebentou as tiras de couro como se fossem um fio de estopa que chega perto do fogo. Assim, não se descobriu de onde vinha a sua força.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6959, 'nvi', 'Juízes', 'jz', 16, 10, 'Disse Dalila a Sansão: "Você me fez de boba; mentiu para mim! Agora conte-me, por favor, como você pode ser amarrado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6960, 'nvi', 'Juízes', 'jz', 16, 11, 'Ele disse: "Se me amarrarem firmemente com cordas que nunca tenham sido usadas, ficarei tão fraco quanto qualquer outro homem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6961, 'nvi', 'Juízes', 'jz', 16, 12, 'Dalila o amarrou com cordas novas. Depois, tendo homens escondidos no quarto, ela o chamou: "Sansão, os filisteus o estão atacando! " Mas ele arrebentou as cordas de seus braços como se fossem uma linha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6962, 'nvi', 'Juízes', 'jz', 16, 13, 'Disse Dalila a Sansão: "Até agora você me fez de boba e mentiu para mim. Diga-me como pode ser amarrado". Ele respondeu: "Se você tecer num pano as sete tranças da minha cabeça e o prender com uma lançadeira, ficarei tão fraco quanto qualquer outro homem". Assim, quando ele dormia, Dalila teceu as sete tranças da sua cabeça num pano');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6963, 'nvi', 'Juízes', 'jz', 16, 14, 'e o prendeu com a lançadeira. Novamente ela o chamou: "Sansão, os filisteus estão vindo sobre você! " Ele despertou do sono e arrancou a lançadeira e o tear, junto com os fios do tear.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6964, 'nvi', 'Juízes', 'jz', 16, 15, 'Então ela lhe disse: "Como você pode dizer que me ama, se não confia em mim? Esta é a terceira vez que você me fez de boba e não contou o segredo da sua grande força".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6965, 'nvi', 'Juízes', 'jz', 16, 16, 'Importunando-o o tempo todo, ela o esgotava dia após dia, ficando ele a ponto de morrer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6966, 'nvi', 'Juízes', 'jz', 16, 17, 'Por isso ele lhe contou o segredo: "Jamais se passou navalha em minha cabeça", disse ele, "pois sou nazireu, desde o ventre materno. Se fosse rapado o cabelo da minha cabeça, a minha força se afastaria de mim, e eu ficaria tão fraco quanto qualquer outro homem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6967, 'nvi', 'Juízes', 'jz', 16, 18, 'Quando Dalila viu que Sansão lhe tinha contado todo o segredo, enviou esta mensagem aos líderes dos filisteus: "Subam mais esta vez; pois ele me contou todo o segredo". Os líderes dos filisteus voltaram a ela levando a prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6968, 'nvi', 'Juízes', 'jz', 16, 19, 'Fazendo-o dormir no seu colo, ela chamou um homem para cortar as sete tranças do cabelo dele, e assim começou a subjugá-lo. E a sua força o deixou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6969, 'nvi', 'Juízes', 'jz', 16, 20, 'Então ela chamou: "Sansão, os filisteus o estão atacando! " Ele acordou do sono e pensou: "Sairei como antes e me livrarei". Mas não sabia que o Senhor o tinha deixado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6970, 'nvi', 'Juízes', 'jz', 16, 21, 'Os filisteus o prenderam, furaram os seus olhos e o levaram para Gaza. Prenderam-no com algemas de bronze, e o puseram a girar um moinho na prisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6971, 'nvi', 'Juízes', 'jz', 16, 22, 'Mas, logo o cabelo da sua cabeça começou a crescer de novo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6972, 'nvi', 'Juízes', 'jz', 16, 23, 'Então os líderes dos filisteus se reuniram para oferecer um grande sacrifício a seu deus Dagom e para festejar, comemorando: "O nosso deus entregou o nosso inimigo Sansão em nossas mãos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6973, 'nvi', 'Juízes', 'jz', 16, 24, 'Quando o povo o viu, louvou o seu deus: "O nosso deus nos entregou o nosso inimigo, o devastador da nossa terra, aquele que multiplicava os nossos mortos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6974, 'nvi', 'Juízes', 'jz', 16, 25, 'Com o coração cheio de alegria, gritaram: "Tragam-nos Sansão para nos divertir! " E mandaram trazer Sansão da prisão, e ele os divertia. Quando o puseram entre as colunas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6975, 'nvi', 'Juízes', 'jz', 16, 26, 'Sansão disse ao jovem que o guiava pela mão: "Ponha-me onde eu possa apalpar as colunas que sustentam o templo, para que eu me apóie nelas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6976, 'nvi', 'Juízes', 'jz', 16, 27, 'Homens e mulheres lotavam o templo; todos os líderes dos filisteus estavam presentes, e no alto, na galeria, havia cerca de três mil homens e mulheres vendo Sansão, que os divertia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6977, 'nvi', 'Juízes', 'jz', 16, 28, 'E Sansão orou ao Senhor: "Ó Soberano Senhor, lembra-te de mim! Ó Deus, eu te suplico, dá-me forças, mais uma vez, e faze com que eu me vingue dos filisteus por causa dos meus dois olhos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6978, 'nvi', 'Juízes', 'jz', 16, 29, 'Então Sansão forçou as duas colunas centrais sobre as quais o templo se firmava. Apoiando-se nelas, tendo a mão direita numa coluna e a esquerda na outra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6979, 'nvi', 'Juízes', 'jz', 16, 30, 'disse: "Que eu morra com os filisteus! " Então, ele as empurrou com toda a força, e o templo desabou sobre os líderes e sobre todo o povo que ali estava. Assim, na sua morte, Sansão matou mais homens do que em toda a sua vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6980, 'nvi', 'Juízes', 'jz', 16, 31, 'Foram então os seus irmãos e toda a família do seu pai para buscá-lo. Trouxeram-no e o sepultaram entre Zorá e Estaol, no túmulo de Manoá, seu pai. Sansão liderou Israel durante vinte anos.');
