INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7025, 'nvi', 'Juízes', 'jz', 19, 1, 'Naquela época não havia rei em Israel. Aconteceu que um levita que vivia nos montes de Efraim, numa região afastada, tomou para si uma concubina, que era de Belém de Judá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7026, 'nvi', 'Juízes', 'jz', 19, 2, 'Mas ela lhe foi infiel. Deixou-o e voltou para a casa do seu pai, em Belém de Judá. Quatro meses depois,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7027, 'nvi', 'Juízes', 'jz', 19, 3, 'seu marido foi convencê-la a voltar. Ele tinha levado o seu servo e dois jumentos. A mulher o levou para dentro da casa do seu pai, e quando seu pai o viu, alegrou-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7028, 'nvi', 'Juízes', 'jz', 19, 4, 'O sogro dele, pai da moça, o convenceu a ficar ali; e ele permaneceu com eles três dias; todos eles comendo, bebendo e dormindo ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7029, 'nvi', 'Juízes', 'jz', 19, 5, 'No quarto dia, eles se levantaram cedo, e o levita se preparou para partir, mas o pai da moça disse ao genro: "Coma alguma coisa, e depois vocês poderão partir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7030, 'nvi', 'Juízes', 'jz', 19, 6, 'Os dois se assentaram para comer e beber juntos. Mas o pai da moça disse: "Eu lhe peço que fique esta noite, e que se alegre".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7031, 'nvi', 'Juízes', 'jz', 19, 7, 'E, quando o homem se levantou para partir, seu sogro o convenceu a ficar ainda aquela noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7032, 'nvi', 'Juízes', 'jz', 19, 8, 'Na manhã do quinto dia, quando ele se preparou para partir, o pai da moça disse: "Vamos comer! Espere até a tarde! " E os dois comeram juntos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7033, 'nvi', 'Juízes', 'jz', 19, 9, 'Então, quando o homem, com a sua concubina e com o seu servo, levantaram-se para partir, o seu sogro, pai da moça, disse outra vez: "Veja, o dia está quase acabando, é quase noite; passe a noite aqui. Fique e alegre-se. Amanhã de madrugada vocês poderão levantar-se e ir para casa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7034, 'nvi', 'Juízes', 'jz', 19, 10, 'Não desejando ficar outra noite, o homem partiu rumo a Jebus, isto é, Jerusalém, com dois jumentos selados e com a sua concubina.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7035, 'nvi', 'Juízes', 'jz', 19, 11, 'Quando estavam perto de Jebus e já se findava o dia, o servo disse a seu senhor: "Venha. Vamos parar nesta cidade dos jebuseus e passar a noite aqui".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7036, 'nvi', 'Juízes', 'jz', 19, 12, 'O seu senhor respondeu: "Não. Não vamos entrar numa cidade estrangeira, cujo povo não seja israelita. Iremos para Gibeá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7037, 'nvi', 'Juízes', 'jz', 19, 13, 'E acrescentou: "Ande! Vamos tentar chegar a Gibeá ou a Ramá e passar a noite num desses lugares".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7038, 'nvi', 'Juízes', 'jz', 19, 14, 'Então prosseguiram, e o sol se pôs quando se aproximavam de Gibeá de Benjamim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7039, 'nvi', 'Juízes', 'jz', 19, 15, 'Ali entraram para passar a noite. Foram sentar-se na praça da cidade. E ninguém os convidou para passarem a noite em casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7040, 'nvi', 'Juízes', 'jz', 19, 16, 'Naquela noite um homem idoso procedente dos montes de Efraim e que estava morando em Gibeá ( os homens do lugar eram benjamitas ), voltou do seu trabalho no campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7041, 'nvi', 'Juízes', 'jz', 19, 17, 'Quando viu o viajante na praça da cidade, o homem idoso perguntou: "Para onde você está indo? De onde vem? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7042, 'nvi', 'Juízes', 'jz', 19, 18, 'Ele respondeu: "Estamos de viagem, indo de Belém de Judá para uma região afastada, nos montes de Efraim, onde moro. Fui a Belém de Judá, e agora estou indo ao santuário do Senhor. Mas aqui ninguém me recebeu em casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7043, 'nvi', 'Juízes', 'jz', 19, 19, 'Temos palha e forragem para os nossos jumentos, e para nós mesmos, que somos seus servos temos pão e vinho, para mim, para a sua serva e para o jovem que está conosco. Não temos falta de nada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7044, 'nvi', 'Juízes', 'jz', 19, 20, '"Você é bem-vindo em minha casa", disse o homem idoso. "Vou atendê-lo no que você precisar. Não passe a noite na praça. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7045, 'nvi', 'Juízes', 'jz', 19, 21, 'E os levou para a sua casa e alimentou os jumentos. Depois de lavarem os pés, comeram e beberam alguma coisa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7046, 'nvi', 'Juízes', 'jz', 19, 22, 'Quando estavam entretidos, alguns vadios da cidade cercaram a casa. Esmurrando a porta, gritaram para o homem idoso, dono da casa: "Traga para fora o homem que entrou na sua casa para que tenhamos relações com ele! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7047, 'nvi', 'Juízes', 'jz', 19, 23, 'O dono da casa saiu e lhes disse: "Não sejam tão perversos, meus amigos. Já que esse homem é meu hóspede, não cometam essa loucura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7048, 'nvi', 'Juízes', 'jz', 19, 24, 'Vejam, aqui está minha filha virgem e a concubina do meu hóspede. Eu as trarei para vocês, e vocês poderão usá-las e fazer com elas o que quiserem. Mas, nada façam com esse homem, não cometam tal loucura! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7049, 'nvi', 'Juízes', 'jz', 19, 25, 'Mas os homens não quiseram ouvi-lo. Então o levita mandou a sua concubina para fora, e eles a violentaram e abusaram dela a noite toda. Ao alvorecer a deixaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7050, 'nvi', 'Juízes', 'jz', 19, 26, 'Ao romper do dia a mulher voltou para a casa onde o seu senhor estava hospedado, caiu junto à porta e ali ficou até o dia clarear.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7051, 'nvi', 'Juízes', 'jz', 19, 27, 'Quando o seu senhor se levantou de manhã, abriu a porta da casa e saiu para prosseguir viagem, lá estava a sua concubina, caída à entrada da casa, com as mãos na soleira da porta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7052, 'nvi', 'Juízes', 'jz', 19, 28, 'Ele lhe disse: "Levante-se, vamos! " Não houve resposta. Então o homem a pôs em seu jumento e foi para casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7053, 'nvi', 'Juízes', 'jz', 19, 29, 'Quando chegou em casa, apanhou uma faca e cortou o corpo da sua concubina em doze partes, e as enviou a todas as regiões de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (7054, 'nvi', 'Juízes', 'jz', 19, 30, 'Todos os que viram isso disseram: "Nunca se viu nem se fez uma coisa dessas desde o dia em que os israelitas saíram do Egito. Pensem! Reflitam! Digam o que se deve fazer! "');
