INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6546, 'nvi', 'Juízes', 'jz', 2, 1, 'O anjo do Senhor subiu de Gilgal a Boquim e disse: "Tirei vocês do Egito e os trouxe para a terra que prometi com juramento que daria a seus antepassados. Eu disse: Jamais quebrarei a minha aliança com vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6547, 'nvi', 'Juízes', 'jz', 2, 2, 'E vocês não farão acordo com o povo desta terra, mas demolirão os altares deles. Por quê vocês não me obedeceram?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6548, 'nvi', 'Juízes', 'jz', 2, 3, 'Portanto, agora lhes digo que não os expulsarei da presença de vocês; eles serão seus adversários, e os deuses deles serão uma armadilha para vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6549, 'nvi', 'Juízes', 'jz', 2, 4, 'Quando o anjo do Senhor acabou de falar a todos os israelitas, o povo chorou em alta voz,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6550, 'nvi', 'Juízes', 'jz', 2, 5, 'e ao lugar chamaram Boquim. Ali ofereceram sacrifícios ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6551, 'nvi', 'Juízes', 'jz', 2, 6, 'Depois que Josué despediu os israelitas, eles saíram para ocupar a terra, cada um a sua herança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6552, 'nvi', 'Juízes', 'jz', 2, 7, 'O povo prestou culto ao Senhor durante toda a vida de Josué e dos líderes que continuaram vivos depois de Josué e que tinham visto todos os grandes feitos que o Senhor realizara em favor de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6553, 'nvi', 'Juízes', 'jz', 2, 8, 'Josué, filho de Num, servo do Senhor, morreu com a idade de cento e dez anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6554, 'nvi', 'Juízes', 'jz', 2, 9, 'Foi sepultado na terra de sua herança, em Timnate-Heres, nos montes de Efraim, ao norte do monte Gaás.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6555, 'nvi', 'Juízes', 'jz', 2, 10, 'Depois que toda aquela geração foi reunida a seus antepassados, surgiu uma nova geração que não conhecia o Senhor e o que ele havia feito por Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6556, 'nvi', 'Juízes', 'jz', 2, 11, 'Então os israelitas fizeram o que o Senhor reprova e prestaram culto aos baalins.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6557, 'nvi', 'Juízes', 'jz', 2, 12, 'Abandonaram o Senhor, o Deus dos seus antepassados, que os havia tirado do Egito, e seguiram e adoraram vários deuses dos povos ao seu redor, provocando a ira do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6558, 'nvi', 'Juízes', 'jz', 2, 13, 'Abandonaram o Senhor e prestaram culto a Baal e aos postes sagrados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6559, 'nvi', 'Juízes', 'jz', 2, 14, 'A ira do Senhor se acendeu contra Israel, e ele os entregou nas mãos de invasores que os saquearam. Ele os entregou aos inimigos ao seu redor, aos quais já não conseguiam resistir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6560, 'nvi', 'Juízes', 'jz', 2, 15, 'Sempre que os israelitas saíam para a batalha, a mão do Senhor era contra eles para derrotá-los, conforme lhes havia advertido e jurado. Grande angústia os dominava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6561, 'nvi', 'Juízes', 'jz', 2, 16, 'Então o Senhor levantou juízes, que os libertaram das mãos daqueles que os atacavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6562, 'nvi', 'Juízes', 'jz', 2, 17, 'Mesmo assim eles não quiseram ouvir os juízes, antes se prostituíram com outros deuses e os adoraram. Ao contrário dos seus antepassados, logo se desviaram do caminho pelo qual os seus antepassados tinham andado, o caminho da obediência aos mandamentos do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6563, 'nvi', 'Juízes', 'jz', 2, 18, 'Sempre que o Senhor lhes levantava um juiz, ele estava com o juiz e os salvava das mãos de seus inimigos enquanto o juiz vivia; pois o Senhor tinha misericórdia por causa dos gemidos deles diante daqueles que os oprimiam e os afligiam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6564, 'nvi', 'Juízes', 'jz', 2, 19, 'Mas, quando o juiz morria, o povo voltava a caminhos ainda piores do que os caminhos dos seus antepassados, seguindo outros deuses, prestando-lhes culto e adorando-os. Recusavam-se a abandonar suas práticas e seu caminho obstinado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6565, 'nvi', 'Juízes', 'jz', 2, 20, 'Por isso a ira do Senhor acendeu-se contra Israel, e ele disse: "Como este povo violou a aliança que fiz com os seus antepassados e não tem ouvido a minha voz,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6566, 'nvi', 'Juízes', 'jz', 2, 21, 'não expulsarei de diante deles nenhuma das nações que Josué deixou quando morreu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6567, 'nvi', 'Juízes', 'jz', 2, 22, 'Eu as usarei para pôr Israel à prova e ver se guardarão o caminho do Senhor e se andarão nele como o fizeram os seus antepassados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6568, 'nvi', 'Juízes', 'jz', 2, 23, 'O Senhor havia permitido que essas nações permanecessem; não as expulsou de imediato, e não as entregou nas mãos de Josué.');
