INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6569, 'nvi', 'Juízes', 'jz', 3, 1, 'São estas as nações que o Senhor deixou para pôr à prova todos os israelitas que não tinham visto nenhuma das guerras em Canaã');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6570, 'nvi', 'Juízes', 'jz', 3, 2, '( fez isso apenas para treinar na guerra os descendentes dos israelitas, pois não tinham tido experiência anterior de combate ):');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6571, 'nvi', 'Juízes', 'jz', 3, 3, 'os cinco governantes dos filisteus, todos os cananeus, os sidônios e os heveus que viviam nos montes do Líbano, desde o monte Baal-Hermom até Lebo-Hamate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6572, 'nvi', 'Juízes', 'jz', 3, 4, 'Essas nações foram deixadas para que por elas os israelitas fossem postos à prova, se obedeceriam aos mandamentos que o Senhor dera aos seus antepassados por meio de Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6573, 'nvi', 'Juízes', 'jz', 3, 5, 'Os israelitas viviam entre os cananeus, os hititas, os amorreus, os ferezeus, os heveus e os jebuseus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6574, 'nvi', 'Juízes', 'jz', 3, 6, 'Tomaram as filhas deles em casamento e deram suas filhas aos filhos deles, e prestaram culto aos deuses deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6575, 'nvi', 'Juízes', 'jz', 3, 7, 'Os israelitas fizeram o que o Senhor reprova, pois esqueceram-se do Senhor seu Deus e prestaram culto aos baalins e aos postes sagrados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6576, 'nvi', 'Juízes', 'jz', 3, 8, 'Acendeu-se a ira do Senhor de tal forma contra Israel que ele os entregou nas mãos de Cuchã-Risataim, rei da Mesopotâmia, por quem os israelitas foram subjugados durante oito anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6577, 'nvi', 'Juízes', 'jz', 3, 9, 'Mas, quando clamaram ao Senhor, ele lhes levantou um libertador, Otoniel, filho de Quenaz, o irmão mais novo de Calebe, que os libertou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6578, 'nvi', 'Juízes', 'jz', 3, 10, 'O Espírito do Senhor veio sobre ele, de modo que liderou Israel e foi à guerra. O Senhor entregou Cuchã-Risataim, rei de Arã, nas mãos de Otoniel, que prevaleceu contra ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6579, 'nvi', 'Juízes', 'jz', 3, 11, 'E a terra teve paz durante quarenta anos, até a morte de Otoniel, filho de Quenaz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6580, 'nvi', 'Juízes', 'jz', 3, 12, 'Mais uma vez os israelitas fizeram o que o Senhor reprova, e por isso o Senhor deu a Eglom, rei de Moabe, poder sobre Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6581, 'nvi', 'Juízes', 'jz', 3, 13, 'Conseguindo uma aliança com os amonitas e com os amalequitas, Eglom veio e derrotou Israel, e conquistou a Cidade das Palmeiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6582, 'nvi', 'Juízes', 'jz', 3, 14, 'Os israelitas ficaram sob o domínio de Eglom, rei de Moabe, durante dezoito anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6583, 'nvi', 'Juízes', 'jz', 3, 15, 'Novamente os israelitas clamaram ao Senhor, que lhes deu um libertador chamado Eúde, homem canhoto, filho do benjamita Gera. Os israelitas o enviaram com o pagamento de tributos a Eglom, rei de Moabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6584, 'nvi', 'Juízes', 'jz', 3, 16, 'Eúde havia feito uma espada de dois gumes, de quarenta e cinco centímetros de comprimento, e a tinha amarrado na coxa direita, debaixo da roupa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6585, 'nvi', 'Juízes', 'jz', 3, 17, 'Ele entregou o tributo a Eglom, rei de Moabe, homem muito gordo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6586, 'nvi', 'Juízes', 'jz', 3, 18, 'Em seguida, Eúde mandou embora os carregadores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6587, 'nvi', 'Juízes', 'jz', 3, 19, 'Junto aos ídolos que estão perto de Gilgal, ele voltou e disse: "Tenho uma mensagem secreta para ti, ó rei". O rei respondeu: "Calado! " E todos os seus auxiliares saíram de sua presença.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6588, 'nvi', 'Juízes', 'jz', 3, 20, 'Eúde aproximou-se do rei, que estava sentado sozinho na sala superior do palácio de verão, e repetiu: "Tenho uma mensagem de Deus para ti". Quando o rei se levantou do trono,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6589, 'nvi', 'Juízes', 'jz', 3, 21, 'Eúde estendeu a mão esquerda, apanhou a espada de sua coxa direita e cravou-a na barriga do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6590, 'nvi', 'Juízes', 'jz', 3, 22, 'Até o cabo penetrou com a lâmina; e, como não tirou a espada, a gordura se fechou sobre ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6591, 'nvi', 'Juízes', 'jz', 3, 23, 'Então Eúde saiu para o pórtico, depois de fechar e trancar as portas da sala atrás de si.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6592, 'nvi', 'Juízes', 'jz', 3, 24, 'Depois que ele saiu, vieram os servos e encontraram trancadas as portas da sala superior, e disseram: "Ele deve estar fazendo suas necessidades em seu cômodo privativo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6593, 'nvi', 'Juízes', 'jz', 3, 25, 'Cansaram-se de esperar, e como ele não abria a porta da sala, pegaram a chave e a abriram. E lá estava o seu senhor, caído no chão, morto!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6594, 'nvi', 'Juízes', 'jz', 3, 26, 'Enquanto esperavam, Eúde escapou. Passou pelos ídolos e fugiu para Seirá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6595, 'nvi', 'Juízes', 'jz', 3, 27, 'Quando chegou, tocou a trombeta nos montes de Efraim, e os israelitas desceram dos montes, com ele à sua frente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6596, 'nvi', 'Juízes', 'jz', 3, 28, '"Sigam-me", ordenou, "pois o Senhor entregou Moabe, o inimigo de vocês, em suas mãos. " Eles o seguiram e, tomaram posse do lugar de passagem do Jordão que levava a Moabe, e não deixaram ninguém atravessar o rio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6597, 'nvi', 'Juízes', 'jz', 3, 29, 'Naquela ocasião mataram cerca de dez mil moabitas, todos eles fortes e vigorosos; nem um só homem escapou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6598, 'nvi', 'Juízes', 'jz', 3, 30, 'Naquele dia Moabe foi subjugado por Israel, e a terra teve paz durante oitenta anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6599, 'nvi', 'Juízes', 'jz', 3, 31, 'Depois de Eúde veio Sangar, filho de Anate, que matou seiscentos filisteus com uma aguilhada de bois. Ele também libertou Israel.');
