INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6600, 'nvi', 'Juízes', 'jz', 4, 1, 'Depois da morte de Eúde, mais uma vez os israelitas fizeram o que o Senhor reprova.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6601, 'nvi', 'Juízes', 'jz', 4, 2, 'Assim o Senhor os entregou nas mãos de Jabim, rei de Canaã, que reinava em Hazor. O comandante do seu exército era Sísera, que habitava em Harosete-Hagoim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6602, 'nvi', 'Juízes', 'jz', 4, 3, 'Os israelitas clamaram ao Senhor, porque Jabim, que tinha novecentos carros de ferro, os havia oprimido cruelmente durante vinte anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6603, 'nvi', 'Juízes', 'jz', 4, 4, 'Débora, uma profetisa, mulher de Lapidote, liderava Israel naquela época.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6604, 'nvi', 'Juízes', 'jz', 4, 5, 'Ela se sentava debaixo da tamareira de Débora, entre Ramá e Betel, nos montes de Efraim, e os israelitas a procuravam, para que ela decidisse as suas questões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6605, 'nvi', 'Juízes', 'jz', 4, 6, 'Débora mandou chamar Baraque, filho de Abinoão, de Quedes, em Naftali, e lhe disse: "O Senhor, o Deus de Israel, lhe ordena que reúna dez mil homens de Naftali e Zebulom e vá ao monte Tabor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6606, 'nvi', 'Juízes', 'jz', 4, 7, 'Ele fará que Sísera, o comandante do exército de Jabim, vá atacá-lo, com seus carros de guerra e tropas, junto ao rio Quisom mulher, e os entregará em suas mãos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6607, 'nvi', 'Juízes', 'jz', 4, 8, 'Baraque disse a ela: "Se você for comigo, irei; mas, se não for, não irei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6608, 'nvi', 'Juízes', 'jz', 4, 9, 'Respondeu Débora: "Está bem, irei com você. Mas saiba que, por causa do seu modo de agir, a honra não será sua; porque o Senhor entregará Sísera nas mãos de uma mulher". Então Débora foi a Quedes com Baraque,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6609, 'nvi', 'Juízes', 'jz', 4, 10, 'onde ele convocou Zebulom e Naftali. Dez mil homens o seguiram, e Débora também foi com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6610, 'nvi', 'Juízes', 'jz', 4, 11, 'Ora, o queneu Héber se havia separado dos outros queneus, descendentes de Hobabe, sogro de Moisés, e armou a sua tenda junto ao carvalho de Zaanim, perto de Quedes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6611, 'nvi', 'Juízes', 'jz', 4, 12, 'Quando disseram a Sísera que Baraque, filho de Abinoão, tinha subido o monte Tabor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6612, 'nvi', 'Juízes', 'jz', 4, 13, 'Sísera reuniu seus novecentos carros de ferro e todos os seus soldados, de Harosete-Hagoim ao rio Quisom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6613, 'nvi', 'Juízes', 'jz', 4, 14, 'E Débora disse também a Baraque: "Vá! Este é o dia em que o Senhor entregou Sísera em suas mãos. O Senhor está indo à sua frente! " Então Baraque desceu o monte Tabor, seguido por dez mil homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6614, 'nvi', 'Juízes', 'jz', 4, 15, 'Diante do avanço de Baraque, o Senhor pela espada derrotou Sísera e todos os seus carros de guerra e o seu exército, e Sísera desceu do seu carro e fugiu a pé.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6615, 'nvi', 'Juízes', 'jz', 4, 16, 'Baraque perseguiu os carros de guerra e o exército até Harosete-Hagoim. Todo o exército de Sísera caiu ao fio da espada; não sobrou um só homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6616, 'nvi', 'Juízes', 'jz', 4, 17, 'Sísera, porém, fugiu a pé para a tenda de Jael, mulher do queneu Héber, pois havia paz entre Jabim, rei de Hazor, e o clã do queneu Héber.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6617, 'nvi', 'Juízes', 'jz', 4, 18, 'Jael saiu ao encontro de Sísera e o convidou: "Venha, entre na minha tenda, meu senhor. Não tenha medo! " Ele entrou, e ela o cobriu com um pano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6618, 'nvi', 'Juízes', 'jz', 4, 19, '"Estou com sede", disse ele. "Por favor, dê-me um pouco de água. " Ela abriu uma vasilha de leite feita de couro, deu-lhe de beber, e tornou a cobri-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6619, 'nvi', 'Juízes', 'jz', 4, 20, 'E Sísera disse à mulher: "Fique à entrada da tenda. Se alguém passar e perguntar se há alguém aqui, responda que não".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6620, 'nvi', 'Juízes', 'jz', 4, 21, 'Entretanto, Jael, mulher de Héber, apanhou uma estaca da tenda e um martelo e aproximou-se silenciosamente enquanto ele, exausto, dormia um sono profundo. E cravou-lhe a estaca na têmpora até penetrar o chão, e ele morreu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6621, 'nvi', 'Juízes', 'jz', 4, 22, 'Baraque passou à procura de Sísera, e Jael saiu ao seu encontro. "Venha", disse ela, "eu lhe mostrarei o homem que você está procurando. " E entrando ele na tenda, viu ali caído Sísera, morto, com a estaca atravessada nas têmporas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6622, 'nvi', 'Juízes', 'jz', 4, 23, 'Naquele dia Deus subjugou Jabim, o rei cananeu, perante os israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6623, 'nvi', 'Juízes', 'jz', 4, 24, 'E os israelitas atacaram cada vez mais a Jabim, o rei cananeu, até que eles o destruíram.');
