INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6624, 'nvi', 'Juízes', 'jz', 5, 1, 'Naquele dia Débora e Baraque, filho de Abinoão, entoaram este cântico:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6625, 'nvi', 'Juízes', 'jz', 5, 2, '"Consagrem-se para a guerra os chefes de Israel. Voluntariamente o povo se apresenta. Louvem o Senhor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6626, 'nvi', 'Juízes', 'jz', 5, 3, '"Ouçam, ó reis! Governantes, escutem! Cantarei ao Senhor, cantarei; comporei músicas ao Senhor, ao Deus de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6627, 'nvi', 'Juízes', 'jz', 5, 4, '"Ó Senhor, quando saíste de Seir, quando marchaste desde os campos de Edom, a terra estremeceu, os céus gotejaram, as nuvens despejaram água!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6628, 'nvi', 'Juízes', 'jz', 5, 5, 'Os montes tremeram perante o Senhor, o Deus do Sinai, perante o Senhor, o Deus de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6629, 'nvi', 'Juízes', 'jz', 5, 6, '"Nos dias de Sangar, filho de Anate, nos dias de Jael, as estradas estavam desertas; os que viajavam seguiam caminhos tortuosos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6630, 'nvi', 'Juízes', 'jz', 5, 7, 'Já tinham desistido os camponeses de Israel, já tinham desistido, até que eu, Débora, me levantei; levantou-se uma mãe em Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6631, 'nvi', 'Juízes', 'jz', 5, 8, 'Quando escolheram novos deuses, a guerra chegou às portas, e não se via um só escudo ou lança entre quarenta mil de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6632, 'nvi', 'Juízes', 'jz', 5, 9, 'Meu coração está com os comandantes de Israel, com os voluntários dentre o povo. Louvem o Senhor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6633, 'nvi', 'Juízes', 'jz', 5, 10, '"Vocês, que cavalgam em brancos jumentos, assentam-se em ricos tapetes, que caminham pela estrada, considerem!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6634, 'nvi', 'Juízes', 'jz', 5, 11, 'Mais alto que a voz dos que distribuem água junto aos bebedouros, recitem-se os justos feitos do Senhor, os justos feitos em favor dos camponeses de Israel. "Então o povo do Senhor desceu às portas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6635, 'nvi', 'Juízes', 'jz', 5, 12, '‘Desperte, Débora! Desperte! Desperte, desperte, irrompa em cânticos! Levante-se, Baraque! Leve presos os seus prisioneiros, ó filho de Abinoão! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6636, 'nvi', 'Juízes', 'jz', 5, 13, '"Então desceram os restantes e foram aos nobres; o povo do Senhor veio a mim contra os poderosos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6637, 'nvi', 'Juízes', 'jz', 5, 14, 'Alguns vieram de Efraim, das raízes de Amaleque; Benjamim estava com o povo que seguiu você. De Maquir desceram comandantes; de Zebulom, os que levam a vara de oficial.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6638, 'nvi', 'Juízes', 'jz', 5, 15, 'Os líderes de Issacar estavam com Débora; sim, Issacar também estava com Baraque, apressando-se após ele até o vale. Nas divisões de Rúben houve muita inquietação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6639, 'nvi', 'Juízes', 'jz', 5, 16, 'Por que vocês permaneceram entre as fogueiras a ouvir o balido dos rebanhos? Nas divisões de Rúben houve muita indecisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6640, 'nvi', 'Juízes', 'jz', 5, 17, 'Gileade permaneceu do outro lado do Jordão. E Dã, por que se deteve junto aos navios? Aser permaneceu no litoral e em suas enseadas ficou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6641, 'nvi', 'Juízes', 'jz', 5, 18, 'O povo de Zebulom arriscou a vida; como o fez Naftali nas altas regiões do campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6642, 'nvi', 'Juízes', 'jz', 5, 19, '"Vieram reis e lutaram. Os reis de Canaã lutaram em Taanaque, junto às águas de Megido, mas não levaram prata alguma, despojo algum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6643, 'nvi', 'Juízes', 'jz', 5, 20, 'Desde o céu lutaram as estrelas, desde as suas órbitas lutaram contra Sísera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6644, 'nvi', 'Juízes', 'jz', 5, 21, 'O rio Quisom os levou, o antigo rio, o rio Quisom. Avante, minh’alma! Seja forte!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6645, 'nvi', 'Juízes', 'jz', 5, 22, 'Os cascos dos cavalos faziam tremer o chão; galopavam, galopavam os seus poderosos cavalos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6646, 'nvi', 'Juízes', 'jz', 5, 23, '‘Amaldiçoem Meroz’, disse o anjo do Senhor. ‘Amaldiçoem o seu povo, pois não vieram ajudar o Senhor, ajudar o Senhor contra os poderosos. ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6647, 'nvi', 'Juízes', 'jz', 5, 24, '"Que Jael seja a mais bendita das mulheres, Jael, mulher de Héber, o queneu! Seja ela bendita entre as mulheres que habitam em tendas!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6648, 'nvi', 'Juízes', 'jz', 5, 25, 'Ele pediu água, e ela lhe deu leite; numa tigela digna de príncipes trouxe-lhe coalhada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6649, 'nvi', 'Juízes', 'jz', 5, 26, 'Ela estendeu a mão e apanhou a estaca da tenda; e com a mão direita o martelo do trabalhador. Golpeou Sísera, esmigalhou sua cabeça, esmagou e traspassou suas têmporas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6650, 'nvi', 'Juízes', 'jz', 5, 27, 'Aos seus pés ele se curvou, caiu e ali ficou prostrado. Aos seus pés ele se curvou e caiu; onde caiu, ali ficou, morto!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6651, 'nvi', 'Juízes', 'jz', 5, 28, '"Pela janela olhava a mãe de Sísera; atrás da grade ela exclamava: ‘Por que o seu carro se demora tanto? Por que custa a chegar o ruído de seus carros? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6652, 'nvi', 'Juízes', 'jz', 5, 29, 'As mais sábias de suas damas responderam, e ela continuava falando consigo mesma:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6653, 'nvi', 'Juízes', 'jz', 5, 30, '‘Estarão achando e repartindo os despojos? Uma moça ou duas moças para cada homem, roupas coloridas como despojo para Sísera, roupas coloridas e bordadas, tecidos bordados para o meu pescoço, tudo isso como despojo? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6654, 'nvi', 'Juízes', 'jz', 5, 31, '"Assim pereçam todos os teus inimigos, ó Senhor! Mas os que te amam sejam como o sol quando se levanta na sua força". E a terra teve paz durante quarenta anos.');
