INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6695, 'nvi', 'Juízes', 'jz', 7, 1, 'De madrugada Jerubaal, isto é, Gideão, e todo o seu exército acampou junto à fonte de Harode. O acampamento de Midiã estava ao norte deles, no vale, perto do monte Moré.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6696, 'nvi', 'Juízes', 'jz', 7, 2, 'E o Senhor disse a Gideão: "Você tem gente demais, para eu entregar Midiã nas suas mãos. A fim de que Israel não se orgulhe contra mim, dizendo que a sua própria força o libertou,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6697, 'nvi', 'Juízes', 'jz', 7, 3, 'anuncie, pois, ao povo que todo aquele que estiver tremendo de medo poderá ir embora do monte Gileade". Então vinte e dois mil homens partiram, e ficaram apenas dez mil.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6698, 'nvi', 'Juízes', 'jz', 7, 4, 'Mas o Senhor tornou a dizer a Gideão: "Ainda há gente demais. Desça com eles à beira d’água, e eu separarei os que ficarão com você. Se eu disser: Este irá com você, ele irá; mas, se eu disser: Este não irá com você, ele não irá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6699, 'nvi', 'Juízes', 'jz', 7, 5, 'Assim Gideão levou os homens à beira d’água, e o Senhor lhe disse: "Separe os que beberem a água lambendo-a como faz o cachorro, daqueles que se ajoelharem para beber".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6700, 'nvi', 'Juízes', 'jz', 7, 6, 'O número dos que lamberam a água levando-a com as mãos à boca foi de trezentos homens. Todos os demais se ajoelharam para beber.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6701, 'nvi', 'Juízes', 'jz', 7, 7, 'O Senhor disse a Gideão: "Com os trezentos homens que lamberam a água livrarei vocês e entregarei os midianitas nas suas mãos. Mande para casa todos os outros homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6702, 'nvi', 'Juízes', 'jz', 7, 8, 'Gideão mandou os israelitas para as suas tendas, mas reteve os trezentos. E estes ficaram com as provisões e as trombetas dos que partiram. O acampamento de Midiã ficava abaixo deles, no vale.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6703, 'nvi', 'Juízes', 'jz', 7, 9, 'Naquela noite o Senhor disse a Gideão: "Levante-se, e desça ao acampamento, pois vou entregá-lo nas suas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6704, 'nvi', 'Juízes', 'jz', 7, 10, 'Se você está com medo de atacá-los, desça ao acampamento com o seu servo Pura');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6705, 'nvi', 'Juízes', 'jz', 7, 11, 'e ouça o que estiverem dizendo. Depois disso você terá coragem para atacar". Então ele e o seu servo Pura desceram até os postos avançados do acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6706, 'nvi', 'Juízes', 'jz', 7, 12, 'Os midianitas, os amalequitas e todos os outros povos que vinham do leste haviam se instalado no vale; eram numerosos como nuvens de gafanhotos. Assim como não se pode contar a areia da praia, também não se podia contar os seus camelos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6707, 'nvi', 'Juízes', 'jz', 7, 13, 'Gideão chegou bem no momento em que um homem estava contando seu sonho a um amigo. "Tive um sonho", dizia ele. "Um pão de cevada vinha rolando dentro do acampamento midianita, e atingiu a tenda com tanta força que ela tombou e se desmontou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6708, 'nvi', 'Juízes', 'jz', 7, 14, 'Seu amigo respondeu: "Não pode ser outra coisa senão a espada de Gideão, filho de Joás, o israelita. Deus entregou os midianitas e todo o acampamento nas mãos dele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6709, 'nvi', 'Juízes', 'jz', 7, 15, 'Quando Gideão ouviu o sonho e a sua interpretação, adorou a Deus. Voltou para o acampamento de Israel e gritou: "Levantem-se! O Senhor entregou o acampamento midianita nas suas mãos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6710, 'nvi', 'Juízes', 'jz', 7, 16, 'Dividiu os trezentos homens em três companhias, pôs nas mãos de todos eles trombetas e jarros vazios, com tochas dentro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6711, 'nvi', 'Juízes', 'jz', 7, 17, 'E ele lhes disse: "Observem-me. Façam o que eu fizer. Quando eu chegar à extremidade do acampamento, façam o que eu fizer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6712, 'nvi', 'Juízes', 'jz', 7, 18, 'Quando eu e todos os que estiverem comigo tocarmos as nossas trombetas ao redor do acampamento, toquem as suas, e gritem: Pelo Senhor e por Gideão! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6713, 'nvi', 'Juízes', 'jz', 7, 19, 'Gideão e os cem homens que o acompanhavam chegaram aos postos avançados do acampamento pouco depois da meia noite, assim que foram trocadas as sentinelas. Tocaram as suas trombetas e quebraram os jarros que tinham nas mãos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6714, 'nvi', 'Juízes', 'jz', 7, 20, 'as três companhias tocaram as trombetas e despedaçaram os jarros. Empunhando as tochas com a mão esquerda e as trombetas com a direita, gritaram: "À espada, pelo Senhor e por Gideão! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6715, 'nvi', 'Juízes', 'jz', 7, 21, 'Cada homem mantinha a sua posição em torno do acampamento, e todos os midianitas fugiam correndo e gritando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6716, 'nvi', 'Juízes', 'jz', 7, 22, 'Quando as trezentas trombetas soaram, o Senhor fez que em todo o acampamento os homens se voltassem uns contra os outros com as suas espadas. Mas muitos fugiram para Bete-Sita, na direção de Zererá, até a fronteira de Abel-Meolá, perto de Tabate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6717, 'nvi', 'Juízes', 'jz', 7, 23, 'Os israelitas de Naftali, de Aser e de todo o Manassés foram convocados, e perseguiram os midianitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6718, 'nvi', 'Juízes', 'jz', 7, 24, 'Gideão enviou mensageiros a todos os montes de Efraim, dizendo: "Desçam para atacar os midianitas e cerquem as águas do Jordão à frente deles até Bete-Bara". Foram, pois, convocados todos os homens de Efraim, e eles ocuparam as águas do Jordão até Bete-Bara.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6719, 'nvi', 'Juízes', 'jz', 7, 25, 'Eles prenderam dois líderes midianitas, Orebe e Zeebe. Mataram Orebe na rocha de Orebe, e Zeebe no tanque de prensar uvas de Zeebe. E, depois de perseguir os midianitas, trouxeram a cabeça de Orebe e a de Zeebe a Gideão, que estava do outro lado do Jordão.');
