INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6720, 'nvi', 'Juízes', 'jz', 8, 1, 'Os efraimitas perguntaram então a Gideão: "Por que você nos tratou dessa forma? Por que não nos chamou quando foi lutar contra Midiã? " E o criticaram duramente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6721, 'nvi', 'Juízes', 'jz', 8, 2, 'Ele, porém, lhes respondeu: "Que é que eu fiz, em comparação com vocês? O resto das uvas de Efraim não são melhores do que toda a colheita de Abiezer?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6722, 'nvi', 'Juízes', 'jz', 8, 3, 'Deus entregou os líderes midianitas Orebe e Zeebe nas mãos de vocês. O que pude fazer não se compara com o que vocês fizeram? " Diante disso, acalmou-se a indignação deles contra Gideão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6723, 'nvi', 'Juízes', 'jz', 8, 4, 'Gideão e seus trezentos homens, já exaustos, continuaram a perseguição, chegaram ao Jordão e o atravessaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6724, 'nvi', 'Juízes', 'jz', 8, 5, 'Em Sucote, disse ele aos homens dali: "Peço-lhes um pouco de pão para as minhas tropas; os homens estão cansados, e eu ainda estou perseguindo os reis de Midiã, Zeba e Zalmuna".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6725, 'nvi', 'Juízes', 'jz', 8, 6, 'Os líderes de Sucote, porém, disseram: "Ainda não estão em seu poder Zeba e Zalmuna? Por que deveríamos dar pão às suas tropas? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6726, 'nvi', 'Juízes', 'jz', 8, 7, '"É assim? ", replicou Gideão. "Quando o Senhor entregar Zeba e Zalmuna em minhas mãos, rasgarei a carne de vocês com espinhos e espinheiros do deserto. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6727, 'nvi', 'Juízes', 'jz', 8, 8, 'Dali subiu a Peniel e fez o mesmo pedido aos homens de Peniel, mas eles responderam como os de Sucote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6728, 'nvi', 'Juízes', 'jz', 8, 9, 'Aos homens de Peniel ele disse: "Quando eu voltar triunfante, destruirei esta fortaleza".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6729, 'nvi', 'Juízes', 'jz', 8, 10, 'Ora, Zeba e Zalmuna estavam em Carcor, e com eles cerca de quinze mil homens. Estes foram todos os que sobraram dos exércitos dos povos que vinham do leste, pois cento e vinte mil homens que portavam espada tinham sido mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6730, 'nvi', 'Juízes', 'jz', 8, 11, 'Gideão subiu pela rota dos nômades, a leste de Noba e Jogbeá, e atacou de surpresa o exército.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6731, 'nvi', 'Juízes', 'jz', 8, 12, 'Zeba e Zalmuna, os dois reis de Midiã, fugiram, mas ele os perseguiu e os capturou; derrotando também o exército.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6732, 'nvi', 'Juízes', 'jz', 8, 13, 'Depois Gideão, filho de Joás, voltou da batalha pela subida de Heres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6733, 'nvi', 'Juízes', 'jz', 8, 14, 'Ele capturou um jovem de Sucote e o interrogou, e o jovem escreveu para Gideão os nomes dos setenta e sete líderes e autoridades da cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6734, 'nvi', 'Juízes', 'jz', 8, 15, 'Gideão foi então a Sucote e disse aos homens de lá: "Aqui estão Zeba e Zalmuna, acerca dos quais vocês zombaram de mim, dizendo: ‘Ainda não estão em seu poder Zeba e Zalmuna? Por que deveríamos dar pão aos seus homens exaustos? ’ "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6735, 'nvi', 'Juízes', 'jz', 8, 16, 'Gideão prendeu os líderes da cidade de Sucote, castigando-os com espinhos e espinheiros do deserto;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6736, 'nvi', 'Juízes', 'jz', 8, 17, 'derrubou a fortaleza de Peniel e matou os homens daquela cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6737, 'nvi', 'Juízes', 'jz', 8, 18, 'Então perguntou a Zeba e a Zalmuna: "Como eram os homens que vocês mataram em Tabor? " "Eram como você", responderam, "cada um tinha o porte de um príncipe".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6738, 'nvi', 'Juízes', 'jz', 8, 19, 'Gideão prosseguiu: "Aqueles homens eram meus irmãos, filhos de minha própria mãe. Juro pelo nome do Senhor que, se vocês tivessem poupado a vida deles, eu não mataria vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6739, 'nvi', 'Juízes', 'jz', 8, 20, 'E Gideão voltou-se para Jéter, seu filho mais velho, e lhe disse: "Mate-os! " Jéter, porém, teve medo e não desembainhou a espada, pois era muito jovem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6740, 'nvi', 'Juízes', 'jz', 8, 21, 'Mas Zeba e Zalmuna disseram: "Venha, mate-nos você mesmo. Isso exige coragem de homem". Então Gideão avançou e os matou, e tirou os enfeites do pescoço dos camelos deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6741, 'nvi', 'Juízes', 'jz', 8, 22, 'Os israelitas disseram a Gideão, "Reine sobre nós! você, seu filho e seu neto, pois você nos libertou das mãos de Midiã".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6742, 'nvi', 'Juízes', 'jz', 8, 23, '"Não reinarei sobre vocês", respondeu-lhes Gideão, "nem meu filho reinará sobre vocês. O Senhor reinará sobre vocês. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6743, 'nvi', 'Juízes', 'jz', 8, 24, 'E prosseguiu: "Só lhes faço um pedido: que cada um de vocês me dê um brinco da sua parte dos despojos". ( Os ismaelitas costumavam usar brincos de ouro. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6744, 'nvi', 'Juízes', 'jz', 8, 25, 'Eles responderam: "De boa vontade os daremos a você! " Então estenderam uma capa, e cada homem jogou sobre ela um brinco tirado de seus despojos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6745, 'nvi', 'Juízes', 'jz', 8, 26, 'O peso dos brincos de ouro chegou a vinte quilos e meio, sem contar os enfeites, os pendentes e as roupas de púrpura que os reis de Midiã usavam e os colares que estavam no pescoço de seus camelos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6746, 'nvi', 'Juízes', 'jz', 8, 27, 'Gideão usou o ouro para fazer um manto sacerdotal, que ele colocou em sua cidade, em Ofra. Todo o Israel prostituiu-se, fazendo dele objeto de adoração; e veio a ser uma armadilha para Gideão e sua família.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6747, 'nvi', 'Juízes', 'jz', 8, 28, 'Assim Midiã foi subjugado pelos israelitas, e não tornou a erguer a cabeça. Durante a vida de Gideão a terra desfrutou paz quarenta anos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6748, 'nvi', 'Juízes', 'jz', 8, 29, 'Jerubaal, filho de Joás, retirou-se e foi para casa, onde ficou morando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6749, 'nvi', 'Juízes', 'jz', 8, 30, 'Teve setenta filhos, todos gerados por ele, pois tinha muitas mulheres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6750, 'nvi', 'Juízes', 'jz', 8, 31, 'Sua concubina, que morava em Siquém, também lhe deu um filho, a quem ele deu o nome de Abimeleque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6751, 'nvi', 'Juízes', 'jz', 8, 32, 'Gideão, filho de Joás, morreu em idade avançada e foi sepultado no túmulo de seu pai, Joás, em Ofra dos abiezritas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6752, 'nvi', 'Juízes', 'jz', 8, 33, 'Logo depois que Gideão morreu, os israelitas voltaram a prostituir-se com os baalins, cultuando-os. Ergueram Baal-Berite como seu deus, e');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6753, 'nvi', 'Juízes', 'jz', 8, 34, 'não se lembraram do Senhor, do seu Deus, que os tinha livrado das mãos dos seus inimigos em redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6754, 'nvi', 'Juízes', 'jz', 8, 35, 'Também não foram bondosos com a família de Jerubaal, isto é, Gideão, reconhecendo todo o bem que ele tinha feito por Israel.');
