INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6755, 'nvi', 'Juízes', 'jz', 9, 1, 'Abimeleque, filho de Jerubaal, foi aos irmãos de sua mãe em Siquém e disse a eles e a todo o clã da família de sua mãe:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6756, 'nvi', 'Juízes', 'jz', 9, 2, '"Perguntem a todos os cidadãos de Siquém: O que é melhor para vocês: ter todos os setenta filhos de Jerubaal governando sobre vocês, ou somente um homem? Lembrem-se de que eu sou sua própria carne".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6757, 'nvi', 'Juízes', 'jz', 9, 3, 'Os irmãos de sua mãe repetiram tudo aos cidadãos de Siquém, e estes se mostraram propensos a seguir Abimeleque, pois disseram: "Ele é nosso irmão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6758, 'nvi', 'Juízes', 'jz', 9, 4, 'Deram-lhe setenta peças de prata tiradas do templo de Baal-Berite, as quais Abimeleque usou para contratar alguns desocupados e vadios, que se tornaram seus seguidores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6759, 'nvi', 'Juízes', 'jz', 9, 5, 'Foi à casa de seu pai em Ofra e matou seus setenta irmãos, filhos de Jerubaal, sobre uma rocha. Mas Jotão, o filho mais novo de Jerubaal, escondeu-se e escapou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6760, 'nvi', 'Juízes', 'jz', 9, 6, 'Então todos os cidadãos de Siquém e de Bete-Milo reuniram-se ao lado do Carvalho, junto à coluna de Siquém, para coroar Abimeleque rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6761, 'nvi', 'Juízes', 'jz', 9, 7, 'Quando Jotão soube disso, subiu ao topo do monte Gerizim e gritou para eles: "Ouçam-me, cidadãos de Siquém, para que Deus os ouça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6762, 'nvi', 'Juízes', 'jz', 9, 8, 'Certo dia as árvores saíram para ungir um rei para si. Disseram à oliveira: ‘Seja o nosso rei! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6763, 'nvi', 'Juízes', 'jz', 9, 9, '"A oliveira, porém, respondeu: ‘Deveria eu renunciar ao meu azeite, com o qual se presta honra aos deuses e aos homens, para dominar sobre as árvores? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6764, 'nvi', 'Juízes', 'jz', 9, 10, '"Então as árvores disseram à figueira: ‘Venha ser o nosso rei! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6765, 'nvi', 'Juízes', 'jz', 9, 11, '"A figueira, porém, respondeu: ‘Deveria eu renunciar ao meu fruto saboroso e doce, para dominar sobre as árvores? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6766, 'nvi', 'Juízes', 'jz', 9, 12, '"Depois as árvores disseram à videira: ‘Venha ser o nosso rei! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6767, 'nvi', 'Juízes', 'jz', 9, 13, '"A videira, porém, respondeu: ‘Deveria eu renunciar ao meu vinho, que alegra os deuses e os homens, para ter domínio sobre as árvores? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6768, 'nvi', 'Juízes', 'jz', 9, 14, '"Finalmente todas as árvores disseram ao espinheiro: ‘Venha ser o nosso rei! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6769, 'nvi', 'Juízes', 'jz', 9, 15, '"O espinheiro disse às árvores: ‘Se querem realmente ungir-me rei sobre vocês, venham abrigar-se à minha sombra; do contrário, sairá fogo do espinheiro e consumirá até os cedros do Líbano! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6770, 'nvi', 'Juízes', 'jz', 9, 16, '"Será que vocês agiram de fato com sinceridade quando fizeram Abimeleque rei? Foram justos com Jerubaal e sua família, como ele merecia?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6771, 'nvi', 'Juízes', 'jz', 9, 17, 'Meu pai lutou por vocês e arriscou a vida para livrá-los das mãos de Midiã,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6772, 'nvi', 'Juízes', 'jz', 9, 18, 'hoje, porém, vocês se revoltaram contra a família de meu pai, mataram seus setenta filhos sobre a mesma rocha, e proclamaram Abimeleque, o filho de sua escrava, rei sobre os cidadãos de Siquém pelo fato de ser irmão de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6773, 'nvi', 'Juízes', 'jz', 9, 19, 'Se hoje vocês de fato agiram com sinceridade para com Jerubaal e sua família, alegrem-se com Abimeleque, e alegre-se ele com vocês!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6774, 'nvi', 'Juízes', 'jz', 9, 20, 'Entretanto, se não foi assim, que saia fogo de Abimeleque e consuma os cidadãos de Siquém e de Bete-Milo, e que saia fogo dos cidadãos de Siquém e de Bete-Milo, e consuma Abimeleque! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6775, 'nvi', 'Juízes', 'jz', 9, 21, 'Depois Jotão fugiu para Beer, e foi morar ali, longe de seu irmão Abimeleque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6776, 'nvi', 'Juízes', 'jz', 9, 22, 'Fazia três anos que Abimeleque governava Israel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6777, 'nvi', 'Juízes', 'jz', 9, 23, 'quando Deus enviou um espírito maligno entre Abimeleque e os cidadãos de Siquém, e estes agiram traiçoeiramente contra Abimeleque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6778, 'nvi', 'Juízes', 'jz', 9, 24, 'Isso aconteceu para que o crime contra os setenta filhos de Jerubaal, o derramamento do sangue deles, fosse vingado em seu irmão Abimeleque e nos cidadãos de Siquém que o ajudaram a assassinar os seus irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6779, 'nvi', 'Juízes', 'jz', 9, 25, 'Os cidadãos de Siquém enviaram homens para o alto das colinas para emboscarem os que passassem por ali, e Abimeleque foi informado disso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6780, 'nvi', 'Juízes', 'jz', 9, 26, 'Nesse meio tempo Gaal, filho de Ebede, mudou-se com seus parentes para Siquém, cujos cidadãos confiavam nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6781, 'nvi', 'Juízes', 'jz', 9, 27, 'Sucedeu que foram ao campo, colheram uvas, pisaram-nas, e fizeram uma festa no templo do seu deus. Comendo e bebendo, amaldiçoaram Abimeleque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6782, 'nvi', 'Juízes', 'jz', 9, 28, 'Então Gaal, filho de Ebede, disse: "Quem é Abimeleque para que o sirvamos? E quem é Siquém? Não é ele o filho de Jerubaal, e não é Zebul o seu representante? Sirvam aos homens de Hamor, o pai de Siquém! Por que servir a Abimeleque?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6783, 'nvi', 'Juízes', 'jz', 9, 29, 'Ah! Se eu tivesse esse povo sob o meu comando! Eu me livraria dele. E diria a Abimeleque: ‘Mobilize o seu exército e venha! ’"');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6784, 'nvi', 'Juízes', 'jz', 9, 30, 'Quando Zebul, o governante da cidade, ouviu o que dizia Gaal, filho de Ebede, ficou indignado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6785, 'nvi', 'Juízes', 'jz', 9, 31, 'Secretamente enviou mensageiros a Abimeleque dizendo: "Gaal, filho de Ebede, e seus parentes vieram a Siquém e estão agitando a cidade contra você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6786, 'nvi', 'Juízes', 'jz', 9, 32, 'Venha de noite, você e seus homens, e fiquem à espera no campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6787, 'nvi', 'Juízes', 'jz', 9, 33, 'De manhã, ao nascer do sol, avance contra a cidade. Quando Gaal e sua tropa atacarem, faça com eles o que achar melhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6788, 'nvi', 'Juízes', 'jz', 9, 34, 'E assim Abimeleque e todas as suas tropas partiram de noite e prepararam emboscadas perto de Siquém, em quatro companhias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6789, 'nvi', 'Juízes', 'jz', 9, 35, 'Ora, Gaal, filho de Ebede, tinha saído e estava à porta da cidade quando Abimeleque e seus homens saíram da sua emboscada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6790, 'nvi', 'Juízes', 'jz', 9, 36, 'Quando Gaal os viu, disse a Zebul: "Veja, vem gente descendo do alto das colinas! " Zebul, porém, respondeu: "Você está confundindo as sombras dos montes com homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6791, 'nvi', 'Juízes', 'jz', 9, 37, 'Mas Gaal tornou a falar: "Veja, vem gente descendo da parte central do território, e uma companhia está vindo pelo caminho do carvalho dos Adivinhadores".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6792, 'nvi', 'Juízes', 'jz', 9, 38, 'Disse-lhe Zebul: "Onde está toda aquela sua conversa? Você dizia: ‘Quem é Abimeleque, para que o sirvamos? ’ Não são estes os homens que você ridicularizou? Saia e lute contra eles! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6793, 'nvi', 'Juízes', 'jz', 9, 39, 'Então Gaal conduziu para fora os cidadãos de Siquém e lutou contra Abimeleque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6794, 'nvi', 'Juízes', 'jz', 9, 40, 'Abimeleque o perseguiu, e ele fugiu. Muitos dos homens de Siquém caíram mortos ao longo de todo o caminho, até à porta da cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6795, 'nvi', 'Juízes', 'jz', 9, 41, 'Abimeleque permaneceu em Arumá. E Zebul expulsou Gaal e os seus parentes de Siquém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6796, 'nvi', 'Juízes', 'jz', 9, 42, 'No dia seguinte o povo de Siquém saiu aos campos, e Abimeleque ficou sabendo disso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6797, 'nvi', 'Juízes', 'jz', 9, 43, 'Então dividiu os seus homens em três companhias e armou emboscadas no campo. Quando viu o povo saindo da cidade, levantou-se contra ele e atacou-o.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6798, 'nvi', 'Juízes', 'jz', 9, 44, 'Abimeleque e as tropas que estavam com ele avançaram até à porta da cidade. Então duas companhias avançaram sobre os que estavam nos campos e os mataram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6799, 'nvi', 'Juízes', 'jz', 9, 45, 'E Abimeleque atacou a cidade o dia todo, até conquistá-la e matar o seu povo. Depois destruiu a cidade e espalhou sal sobre ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6800, 'nvi', 'Juízes', 'jz', 9, 46, 'Ao saberem disso, os cidadãos que estavam na torre de Siquém entraram na fortaleza do templo de El-Berite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6801, 'nvi', 'Juízes', 'jz', 9, 47, 'Quando Abimeleque soube que se haviam reunido lá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6802, 'nvi', 'Juízes', 'jz', 9, 48, 'ele e todos os seus homens subiram o monte Zalmom. Ele apanhou um machado e cortou um galho de árvore e o pôs nos ombros. Então deu esta ordem aos homens que estavam com ele: "Rápido! Façam o que eu fizer! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6803, 'nvi', 'Juízes', 'jz', 9, 49, 'Todos os homens cortaram galhos e seguiram Abimeleque. Empilharam os galhos junto à fortaleza e a incendiaram. Assim morreu também o povo que estava na torre de Siquém, cerca de mil homens e mulheres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6804, 'nvi', 'Juízes', 'jz', 9, 50, 'A seguir Abimeleque foi a Tebes, sitiou-a e conquistou-a.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6805, 'nvi', 'Juízes', 'jz', 9, 51, 'Mas dentro da cidade havia uma torre bastante forte, para a qual fugiram todos os homens e mulheres, todo o povo da cidade. Trancaram-se por dentro e subiram para o telhado da torre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6806, 'nvi', 'Juízes', 'jz', 9, 52, 'Abimeleque foi para a torre e atacou-a. E, quando se aproximava da entrada da torre para incendiá-la,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6807, 'nvi', 'Juízes', 'jz', 9, 53, 'uma mulher jogou-lhe uma pedra de moinho na cabeça, que lhe rachou o crânio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6808, 'nvi', 'Juízes', 'jz', 9, 54, 'Imediatamente ele chamou seu escudeiro e lhe ordenou: "Tire a espada e mate-me, para que não digam que uma mulher me matou". Então o jovem o atravessou, e ele morreu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6809, 'nvi', 'Juízes', 'jz', 9, 55, 'Quando os israelitas viram que Abimeleque estava morto, voltaram para casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6810, 'nvi', 'Juízes', 'jz', 9, 56, 'Assim Deus retribuiu a maldade que Abimeleque praticara contra o seu pai, matando os seus setenta irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (6811, 'nvi', 'Juízes', 'jz', 9, 57, 'Deus fez também os homens de Siquém pagarem por toda a sua maldade. A maldição de Jotão, filho de Jerubaal, caiu sobre eles.');
