INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13262, 'nvi', 'Jó', 'jó', 17, 1, '"Meu espírito está quebrantado, os meus dias se encurtam, a sepultura me espera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13263, 'nvi', 'Jó', 'jó', 17, 2, 'A verdade é que zombadores me rodeiam, e tenho que ficar olhando a hostilidade deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13264, 'nvi', 'Jó', 'jó', 17, 3, '"Dá-me, ó Deus, a garantia que exiges. Quem, senão tu, me dará segurança?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13265, 'nvi', 'Jó', 'jó', 17, 4, 'Fechaste as mentes deles para o entendimento, e com isso não os deixarás triunfar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13266, 'nvi', 'Jó', 'jó', 17, 5, 'Se alguém denunciar os seus amigos por recompensa, os olhos dos filhos dele fraquejarão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13267, 'nvi', 'Jó', 'jó', 17, 6, '"mas de mim Deus fez um provérbio para todos, um homem em cujo rosto os outros cospem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13268, 'nvi', 'Jó', 'jó', 17, 7, 'Meus olhos se turvaram de tristeza; o meu corpo não passa de uma sombra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13269, 'nvi', 'Jó', 'jó', 17, 8, 'Os íntegros ficam atônitos em face disso, e os inocentes se levantam contra os ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13270, 'nvi', 'Jó', 'jó', 17, 9, 'Mas os justos se manterão firmes em seus caminhos, e os homens de mãos puras se tornarão cada vez mais fortes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13271, 'nvi', 'Jó', 'jó', 17, 10, '"Venham, porém, vocês todos, e façam nova tentativa! Não acharei nenhum sábio entre vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13272, 'nvi', 'Jó', 'jó', 17, 11, 'Foram-se os meus dias, os meus planos fracassaram, como também os desejos do meu coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13273, 'nvi', 'Jó', 'jó', 17, 12, 'Andam querendo tornar a noite em dia; ante a aproximação das trevas dizem: ‘Vem chegando a luz’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13274, 'nvi', 'Jó', 'jó', 17, 13, 'Ora, se o único lar pelo qual espero é a sepultura; se estendo a minha cama nas trevas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13275, 'nvi', 'Jó', 'jó', 17, 14, 'se digo à corrupção mortal: Você é o meu pai, e se aos vermes digo: Vocês são minha mãe e minha irmã,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13276, 'nvi', 'Jó', 'jó', 17, 15, 'onde está então minha esperança? Quem poderá ver alguma esperança para mim?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13277, 'nvi', 'Jó', 'jó', 17, 16, 'Descerá ela às portas do Sheol? Desceremos juntos ao pó? "');
