INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13421, 'nvi', 'Jó', 'jó', 23, 1, 'Então Jó respondeu:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13422, 'nvi', 'Jó', 'jó', 23, 2, '"Até agora me queixo com amargura; a mão dele é pesada, a despeito de meu gemido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13423, 'nvi', 'Jó', 'jó', 23, 3, 'Se tão-somente eu soubesse onde encontrá-lo e ir à sua habitação!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13424, 'nvi', 'Jó', 'jó', 23, 4, 'Eu lhe apresentaria a minha causa e encheria a minha boca de argumentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13425, 'nvi', 'Jó', 'jó', 23, 5, 'Estudaria o que ele me respondesse e analisaria o que me dissesse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13426, 'nvi', 'Jó', 'jó', 23, 6, 'Será que ele se oporia a mim com grande poder? Não, ele não me faria acusações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13427, 'nvi', 'Jó', 'jó', 23, 7, 'O homem íntegro poderia apresentar-lhe sua causa; eu seria liberto para sempre de quem me julga.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13428, 'nvi', 'Jó', 'jó', 23, 8, '"Mas, se vou para o oriente, lá ele não está; se vou para o ocidente, não o encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13429, 'nvi', 'Jó', 'jó', 23, 9, 'Quando ele está em ação no norte, não o enxergo; quando vai para o sul, nem sombra dele eu vejo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13430, 'nvi', 'Jó', 'jó', 23, 10, 'Mas ele conhece o caminho por onde ando; se me puser à prova, aparecerei como o ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13431, 'nvi', 'Jó', 'jó', 23, 11, 'Meus pés seguiram de perto as suas pegadas; mantive-me no seu caminho, sem desviar-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13432, 'nvi', 'Jó', 'jó', 23, 12, 'Não me afastei dos mandamentos dos seus lábios; dei mais valor às palavras de sua boca, do que ao meu pão de cada dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13433, 'nvi', 'Jó', 'jó', 23, 13, '"Mas ele é ele! Quem poderá fazer-lhe oposição? Ele faz o que quer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13434, 'nvi', 'Jó', 'jó', 23, 14, 'Executa o seu decreto contra mim, e tem muitos outros semelhantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13435, 'nvi', 'Jó', 'jó', 23, 15, 'Por isso fico apavorado diante dele; pensar nisso me enche de medo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13436, 'nvi', 'Jó', 'jó', 23, 16, 'Deus fez desmaiar o meu coração; o Todo-poderoso causou-me pavor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13437, 'nvi', 'Jó', 'jó', 23, 17, 'Contudo não fui silenciado pelas trevas, pelas densas trevas que cobrem o meu rosto.');
