INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13438, 'nvi', 'Jó', 'jó', 24, 1, '"Por que o Todo-poderoso não marca as datas para julgamento? Por que aqueles que o conhecem não chegam a vê-las?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13439, 'nvi', 'Jó', 'jó', 24, 2, 'Há os que mudam os marcos dos limites e apascentam rebanhos que eles roubaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13440, 'nvi', 'Jó', 'jó', 24, 3, 'Levam o jumento que pertence ao órfão e tomam o boi da viúva como penhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13441, 'nvi', 'Jó', 'jó', 24, 4, 'Forçam os necessitados a saírem do caminho e os pobres da terra a esconder-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13442, 'nvi', 'Jó', 'jó', 24, 5, 'Como jumentos selvagens no deserto, os pobres vão em busca de comida; da terra deserta a obtêm para os seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13443, 'nvi', 'Jó', 'jó', 24, 6, 'Juntam forragem nos campos e respigam nas vinhas dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13444, 'nvi', 'Jó', 'jó', 24, 7, 'Pela falta de roupas, passam a noite nus; não têm com que cobrir-se no frio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13445, 'nvi', 'Jó', 'jó', 24, 8, 'Encharcados pelas chuvas das montanhas, abraçam-se às rochas por falta de abrigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13446, 'nvi', 'Jó', 'jó', 24, 9, 'A criança órfã é arrancada do seio de sua mãe; o recém-nascido do pobre é tomado para pagar uma dívida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13447, 'nvi', 'Jó', 'jó', 24, 10, 'Por falta de roupas, andam nus; carregam os feixes, mas continuam famintos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13448, 'nvi', 'Jó', 'jó', 24, 11, 'Espremem azeitonas dentro dos seus muros; pisam uvas nos lagares, mas assim mesmo sofrem sede.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13449, 'nvi', 'Jó', 'jó', 24, 12, 'Sobem da cidade os gemidos dos que estão para morrer, e as almas dos feridos clamam por socorro. Mas Deus não vê mal nisso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13450, 'nvi', 'Jó', 'jó', 24, 13, '"Há os que se revoltam contra a luz, não conhecem os caminhos dela e não permanecem em suas veredas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13451, 'nvi', 'Jó', 'jó', 24, 14, 'De manhã o assassino se levanta e mata os pobres e os necessitados; de noite age como ladrão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13452, 'nvi', 'Jó', 'jó', 24, 15, 'Os olhos do adúltero ficam à espera do crepúsculo; ‘Nenhum olho me verá’, pensa ele; e mantém oculto o rosto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13453, 'nvi', 'Jó', 'jó', 24, 16, 'No escuro os homens invadem casas, mas de dia se enclausuram; não querem saber da luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13454, 'nvi', 'Jó', 'jó', 24, 17, 'Para eles a manhã é tremenda escuridão; eles são amigos dos pavores das trevas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13455, 'nvi', 'Jó', 'jó', 24, 18, '"São, porém, como espuma sobre as águas; sua parte da terra foi amaldiçoada, e por isso ninguém vai às vinhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13456, 'nvi', 'Jó', 'jó', 24, 19, 'Assim como o calor e a seca depressa consomem a neve derretida, assim a sepultura consome os que pecaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13457, 'nvi', 'Jó', 'jó', 24, 20, 'Sua mãe os esquece, os vermes se banqueteiam neles. Ninguém se lembra dos maus; quebram-se como árvores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13458, 'nvi', 'Jó', 'jó', 24, 21, 'Devoram a estéril e sem filhos e não mostram bondade para com a viúva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13459, 'nvi', 'Jó', 'jó', 24, 22, 'Mas Deus, por seu poder, os arranca; embora firmemente estabelecidos, a vida deles não tem segurança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13460, 'nvi', 'Jó', 'jó', 24, 23, 'Ele poderá deixá-los descansar, sentindo-se seguros, mas os vigia atento nos caminhos que seguem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13461, 'nvi', 'Jó', 'jó', 24, 24, 'Por um breve instante são exaltados, e depois se vão; colhidos como todos os demais; ceifados como espigas de cereal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13462, 'nvi', 'Jó', 'jó', 24, 25, '"Se não é assim, quem poderá provar que minto e reduzir a nada as minhas palavras? "');
