INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13463, 'nvi', 'Jó', 'jó', 25, 1, 'Então Bildade, de Suá, respondeu:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13464, 'nvi', 'Jó', 'jó', 25, 2, '"O domínio e o temor pertencem a Deus; ele impõe ordem nas alturas, que a ele pertencem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13465, 'nvi', 'Jó', 'jó', 25, 3, 'Seria possível contar os seus exércitos? E a sua luz, sobre quem não se levanta?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13466, 'nvi', 'Jó', 'jó', 25, 4, 'Como pode então o homem ser justo diante de Deus? Como pode ser puro quem nasce de mulher?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13467, 'nvi', 'Jó', 'jó', 25, 5, 'Se nem a lua é brilhante e as estrelas são puras aos olhos dele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13468, 'nvi', 'Jó', 'jó', 25, 6, 'muito menos o será o homem, que não passa de larva, o filho do homem, que não passa de verme! "');
