INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13469, 'nvi', 'Jó', 'jó', 26, 1, 'Então Jó respondeu:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13470, 'nvi', 'Jó', 'jó', 26, 2, 'Grande foi a ajuda que você deu ao desvalido! Que socorro você prestou ao braço frágil!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13471, 'nvi', 'Jó', 'jó', 26, 3, 'Belo conselho você ofereceu a quem não é sábio, e que grande sabedoria você revelou!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13472, 'nvi', 'Jó', 'jó', 26, 4, 'Quem o ajudou a proferir essas palavras, e por meio de que espírito você falou?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13473, 'nvi', 'Jó', 'jó', 26, 5, '"Os mortos estão em grande angústia sob as águas e os que nelas vivem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13474, 'nvi', 'Jó', 'jó', 26, 6, 'Nu está o Sheol diante de Deus, e nada encobre a Destruição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13475, 'nvi', 'Jó', 'jó', 26, 7, 'Ele estende os céus do norte sobre o espaço vazio; suspende a terra sobre o nada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13476, 'nvi', 'Jó', 'jó', 26, 8, 'Envolve as águas em suas nuvens, e estas não se rompem sob o peso delas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13477, 'nvi', 'Jó', 'jó', 26, 9, 'Ele cobre a face da lua cheia estendendo sobre ela as suas nuvens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13478, 'nvi', 'Jó', 'jó', 26, 10, 'Traça o horizonte sobre a superfície das águas para servir de limite entre a luz e as trevas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13479, 'nvi', 'Jó', 'jó', 26, 11, 'As colunas dos céus estremecem e ficam perplexas diante da sua repreensão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13480, 'nvi', 'Jó', 'jó', 26, 12, 'Com seu poder agitou violentamente o mar; com sua sabedoria despedaçou Raabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13481, 'nvi', 'Jó', 'jó', 26, 13, 'Com seu sopro os céus ficaram límpidos; sua mão feriu a serpente arisca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13482, 'nvi', 'Jó', 'jó', 26, 14, 'E isso tudo é apenas a borda das suas obras! Um suave sussurro é o que ouvimos dele. Mas quem poderá compreender o trovão do seu poder? "');
