INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13722, 'nvi', 'Jó', 'jó', 35, 1, 'Eliú prosseguiu:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13723, 'nvi', 'Jó', 'jó', 35, 2, '"Você acha que isso é justo? Pois você diz: ‘Serei absolvido por Deus’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13724, 'nvi', 'Jó', 'jó', 35, 3, 'Contudo, você lhe pergunta: ‘Que vantagem tenho eu, e o que ganho, se não pecar? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13725, 'nvi', 'Jó', 'jó', 35, 4, '"Desejo responder-lhe, a você e aos seus amigos que estão com você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13726, 'nvi', 'Jó', 'jó', 35, 5, 'Olhe para os céus e veja; mire as nuvens, tão elevadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13727, 'nvi', 'Jó', 'jó', 35, 6, 'Se você pecar, em que isso o afetará? Se os seus pecados forem muitos, que é que isso lhe fará?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13728, 'nvi', 'Jó', 'jó', 35, 7, 'Se você for justo, o que lhe dará? Ou o que ele receberá de sua mão?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13729, 'nvi', 'Jó', 'jó', 35, 8, 'A sua impiedade só afeta aos homens, seus semelhantes, e a sua justiça, aos filhos dos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13730, 'nvi', 'Jó', 'jó', 35, 9, '"Os homens se lamentam sob fardos de opressão; imploram que os libertem do braço dos poderosos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13731, 'nvi', 'Jó', 'jó', 35, 10, 'Mas não há quem pergunte: ‘Onde está Deus, o meu Criador, que de noite faz surgirem cânticos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13732, 'nvi', 'Jó', 'jó', 35, 11, 'que nos ensina mais que aos animais da terra e nos faz mais sábios que as aves dos céus? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13733, 'nvi', 'Jó', 'jó', 35, 12, 'Quando clamam, ele não responde por causa da arrogância dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13734, 'nvi', 'Jó', 'jó', 35, 13, 'Aliás, Deus não escuta a vã súplica que fazem; o Todo-poderoso não lhe dá atenção.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13735, 'nvi', 'Jó', 'jó', 35, 14, 'Pois muito menos escutará quando você disser que não o vê, que a sua causa está diante dele e que você tem que esperar por ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13736, 'nvi', 'Jó', 'jó', 35, 15, 'Mais que isso, que a sua ira jamais castiga e que ele não dá a mínima atenção à iniqüidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (13737, 'nvi', 'Jó', 'jó', 35, 16, 'Assim é que Jó abre a sua boca para dizer palavras vãs; em sua ignorância ele multiplica palavras".');
