INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24895, 'nvi', 'Lucas', 'lc', 1, 1, 'Muitos já se dedicaram a elaborar um relato dos fatos que se cumpriram entre nós,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24896, 'nvi', 'Lucas', 'lc', 1, 2, 'conforme nos foram transmitidos por aqueles que desde o início foram testemunhas oculares e servos da palavra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24897, 'nvi', 'Lucas', 'lc', 1, 3, 'Eu mesmo investiguei tudo cuidadosamente, desde o começo, e decidi escrever-te um relato ordenado, ó excelentíssimo Teófilo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24898, 'nvi', 'Lucas', 'lc', 1, 4, 'para que tenhas a certeza das coisas que te foram ensinadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24899, 'nvi', 'Lucas', 'lc', 1, 5, 'No tempo de Herodes, rei da Judéia, havia um sacerdote chamado Zacarias, que pertencia ao grupo sacerdotal de Abias; Isabel, sua mulher, também era descendente de Arão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24900, 'nvi', 'Lucas', 'lc', 1, 6, 'Ambos eram justos aos olhos de Deus, obedecendo de modo irrepreensível a todos os mandamentos e preceitos do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24901, 'nvi', 'Lucas', 'lc', 1, 7, 'Mas eles não tinham filhos, porque Isabel era estéril; e ambos eram de idade avançada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24902, 'nvi', 'Lucas', 'lc', 1, 8, 'Certa vez, estando de serviço o seu grupo, Zacarias estava servindo como sacerdote diante de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24903, 'nvi', 'Lucas', 'lc', 1, 9, 'Ele foi escolhido por sorteio, de acordo com o costume do sacerdócio, para entrar no santuário do Senhor e oferecer incenso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24904, 'nvi', 'Lucas', 'lc', 1, 10, 'Chegando a hora de oferecer incenso, o povo todo estava orando do lado de fora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24905, 'nvi', 'Lucas', 'lc', 1, 11, 'Então um anjo do Senhor apareceu a Zacarias, à direita do altar do incenso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24906, 'nvi', 'Lucas', 'lc', 1, 12, 'Quando Zacarias o viu, perturbou-se e foi dominado pelo medo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24907, 'nvi', 'Lucas', 'lc', 1, 13, 'Mas o anjo lhe disse: "Não tenha medo, Zacarias; sua oração foi ouvida. Isabel, sua mulher, lhe dará um filho, e você lhe dará o nome de João.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24908, 'nvi', 'Lucas', 'lc', 1, 14, 'Ele será motivo de prazer e de alegria para você, e muitos se alegrarão por causa do nascimento dele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24909, 'nvi', 'Lucas', 'lc', 1, 15, 'pois será grande aos olhos do Senhor. Ele nunca tomará vinho nem bebida fermentada, e será cheio do Espírito Santo desde antes do seu nascimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24910, 'nvi', 'Lucas', 'lc', 1, 16, 'Fará retornar muitos dentre o povo de Israel ao Senhor, o seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24911, 'nvi', 'Lucas', 'lc', 1, 17, 'E irá adiante do Senhor, no espírito e no poder de Elias, para fazer voltar o coração dos pais a seus filhos e os desobedientes à sabedoria dos justos, para deixar um povo preparado para o Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24912, 'nvi', 'Lucas', 'lc', 1, 18, 'Zacarias perguntou ao anjo: "Como posso ter certeza disso? Sou velho, e minha mulher é de idade avançada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24913, 'nvi', 'Lucas', 'lc', 1, 19, 'O anjo respondeu: "Sou Gabriel, o que está sempre na presença de Deus. Fui enviado para lhe transmitir estas boas novas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24914, 'nvi', 'Lucas', 'lc', 1, 20, 'Agora você ficará mudo. Não poderá falar até o dia em que isso acontecer, porque não acreditou em minhas palavras, que se cumprirão no tempo oportuno".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24915, 'nvi', 'Lucas', 'lc', 1, 21, 'Enquanto isso, o povo esperava por Zacarias, estranhando sua demora no santuário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24916, 'nvi', 'Lucas', 'lc', 1, 22, 'Quando saiu, não conseguia falar nada; o povo percebeu então que ele tivera uma visão no santuário. Zacarias fazia sinais para eles, mas permanecia mudo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24917, 'nvi', 'Lucas', 'lc', 1, 23, 'Quando se completou seu período de serviço, ele voltou para casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24918, 'nvi', 'Lucas', 'lc', 1, 24, 'Depois disso, Isabel, sua mulher, engravidou e durante cinco meses não saiu de casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24919, 'nvi', 'Lucas', 'lc', 1, 25, 'E ela dizia: "Isto é obra do Senhor fez! Agora ele olhou para mim com favor, para desfazer a minha humilhação perante o povo. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24920, 'nvi', 'Lucas', 'lc', 1, 26, 'No sexto mês Deus enviou o anjo Gabriel a Nazaré, cidade da Galiléia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24921, 'nvi', 'Lucas', 'lc', 1, 27, 'a uma virgem prometida em casamento a certo homem chamado José, descendente de Davi. O nome da virgem era Maria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24922, 'nvi', 'Lucas', 'lc', 1, 28, 'O anjo, aproximando-se dela, disse: "Alegre-se, agraciada! O Senhor está com você! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24923, 'nvi', 'Lucas', 'lc', 1, 29, 'Maria ficou perturbada com essas palavras, pensando no que poderia significar esta saudação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24924, 'nvi', 'Lucas', 'lc', 1, 30, 'Mas o anjo lhe disse: "Não tenha medo, Maria; você foi agraciada por Deus!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24925, 'nvi', 'Lucas', 'lc', 1, 31, 'Você ficará grávida e dará à luz um filho, e lhe porá o nome de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24926, 'nvi', 'Lucas', 'lc', 1, 32, 'Ele será grande e será chamado Filho do Altíssimo. O Senhor Deus lhe dará o trono de seu pai Davi,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24927, 'nvi', 'Lucas', 'lc', 1, 33, 'e ele reinará para sempre sobre o povo de Jacó; seu Reino jamais terá fim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24928, 'nvi', 'Lucas', 'lc', 1, 34, 'Perguntou Maria ao anjo: "Como acontecerá isso, se sou virgem? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24929, 'nvi', 'Lucas', 'lc', 1, 35, 'O anjo respondeu: "O Espírito Santo virá sobre você, e o poder do Altíssimo a cobrirá com a sua sombra. Assim, aquele que há de nascer será chamado santo, Filho de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24930, 'nvi', 'Lucas', 'lc', 1, 36, 'Também Isabel, sua parenta, terá um filho na velhice; aquela que diziam ser estéril já está em seu sexto mês de gestação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24931, 'nvi', 'Lucas', 'lc', 1, 37, 'Pois nada é impossível para Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24932, 'nvi', 'Lucas', 'lc', 1, 38, 'Respondeu Maria: "Sou serva do Senhor; que aconteça comigo conforme a tua palavra". Então o anjo a deixou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24933, 'nvi', 'Lucas', 'lc', 1, 39, 'Naqueles dias, Maria preparou-se e foi depressa para a uma cidade da região montanhosa da Judéia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24934, 'nvi', 'Lucas', 'lc', 1, 40, 'onde entrou na casa de Zacarias e saudou Isabel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24935, 'nvi', 'Lucas', 'lc', 1, 41, 'Quando Isabel ouviu a saudação de Maria, o bebê agitou-se em seu ventre, e Isabel ficou cheia do Espírito Santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24936, 'nvi', 'Lucas', 'lc', 1, 42, 'Em alta voz exclamou: "Bendita é você entre as mulheres, e bendito é o filho que você dará à luz!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24937, 'nvi', 'Lucas', 'lc', 1, 43, 'Mas por que sou tão agraciada, a ponto de me visitar a mãe do meu Senhor?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24938, 'nvi', 'Lucas', 'lc', 1, 44, 'Logo que a sua saudação chegou aos meus ouvidos, o bebê que está em meu ventre agitou-se de alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24939, 'nvi', 'Lucas', 'lc', 1, 45, 'Feliz é aquela que creu que se cumprirá aquilo que o Senhor lhe disse! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24940, 'nvi', 'Lucas', 'lc', 1, 46, 'Então disse Maria: "Minha alma engrandece ao Senhor');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24941, 'nvi', 'Lucas', 'lc', 1, 47, 'e o meu espírito se alegra em Deus, meu Salvador,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24942, 'nvi', 'Lucas', 'lc', 1, 48, 'pois atentou para a humildade da sua serva. De agora em diante, todas as gerações me chamarão bem-aventurada,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24943, 'nvi', 'Lucas', 'lc', 1, 49, 'pois o Poderoso fez grandes coisas em meu favor; santo é o seu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24944, 'nvi', 'Lucas', 'lc', 1, 50, 'A sua misericórdia estende-se aos que o temem, de geração em geração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24945, 'nvi', 'Lucas', 'lc', 1, 51, 'Ele realizou poderosos feitos com seu braço; dispersou os que são soberbos no mais íntimo do coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24946, 'nvi', 'Lucas', 'lc', 1, 52, 'Derrubou governantes dos seus tronos, mas exaltou os humildes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24947, 'nvi', 'Lucas', 'lc', 1, 53, 'Encheu de coisas boas os famintos, mas despediu de mãos vazias os ricos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24948, 'nvi', 'Lucas', 'lc', 1, 54, 'Ajudou a seu servo Israel, lembrando-se da sua misericórdia');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24949, 'nvi', 'Lucas', 'lc', 1, 55, 'para com Abraão e seus descendentes para sempre, como dissera aos nossos antepassados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24950, 'nvi', 'Lucas', 'lc', 1, 56, 'Maria ficou com Isabel cerca de três meses e depois voltou para casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24951, 'nvi', 'Lucas', 'lc', 1, 57, 'Ao se completar o tempo de Isabel dar à luz, ela teve um filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24952, 'nvi', 'Lucas', 'lc', 1, 58, 'Seus vizinhos e parentes ouviram falar da grande misericórdia que o Senhor lhe havia demonstrado e se alegraram com ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24953, 'nvi', 'Lucas', 'lc', 1, 59, 'No oitavo dia foram circuncidar o menino e queriam dar-lhe o nome do pai, Zacarias;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24954, 'nvi', 'Lucas', 'lc', 1, 60, 'mas sua mãe tomou a palavra e disse: "Não! Ele será chamado João".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24955, 'nvi', 'Lucas', 'lc', 1, 61, 'Disseram-lhe: "Você não há nenhum parente com esse nome".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24956, 'nvi', 'Lucas', 'lc', 1, 62, 'Então fizeram sinais ao pai do menino, para saber como queria que a criança se chamasse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24957, 'nvi', 'Lucas', 'lc', 1, 63, 'Ele pediu uma tabuinha e, para admiração de todos, escreveu: "O nome dele é João".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24958, 'nvi', 'Lucas', 'lc', 1, 64, 'Imediatamente sua boca se abriu, sua língua se soltou e ele começou a falar, louvando a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24959, 'nvi', 'Lucas', 'lc', 1, 65, 'Todos os vizinhos ficaram cheios de temor, e por toda a região montanhosa da Judéia se falava sobre essas coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24960, 'nvi', 'Lucas', 'lc', 1, 66, 'Todos os que ouviam falar disso se perguntavam: "O que vai ser este menino? " Pois a mão do Senhor estava com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24961, 'nvi', 'Lucas', 'lc', 1, 67, 'Seu pai, Zacarias, foi cheio do Espírito Santo e profetizou:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24962, 'nvi', 'Lucas', 'lc', 1, 68, '"Louvado seja o Senhor, o Deus de Israel, porque visitou e redimiu o seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24963, 'nvi', 'Lucas', 'lc', 1, 69, 'Ele promoveu poderosa salvação para nós, na linhagem do seu servo Davi,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24964, 'nvi', 'Lucas', 'lc', 1, 70, '( como falara pelos seus santos profetas, na antigüidade ),');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24965, 'nvi', 'Lucas', 'lc', 1, 71, 'salvando-nos dos nossos inimigos e da mão de todos os que nos odeiam,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24966, 'nvi', 'Lucas', 'lc', 1, 72, 'para mostrar sua misericórdia aos nossos antepassados e lembrar sua santa aliança,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24967, 'nvi', 'Lucas', 'lc', 1, 73, 'o juramento que fez ao nosso pai Abraão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24968, 'nvi', 'Lucas', 'lc', 1, 74, 'resgatar-nos da mão dos nossos inimigos para servi-lo sem medo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24969, 'nvi', 'Lucas', 'lc', 1, 75, 'em santidade e justiça, diante dele todos os nossos dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24970, 'nvi', 'Lucas', 'lc', 1, 76, 'E você, menino, será chamado profeta do Altíssimo, pois irá adiante do Senhor, para lhe preparar o caminho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24971, 'nvi', 'Lucas', 'lc', 1, 77, 'para dar ao seu povo o conhecimento da salvação, mediante o perdão dos seus pecados,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24972, 'nvi', 'Lucas', 'lc', 1, 78, 'por causa das ternas misericórdias de nosso Deus, pelas quais do alto nos visitará o sol nascente');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24973, 'nvi', 'Lucas', 'lc', 1, 79, 'para brilhar sobre aqueles que estão vivendo nas trevas e na sombra da morte, e guiar nossos pés no caminho da paz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24974, 'nvi', 'Lucas', 'lc', 1, 80, 'E o menino crescia e se fortalecia no espírito; e viveu no deserto, até aparecer publicamente a Israel.');
