INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25365, 'nvi', 'Lucas', 'lc', 10, 1, 'Depois disso o Senhor designou outros setenta e dois e os enviou dois a dois, adiante dele, a todas as cidades e lugares para onde ele estava prestes a ir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25366, 'nvi', 'Lucas', 'lc', 10, 2, 'E lhes disse: "A colheita é grande, mas os trabalhadores são poucos. Portanto, peçam ao Senhor da colheita que mande trabalhadores para a sua colheita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25367, 'nvi', 'Lucas', 'lc', 10, 3, 'Vão! Eu os estou enviando como cordeiros entre lobos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25368, 'nvi', 'Lucas', 'lc', 10, 4, 'Não levem bolsa nem saco de viagem nem sandálias; e não saúdem ninguém pelo caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25369, 'nvi', 'Lucas', 'lc', 10, 5, '"Quando entrarem numa casa, digam primeiro: ‘Paz a esta casa’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25370, 'nvi', 'Lucas', 'lc', 10, 6, 'Se houver ali um homem de paz, a paz de vocês repousará sobre ele; se não, ela voltará para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25371, 'nvi', 'Lucas', 'lc', 10, 7, 'Fiquem naquela casa, e comam e bebam o que lhes derem, pois o trabalhador merece o seu salário. Não fiquem mudando de casa em casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25372, 'nvi', 'Lucas', 'lc', 10, 8, '"Quando entrarem numa cidade e forem bem recebidos, comam o que for posto diante de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25373, 'nvi', 'Lucas', 'lc', 10, 9, 'Curem os doentes que ali houver e digam-lhes: ‘O Reino de Deus está próximo de vocês’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25374, 'nvi', 'Lucas', 'lc', 10, 10, 'Mas quando entrarem numa cidade e não forem bem recebidos, saiam por suas ruas e digam:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25375, 'nvi', 'Lucas', 'lc', 10, 11, '‘Até o pó da sua cidade, que se apegou aos nossos pés, sacudimos contra vocês. Fiquem certos disto: O Reino de Deus está próximo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25376, 'nvi', 'Lucas', 'lc', 10, 12, 'Eu lhes digo: Naquele dia haverá mais tolerância para Sodoma do que para aquela cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25377, 'nvi', 'Lucas', 'lc', 10, 13, '"Ai de você, Corazim! Ai de você, Betsaida! Porque se os milagres que foram realizados entre vocês o fossem em Tiro e Sidom, há muito tempo elas se teriam arrependido, vestindo roupas de saco e cobrindo-se de cinzas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25378, 'nvi', 'Lucas', 'lc', 10, 14, 'Mas no juízo haverá menor rigor para Tiro e Sidom do que para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25379, 'nvi', 'Lucas', 'lc', 10, 15, 'E você, Cafarnaum: será elevada até o céu? Não; você descerá até ao Hades!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25380, 'nvi', 'Lucas', 'lc', 10, 16, '"Aquele que lhes dá ouvidos, está me dando ouvidos; aquele que os rejeita, está me rejeitando; mas aquele que me rejeita, está rejeitando aquele que me enviou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25381, 'nvi', 'Lucas', 'lc', 10, 17, 'Os setenta e dois voltaram alegres e disseram: "Senhor, até os demônios se submetem a nós, em teu nome".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25382, 'nvi', 'Lucas', 'lc', 10, 18, 'Ele respondeu: "Eu vi Satanás caindo do céu como relâmpago.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25383, 'nvi', 'Lucas', 'lc', 10, 19, 'Eu lhes dei autoridade para pisarem sobre cobras e escorpiões, e sobre todo o poder do inimigo; nada lhes fará dano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25384, 'nvi', 'Lucas', 'lc', 10, 20, 'Contudo, alegrem-se, não porque os espíritos se submetem a vocês, mas porque seus nomes estão escritos nos céus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25385, 'nvi', 'Lucas', 'lc', 10, 21, 'Naquela hora Jesus, exultando no Espírito Santo, disse: "Eu te louvo, Pai, Senhor do céu e da terra, porque escondeste estas coisas dos sábios e cultos e as revelaste aos pequeninos. Sim, Pai, pois assim foi do teu agrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25386, 'nvi', 'Lucas', 'lc', 10, 22, '"Todas as coisas me foram entregues por meu Pai. Ninguém sabe quem é o Filho, a não ser o Pai; e ninguém sabe quem é o Pai, a não ser o Filho e aqueles a quem o Filho o quiser revelar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25387, 'nvi', 'Lucas', 'lc', 10, 23, 'Então ele se voltou para os seus discípulos e lhes disse em particular: "Felizes são os olhos que vêem o que vocês vêem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25388, 'nvi', 'Lucas', 'lc', 10, 24, 'Pois eu lhes digo que muitos profetas e reis desejaram ver o que vocês estão vendo, mas não viram; e ouvir o que vocês estão ouvindo, mas não ouviram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25389, 'nvi', 'Lucas', 'lc', 10, 25, 'Certa ocasião, um perito na lei levantou-se para pôr Jesus à prova e lhe perguntou: "Mestre, o que preciso fazer para herdar a vida eterna? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25390, 'nvi', 'Lucas', 'lc', 10, 26, '"O que está escrito na Lei? ", respondeu Jesus. "Como você a lê? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25391, 'nvi', 'Lucas', 'lc', 10, 27, 'Ele respondeu: " ‘Ame o Senhor, o seu Deus de todo o seu coração, de toda a sua alma, de todas as suas forças e de todo o seu entendimento’ e ‘Ame o seu próximo como a si mesmo’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25392, 'nvi', 'Lucas', 'lc', 10, 28, 'Disse Jesus: "Você respondeu corretamente. Faça isso, e viverá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25393, 'nvi', 'Lucas', 'lc', 10, 29, 'Mas ele, querendo justificar-se, perguntou a Jesus: "E quem é o meu próximo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25394, 'nvi', 'Lucas', 'lc', 10, 30, 'Em resposta, disse Jesus: "Um homem descia de Jerusalém para Jericó, quando caiu nas mãos de assaltantes. Estes lhe tiraram as roupas, espancaram-no e se foram, deixando-o quase morto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25395, 'nvi', 'Lucas', 'lc', 10, 31, 'Aconteceu estar descendo pela mesma estrada um sacerdote. Quando viu o homem, passou pelo outro lado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25396, 'nvi', 'Lucas', 'lc', 10, 32, 'E assim também um levita; quando chegou ao lugar e o viu, passou pelo outro lado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25397, 'nvi', 'Lucas', 'lc', 10, 33, 'Mas um samaritano, estando de viagem, chegou onde se encontrava o homem e, quando o viu, teve piedade dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25398, 'nvi', 'Lucas', 'lc', 10, 34, 'Aproximou-se, enfaixou-lhe as feridas, derramando nelas vinho e óleo. Depois colocou-o sobre o seu próprio animal, levou-o para uma hospedaria e cuidou dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25399, 'nvi', 'Lucas', 'lc', 10, 35, 'No dia seguinte, deu dois denários ao hospedeiro e disse-lhe: ‘Cuide dele. Quando voltar lhe pagarei todas as despesas que você tiver’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25400, 'nvi', 'Lucas', 'lc', 10, 36, '"Qual destes três você acha que foi o próximo do homem que caiu nas mãos dos assaltantes? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25401, 'nvi', 'Lucas', 'lc', 10, 37, '"Aquele que teve misericórdia dele", respondeu o perito na lei. Jesus lhe disse: "Vá e faça o mesmo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25402, 'nvi', 'Lucas', 'lc', 10, 38, 'Caminhando Jesus e os seus discípulos, chegaram a um povoado, onde certa mulher chamada Marta o recebeu em sua casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25403, 'nvi', 'Lucas', 'lc', 10, 39, 'Maria, sua irmã, ficou sentada aos pés do Senhor, ouvindo-lhe a palavra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25404, 'nvi', 'Lucas', 'lc', 10, 40, 'Marta, porém, estava ocupada com muito serviço. E, aproximando-se dele, perguntou: "Senhor, não te importas que minha irmã tenha me deixado sozinha com o serviço? Dize-lhe que me ajude! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25405, 'nvi', 'Lucas', 'lc', 10, 41, 'Respondeu o Senhor: "Marta! Marta! Você está preocupada e inquieta com muitas coisas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25406, 'nvi', 'Lucas', 'lc', 10, 42, 'todavia apenas uma é necessária. Maria escolheu a boa parte, e esta não lhe será tirada".');
