INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25461, 'nvi', 'Lucas', 'lc', 12, 1, 'Nesse meio tempo, tendo-se juntado uma multidão de milhares de pessoas, a ponto de se atropelarem umas às outras, Jesus começou a falar primeiramente aos seus discípulos, dizendo: "Tenham cuidado com o fermento dos fariseus, que é a hipocrisia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25462, 'nvi', 'Lucas', 'lc', 12, 2, 'Não há nada escondido que não venha a ser descoberto, ou oculto que não venha a ser conhecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25463, 'nvi', 'Lucas', 'lc', 12, 3, 'O que vocês disseram nas trevas será ouvido à luz do dia, e o que vocês sussurraram aos ouvidos dentro de casa, será proclamado dos telhados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25464, 'nvi', 'Lucas', 'lc', 12, 4, '"Eu lhes digo, meus amigos: não tenham medo dos que matam o corpo e depois nada mais podem fazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25465, 'nvi', 'Lucas', 'lc', 12, 5, 'Mas eu lhes mostrarei a quem vocês devem temer: temam aquele que, depois de matar o corpo, tem poder para lançar no inferno. Sim, eu lhes digo, esse vocês devem temer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25466, 'nvi', 'Lucas', 'lc', 12, 6, 'Não se vendem cinco pardais por duas moedinhas? Contudo, nenhum deles é esquecido por Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25467, 'nvi', 'Lucas', 'lc', 12, 7, 'Até os cabelos da cabeça de vocês estão todos contados. Não tenham medo; vocês valem mais do que muitos pardais!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25468, 'nvi', 'Lucas', 'lc', 12, 8, '"Eu lhes digo: quem me confessar diante dos homens, também o Filho do homem o confessará diante dos anjos de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25469, 'nvi', 'Lucas', 'lc', 12, 9, 'Mas aquele que me negar diante dos homens será negado diante dos anjos de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25470, 'nvi', 'Lucas', 'lc', 12, 10, 'Todo aquele que disser uma palavra contra o Filho do homem será perdoado, mas quem blasfemar contra o Espírito Santo não será perdoado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25471, 'nvi', 'Lucas', 'lc', 12, 11, '"Quando vocês forem levados às sinagogas e diante dos governantes e das autoridades, não se preocupem com a forma pela qual se defenderão, ou com o que dirão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25472, 'nvi', 'Lucas', 'lc', 12, 12, 'pois naquela hora o Espírito Santo lhes ensinará o que devem dizer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25473, 'nvi', 'Lucas', 'lc', 12, 13, 'Alguém da multidão lhe disse: "Mestre, dize a meu irmão que divida a herança comigo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25474, 'nvi', 'Lucas', 'lc', 12, 14, 'Respondeu Jesus: "Homem, quem me designou juiz ou árbitro entre vocês? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25475, 'nvi', 'Lucas', 'lc', 12, 15, 'Então lhes disse: "Cuidado! Fiquem de sobreaviso contra todo tipo de ganância; a vida de um homem não consiste na quantidade dos seus bens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25476, 'nvi', 'Lucas', 'lc', 12, 16, 'Então lhes contou esta parábola: "A terra de certo homem rico produziu muito bem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25477, 'nvi', 'Lucas', 'lc', 12, 17, 'Ele pensou consigo mesmo: ‘O que vou fazer? Não tenho onde armazenar minha colheita’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25478, 'nvi', 'Lucas', 'lc', 12, 18, '"Então disse: ‘Já sei o que vou fazer. Vou derrubar os meus celeiros e construir outros maiores, e ali guardarei toda a minha safra e todos os meus bens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25479, 'nvi', 'Lucas', 'lc', 12, 19, 'E direi a mim mesmo: Você tem grande quantidade de bens, armazenados para muitos anos. Descanse, coma, beba e alegre-se’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25480, 'nvi', 'Lucas', 'lc', 12, 20, '"Contudo, Deus lhe disse: ‘Insensato! Esta mesma noite a sua vida lhe será exigida. Então, quem ficará com o que você preparou? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25481, 'nvi', 'Lucas', 'lc', 12, 21, '"Assim acontece com quem guarda para si riquezas, mas não é rico para com Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25482, 'nvi', 'Lucas', 'lc', 12, 22, 'Dirigindo-se aos seus discípulos, Jesus acrescentou: "Portanto eu lhes digo: não se preocupem com suas próprias vidas, quanto ao que comer; nem com seus próprios corpos, quanto ao que vestir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25483, 'nvi', 'Lucas', 'lc', 12, 23, 'A vida é mais importante do que a comida, e o corpo, mais do que as roupas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25484, 'nvi', 'Lucas', 'lc', 12, 24, 'Observem os corvos: não semeiam nem colhem, não têm armazéns nem celeiros; contudo, Deus os alimenta. E vocês têm muito mais valor do que as aves!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25485, 'nvi', 'Lucas', 'lc', 12, 25, 'Quem de vocês, por mais que se preocupe, pode acrescentar uma hora que seja à sua vida?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25486, 'nvi', 'Lucas', 'lc', 12, 26, 'Visto que vocês não podem sequer fazer uma coisa tão pequena, por que se preocupar com o restante?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25487, 'nvi', 'Lucas', 'lc', 12, 27, '"Observem como crescem os lírios. Eles não trabalham nem tecem. Contudo, eu lhes digo que nem Salomão, em todo o seu esplendor, vestiu-se como um deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25488, 'nvi', 'Lucas', 'lc', 12, 28, 'Se Deus veste assim a erva do campo, que hoje existe e amanhã é lançada ao fogo, quanto mais vestirá vocês, homens de pequena fé!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25489, 'nvi', 'Lucas', 'lc', 12, 29, 'Não busquem ansiosamente o que hão de comer ou beber; não se preocupem com isso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25490, 'nvi', 'Lucas', 'lc', 12, 30, 'Pois o mundo pagão é que corre atrás dessas coisas; mas o Pai sabe que vocês precisam delas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25491, 'nvi', 'Lucas', 'lc', 12, 31, 'Busquem, pois, o Reino de Deus, e essas coisas lhes serão acrescentadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25492, 'nvi', 'Lucas', 'lc', 12, 32, '"Não tenham medo, pequeno rebanho, pois foi do agrado do Pai dar-lhes o Reino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25493, 'nvi', 'Lucas', 'lc', 12, 33, 'Vendam o que têm e dêem esmolas. Façam para vocês bolsas que não se gastem com o tempo, um tesouro nos céus que não se acabe, onde ladrão algum chega perto e nenhuma traça destrói.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25494, 'nvi', 'Lucas', 'lc', 12, 34, 'Pois onde estiver o seu tesouro, ali também estará o seu coração".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25495, 'nvi', 'Lucas', 'lc', 12, 35, '"Estejam prontos para servir, e conservem acesas as suas candeias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25496, 'nvi', 'Lucas', 'lc', 12, 36, 'como aqueles que esperam seu senhor voltar de um banquete de casamento; para que, quando ele chegar e bater, possam abrir-lhe a porta imediatamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25497, 'nvi', 'Lucas', 'lc', 12, 37, 'Felizes os servos cujo senhor os encontrar vigiando, quando voltar. Eu lhes afirmo que ele se vestirá para servir, fará que se reclinem à mesa, e virá servi-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25498, 'nvi', 'Lucas', 'lc', 12, 38, 'Mesmo que ele chegue de noite ou de madrugada, felizes os servos que o senhor encontrar preparados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25499, 'nvi', 'Lucas', 'lc', 12, 39, 'Entendam, porém, isto: se o dono da casa soubesse a que hora viria o ladrão, não permitiria que a sua casa fosse arrombada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25500, 'nvi', 'Lucas', 'lc', 12, 40, 'Estejam também vocês preparados, porque o Filho do homem virá numa hora em que não o esperam".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25501, 'nvi', 'Lucas', 'lc', 12, 41, 'Pedro perguntou: "Senhor, estás contando esta parábola para nós ou para todos? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25502, 'nvi', 'Lucas', 'lc', 12, 42, 'O Senhor respondeu: "Quem é, pois, o administrador fiel e sensato, a quem seu senhor encarrega dos seus servos, para lhes dar sua porção de alimento no tempo devido?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25503, 'nvi', 'Lucas', 'lc', 12, 43, 'Feliz o servo a quem o seu senhor encontrar fazendo assim quando voltar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25504, 'nvi', 'Lucas', 'lc', 12, 44, 'Garanto-lhes que ele o encarregará de todos os seus bens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25505, 'nvi', 'Lucas', 'lc', 12, 45, 'Mas suponham que esse servo diga a si mesmo: ‘Meu senhor se demora a voltar’, e então comece a bater nos servos e nas servas, a comer, a beber e a embriagar-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25506, 'nvi', 'Lucas', 'lc', 12, 46, 'O senhor daquele servo virá num dia em que ele não o espera e numa hora que não sabe, e o punirá severamente e lhe dará um lugar com os infiéis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25507, 'nvi', 'Lucas', 'lc', 12, 47, '"Aquele servo que conhece a vontade de seu senhor e não prepara o que ele deseja, nem o realiza, receberá muitos açoites.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25508, 'nvi', 'Lucas', 'lc', 12, 48, 'Mas aquele que não a conhece e pratica coisas merecedoras de castigo, receberá poucos açoites. A quem muito foi dado, muito será exigido; e a quem muito foi confiado, muito mais será pedido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25509, 'nvi', 'Lucas', 'lc', 12, 49, '"Vim trazer fogo à terra, e como gostaria que já estivesse aceso!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25510, 'nvi', 'Lucas', 'lc', 12, 50, 'Mas tenho que passar por um batismo, e como estou angustiado até que ele se realize!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25511, 'nvi', 'Lucas', 'lc', 12, 51, 'Vocês pensam que vim trazer paz à terra? Não, eu lhes digo. Pelo contrário, vim trazer divisão!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25512, 'nvi', 'Lucas', 'lc', 12, 52, 'De agora em diante haverá cinco numa família divididos uns contra os outros: três contra dois e dois contra três.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25513, 'nvi', 'Lucas', 'lc', 12, 53, 'Estarão divididos pai contra filho e filho contra pai, mãe contra filha e filha contra mãe, sogra contra nora e nora contra sogra".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25514, 'nvi', 'Lucas', 'lc', 12, 54, 'Dizia ele à multidão: "Quando vocês vêem uma nuvem se levantando no ocidente, logo dizem: ‘Vai chover’, e assim acontece.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25515, 'nvi', 'Lucas', 'lc', 12, 55, 'E quando sopra o vento sul, vocês dizem: ‘Vai fazer calor’, e assim ocorre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25516, 'nvi', 'Lucas', 'lc', 12, 56, 'Hipócritas! Vocês sabem interpretar o aspecto da terra e do céu. Como não sabem interpretar o tempo presente?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25517, 'nvi', 'Lucas', 'lc', 12, 57, '"Por que vocês não julgam por si mesmos o que é justo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25518, 'nvi', 'Lucas', 'lc', 12, 58, 'Quando algum de vocês estiver indo com seu adversário para o magistrado, faça tudo para se reconciliar com ele no caminho; para que ele não o arraste ao juiz, o juiz o entregue ao oficial de justiça, e o oficial de justiça o jogue na prisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25519, 'nvi', 'Lucas', 'lc', 12, 59, 'Eu lhe digo que você não sairá de lá enquanto não pagar o último centavo".');
