INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25520, 'nvi', 'Lucas', 'lc', 13, 1, 'Naquela ocasião, alguns dos que estavam presentes contaram a Jesus que Pilatos misturara o sangue de alguns galileus com os sacrifícios deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25521, 'nvi', 'Lucas', 'lc', 13, 2, 'Jesus respondeu: "Vocês pensam que esses galileus eram mais pecadores que todos os outros, por terem sofrido dessa maneira?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25522, 'nvi', 'Lucas', 'lc', 13, 3, 'Eu lhes digo que não! Mas se não se arrependerem, todos vocês também perecerão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25523, 'nvi', 'Lucas', 'lc', 13, 4, 'Ou vocês pensam que aqueles dezoito que morreram, quando caiu sobre eles a torre de Siloé, eram mais culpados do que todos os outros habitantes de Jerusalém?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25524, 'nvi', 'Lucas', 'lc', 13, 5, 'Eu lhes digo que não! Mas se não se arrependerem, todos vocês também perecerão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25525, 'nvi', 'Lucas', 'lc', 13, 6, 'Então contou esta parábola: "Um homem tinha uma figueira plantada em sua vinha. Foi procurar fruto nela, e não achou nenhum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25526, 'nvi', 'Lucas', 'lc', 13, 7, 'Por isso disse ao que cuidava da vinha: ‘Já faz três anos que venho procurar fruto nesta figueira e não acho. Corte-a! Por que deixá-la inutilizar a terra? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25527, 'nvi', 'Lucas', 'lc', 13, 8, '"Respondeu o homem: ‘Senhor, deixe-a por mais um ano, e eu cavarei ao redor dela e a adubarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25528, 'nvi', 'Lucas', 'lc', 13, 9, 'Se der fruto no ano que vem, muito bem! Se não, corte-a’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25529, 'nvi', 'Lucas', 'lc', 13, 10, 'Certo sábado Jesus estava ensinando numa das sinagogas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25530, 'nvi', 'Lucas', 'lc', 13, 11, 'e ali estava uma mulher que tinha um espírito que a mantinha doente havia dezoito anos. Ela andava encurvada e de forma alguma podia endireitar-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25531, 'nvi', 'Lucas', 'lc', 13, 12, 'Ao vê-la, Jesus chamou-a à frente e lhe disse: "Mulher, você está livre da sua doença".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25532, 'nvi', 'Lucas', 'lc', 13, 13, 'Então lhe impôs as mãos; e imediatamente ela se endireitou, e louvava a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25533, 'nvi', 'Lucas', 'lc', 13, 14, 'Indignado porque Jesus havia curado no sábado, o dirigente da sinagoga disse ao povo: "Há seis dias em que se deve trabalhar. Venham para ser curados nesses dias, e não no sábado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25534, 'nvi', 'Lucas', 'lc', 13, 15, 'O Senhor lhe respondeu: "Hipócritas! Cada um de vocês não desamarra no sábado o seu boi ou jumento do estábulo e o leva dali para dar-lhe água?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25535, 'nvi', 'Lucas', 'lc', 13, 16, 'Então, esta mulher, uma filha de Abraão a quem Satanás mantinha presa por dezoito longos anos, não deveria no dia de sábado ser libertada daquilo que a prendia? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25536, 'nvi', 'Lucas', 'lc', 13, 17, 'Tendo dito isso, todos os seus oponentes ficaram envergonhados, mas o povo se alegrava com todas as maravilhas que ele estava fazendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25537, 'nvi', 'Lucas', 'lc', 13, 18, 'Então Jesus perguntou: "Com que se parece o Reino de Deus? Com que o compararei?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25538, 'nvi', 'Lucas', 'lc', 13, 19, 'É como um grão de mostarda que um homem semeou em sua horta. Ele cresceu e se tornou uma árvore, e as aves do céu se fizaram ninhos em seus ramos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25539, 'nvi', 'Lucas', 'lc', 13, 20, 'Mais uma vez ele perguntou: "A que compararei o Reino de Deus?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25540, 'nvi', 'Lucas', 'lc', 13, 21, 'É como o fermento que uma mulher misturou com uma grande quantidade de farinha, e toda a massa ficou fermentada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25541, 'nvi', 'Lucas', 'lc', 13, 22, 'Depois Jesus foi pelas cidades e povoados e ensinava, prosseguindo em direção a Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25542, 'nvi', 'Lucas', 'lc', 13, 23, 'Alguém lhe perguntou: "Senhor, serão poucos os salvos? " Ele lhes disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25543, 'nvi', 'Lucas', 'lc', 13, 24, '"Esforcem-se para entrar pela porta estreita, porque eu lhes digo que muitos tentarão entrar e não conseguirão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25544, 'nvi', 'Lucas', 'lc', 13, 25, 'Quando o dono da casa se levantar e fechar a porta, vocês ficarão do lado de fora, batendo e pedindo: ‘Senhor, abre-nos a porta’. "Ele, porém, responderá: ‘Não os conheço, nem sei de onde são vocês’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25545, 'nvi', 'Lucas', 'lc', 13, 26, '"Então vocês dirão: ‘Comemos e bebemos contigo, e ensinaste em nossas ruas’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25546, 'nvi', 'Lucas', 'lc', 13, 27, '"Mas ele responderá: ‘Não os conheço, nem sei de onde são vocês. Afastem-se de mim, todos vocês, que praticam o mal! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25547, 'nvi', 'Lucas', 'lc', 13, 28, '"Ali haverá choro e ranger de dentes, quando vocês virem Abraão, Isaque e Jacó e todos os profetas no Reino de Deus, mas vocês excluídos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25548, 'nvi', 'Lucas', 'lc', 13, 29, 'Pessoas virão do oriente e do ocidente, do norte e do sul, e ocuparão os seus lugares à mesa no Reino de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25549, 'nvi', 'Lucas', 'lc', 13, 30, 'De fato, há últimos que serão primeiros, e primeiros que serão últimos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25550, 'nvi', 'Lucas', 'lc', 13, 31, 'Naquela mesma hora alguns fariseus aproximaram-se de Jesus e lhe disseram: "Saia e vá embora daqui, pois Herodes quer matá-lo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25551, 'nvi', 'Lucas', 'lc', 13, 32, 'Ele respondeu: "Vão dizer àquela raposa: ‘Expulsarei demônios e curarei o povo hoje e amanhã, e no terceiro dia estarei pronto’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25552, 'nvi', 'Lucas', 'lc', 13, 33, 'Mas, preciso prosseguir hoje, amanhã e depois de amanhã, pois certamente nenhum profeta deve morrer fora de Jerusalém!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25553, 'nvi', 'Lucas', 'lc', 13, 34, '"Jerusalém, Jerusalém, você, que mata os profetas e apedrejas os que lhe são enviados! Quantas vezes eu quis reunir os seus filhos, como a galinha reúne os seus pintinhos debaixo das suas asas, mas vocês não quiseram!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25554, 'nvi', 'Lucas', 'lc', 13, 35, 'Eis que a casa de vocês ficará deserta. Eu lhes digo que vocês não me verão mais até que digam: ‘Bendito o que vem em nome do Senhor’".');
