INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25590, 'nvi', 'Lucas', 'lc', 15, 1, 'Todos os publicanos e "pecadores" estavam se reunindo para ouvi-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25591, 'nvi', 'Lucas', 'lc', 15, 2, 'Mas os fariseus e os mestres da lei o criticavam: "Este homem recebe pecadores e come com eles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25592, 'nvi', 'Lucas', 'lc', 15, 3, 'Então Jesus lhes contou esta parábola:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25593, 'nvi', 'Lucas', 'lc', 15, 4, '"Qual de vocês que, possuindo cem ovelhas, e perdendo uma, não deixa as noventa e nove no campo e vai atrás da ovelha perdida, até encontrá-la?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25594, 'nvi', 'Lucas', 'lc', 15, 5, 'E quando a encontra, coloca-a alegremente sobre os ombros');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25595, 'nvi', 'Lucas', 'lc', 15, 6, 'e vai para casa. Ao chegar, reúne seus amigos e vizinhos e diz: ‘Alegrem-se comigo, pois encontrei minha ovelha perdida’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25596, 'nvi', 'Lucas', 'lc', 15, 7, 'Eu lhes digo que, da mesma forma, haverá mais alegria no céu por um pecador que se arrepende do que por noventa e nove justos que não precisam arrepender-se".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25597, 'nvi', 'Lucas', 'lc', 15, 8, '"Ou, qual é a mulher que, possuindo dez dracmas e, perdendo uma delas, não acende uma candeia, varre a casa e procura atentamente, até encontrá-la?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25598, 'nvi', 'Lucas', 'lc', 15, 9, 'E quando a encontra, reúne suas amigas e vizinhas e diz: ‘Alegrem-se comigo, pois encontrei minha moeda perdida’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25599, 'nvi', 'Lucas', 'lc', 15, 10, 'Eu lhes digo que, da mesma forma, há alegria na presença dos anjos de Deus por um pecador que se arrepende".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25600, 'nvi', 'Lucas', 'lc', 15, 11, 'Jesus continuou: "Um homem tinha dois filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25601, 'nvi', 'Lucas', 'lc', 15, 12, 'O mais novo disse ao seu pai: ‘Pai, quero a minha parte da herança’. Assim, ele repartiu sua propriedade entre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25602, 'nvi', 'Lucas', 'lc', 15, 13, '"Não muito tempo depois, o filho mais novo reuniu tudo o que tinha, e foi para uma região distante; e lá desperdiçou os seus bens vivendo irresponsavelmente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25603, 'nvi', 'Lucas', 'lc', 15, 14, 'Depois de ter gasto tudo, houve uma grande fome em toda aquela região, e ele começou a passar necessidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25604, 'nvi', 'Lucas', 'lc', 15, 15, 'Por isso foi empregar-se com um dos cidadãos daquela região, que o mandou para o seu campo a fim de cuidar de porcos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25605, 'nvi', 'Lucas', 'lc', 15, 16, 'Ele desejava encher o estômago com as vagens de alfarrobeira que os porcos comiam, mas ninguém lhe dava nada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25606, 'nvi', 'Lucas', 'lc', 15, 17, '"Caindo em si, ele disse: ‘Quantos empregados de meu pai têm comida de sobra, e eu aqui, morrendo de fome!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25607, 'nvi', 'Lucas', 'lc', 15, 18, 'Eu me porei a caminho e voltarei para meu pai, e lhe direi: Pai, pequei contra o céu e contra ti.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25608, 'nvi', 'Lucas', 'lc', 15, 19, 'Não sou mais digno de ser chamado teu filho; trata-me como um dos teus empregados’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25609, 'nvi', 'Lucas', 'lc', 15, 20, 'A seguir, levantou-se e foi para seu pai. "Estando ainda longe, seu pai o viu e, cheio de compaixão, correu para seu filho, e o abraçou e beijou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25610, 'nvi', 'Lucas', 'lc', 15, 21, '"O filho lhe disse: ‘Pai, pequei contra o céu e contra ti. Não sou mais digno de ser chamado teu filho’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25611, 'nvi', 'Lucas', 'lc', 15, 22, '"Mas o pai disse aos seus servos: ‘Depressa! Tragam a melhor roupa e vistam nele. Coloquem um anel em seu dedo e calçados em seus pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25612, 'nvi', 'Lucas', 'lc', 15, 23, 'Tragam o novilho gordo e matem-no. Vamos fazer uma festa e comemorar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25613, 'nvi', 'Lucas', 'lc', 15, 24, 'Pois este meu filho estava morto e voltou à vida; estava perdido e foi achado’. E começaram a festejar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25614, 'nvi', 'Lucas', 'lc', 15, 25, '"Enquanto isso, o filho mais velho estava no campo. Quando se aproximou da casa, ouviu a música e a dança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25615, 'nvi', 'Lucas', 'lc', 15, 26, 'Então chamou um dos servos e perguntou-lhe o que estava acontecendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25616, 'nvi', 'Lucas', 'lc', 15, 27, 'Este lhe respondeu: ‘Seu irmão voltou, e seu pai matou o novilho gordo, porque o recebeu de volta são e salvo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25617, 'nvi', 'Lucas', 'lc', 15, 28, '"O filho mais velho encheu-se de ira, e não quis entrar. Então seu pai saiu e insistiu com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25618, 'nvi', 'Lucas', 'lc', 15, 29, 'Mas ele respondeu ao seu pai: ‘Olha! todos esses anos tenho trabalhado como um escravo ao teu serviço e nunca desobedeci às tuas ordens. Mas tu nunca me deste nem um cabrito para eu festejar com os meus amigos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25619, 'nvi', 'Lucas', 'lc', 15, 30, 'Mas quando volta para casa esse seu filho, que esbanjou os teus bens com as prostitutas, matas o novilho gordo para ele! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25620, 'nvi', 'Lucas', 'lc', 15, 31, '"Disse o pai: ‘Meu filho, você está sempre comigo, e tudo o que tenho é seu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25621, 'nvi', 'Lucas', 'lc', 15, 32, 'Mas nós tínhamos que comemorar e alegrar-nos, porque este seu irmão estava morto e voltou à vida, estava perdido e foi achado’ ".');
