INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25622, 'nvi', 'Lucas', 'lc', 16, 1, 'Jesus disse aos seus discípulos: "O administrador de um homem rico foi acusado de estar desperdiçando os seus bens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25623, 'nvi', 'Lucas', 'lc', 16, 2, 'Então ele o chamou e lhe perguntou: ‘Que é isso que estou ouvindo a seu respeito? Preste contas da sua administração, porque você não pode continuar sendo o administrador’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25624, 'nvi', 'Lucas', 'lc', 16, 3, '"O administrador disse a si mesmo: ‘Meu senhor está me despedindo. Que farei? Para cavar não tenho força, e tenho vergonha de mendigar...');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25625, 'nvi', 'Lucas', 'lc', 16, 4, 'Já sei o que vou fazer para que, quando perder o meu emprego aqui, as pessoas me recebam em suas casas’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25626, 'nvi', 'Lucas', 'lc', 16, 5, '"Então chamou cada um dos devedores do seu senhor. Perguntou ao primeiro: ‘Quanto você deve ao meu senhor? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25627, 'nvi', 'Lucas', 'lc', 16, 6, '‘Cem potes de azeite’, respondeu ele. "O administrador lhe disse: ‘Tome a sua conta, sente-se depressa e escreva cinqüenta’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25628, 'nvi', 'Lucas', 'lc', 16, 7, '"A seguir ele perguntou ao segundo: ‘E você, quanto deve? ’ ‘Cem tonéis de trigo’, respondeu ele. "Ele lhe disse: ‘Tome a sua conta e escreva oitenta’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25629, 'nvi', 'Lucas', 'lc', 16, 8, '"O senhor elogiou o administrador desonesto, porque agiu astutamente. Pois os filhos deste mundo são mais astutos no trato entre si do que os filhos da luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25630, 'nvi', 'Lucas', 'lc', 16, 9, 'Por isso, eu lhes digo: usem a riqueza deste mundo ímpio para ganhar amigos, de forma que, quando ela acabar, estes os recebam nas moradas eternas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25631, 'nvi', 'Lucas', 'lc', 16, 10, '"Quem é fiel no pouco, também é fiel no muito, e quem é desonesto no pouco, também é desonesto no muito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25632, 'nvi', 'Lucas', 'lc', 16, 11, 'Assim, se vocês não forem dignos de confiança em lidar com as riquezas deste mundo ímpio, quem lhes confiará as verdadeiras riquezas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25633, 'nvi', 'Lucas', 'lc', 16, 12, 'E se vocês não forem dignos de confiança em relação ao que é dos outros, quem lhes dará o que é de vocês?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25634, 'nvi', 'Lucas', 'lc', 16, 13, '"Nenhum servo pode servir a dois senhores; pois odiará a um e amará ao outro, ou se dedicará a um e desprezará ao outro. Vocês não podem servir a Deus e ao Dinheiro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25635, 'nvi', 'Lucas', 'lc', 16, 14, 'Os fariseus, que amavam o dinheiro, ouviam tudo isso e zombavam de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25636, 'nvi', 'Lucas', 'lc', 16, 15, 'Ele lhes disse: "Vocês são os que se justificam a si mesmos aos olhos dos homens, mas Deus conhece os corações de vocês. Aquilo que tem muito valor entre os homens é detestável aos olhos de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25637, 'nvi', 'Lucas', 'lc', 16, 16, '"A Lei e os Profetas profetizaram até João. Desse tempo em diante estão sendo pregadas as boas novas do Reino de Deus, e todos tentam forçar sua entrada nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25638, 'nvi', 'Lucas', 'lc', 16, 17, 'É mais fácil o céu e a terra desaparecerem do que cair da Lei o menor traço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25639, 'nvi', 'Lucas', 'lc', 16, 18, '"Quem se divorciar de sua mulher e se casar com outra mulher estará cometendo adultério, e o homem que se casar com uma mulher divorciada do seu marido estará cometendo adultério".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25640, 'nvi', 'Lucas', 'lc', 16, 19, '"Havia um homem rico que se vestia de púrpura e de linho fino e vivia no luxo todos os dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25641, 'nvi', 'Lucas', 'lc', 16, 20, 'Diante do seu portão fora deixado um mendigo chamado Lázaro, coberto de chagas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25642, 'nvi', 'Lucas', 'lc', 16, 21, 'este ansiava comer o que caía da mesa do rico. Em vez disso, os cães vinham lamber as suas feridas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25643, 'nvi', 'Lucas', 'lc', 16, 22, '"Chegou o dia em que o mendigo morreu, e os anjos o levaram para junto de Abraão. O rico também morreu e foi sepultado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25644, 'nvi', 'Lucas', 'lc', 16, 23, 'No Hades, onde estava sendo atormentado, ele olhou para cima e viu Abraão de longe, com Lázaro ao seu lado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25645, 'nvi', 'Lucas', 'lc', 16, 24, 'Então, chamou-o: ‘Pai Abraão, tem misericórdia de mim e manda que Lázaro molhe a ponta do dedo na água e refresque a minha língua, porque estou sofrendo muito neste fogo’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25646, 'nvi', 'Lucas', 'lc', 16, 25, '"Mas Abraão respondeu: ‘Filho, lembre-se de que durante a sua vida você recebeu coisas boas, enquanto que Lázaro recebeu coisas más. Agora, porém, ele está sendo consolado aqui e você está em sofrimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25647, 'nvi', 'Lucas', 'lc', 16, 26, 'E além disso, entre vocês e nós há um grande abismo, de forma que os que desejam passar do nosso lado para o seu, ou do seu lado para o nosso, não conseguem’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25648, 'nvi', 'Lucas', 'lc', 16, 27, '"Ele respondeu: ‘Então eu lhe suplico, pai: manda Lázaro ir à casa de meu pai,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25649, 'nvi', 'Lucas', 'lc', 16, 28, 'pois tenho cinco irmãos. Deixa que ele os avise, a fim de que eles não venham também para este lugar de tormento’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25650, 'nvi', 'Lucas', 'lc', 16, 29, '"Abraão respondeu: ‘Eles têm Moisés e os Profetas; que os ouçam’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25651, 'nvi', 'Lucas', 'lc', 16, 30, '" ‘Não, pai Abraão’, disse ele, ‘mas se alguém dentre os mortos fosse até eles, eles se arrependeriam’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25652, 'nvi', 'Lucas', 'lc', 16, 31, '"Abraão respondeu: ‘Se não ouvem a Moisés e aos Profetas, tampouco se deixarão convencer, ainda que ressuscite alguém dentre os mortos’ ".');
