INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25653, 'nvi', 'Lucas', 'lc', 17, 1, 'Jesus disse aos seus discípulos: "É inevitável que aconteçam coisas que levem o povo a tropeçar, mas ai da pessoa por meio de quem elas acontecem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25654, 'nvi', 'Lucas', 'lc', 17, 2, 'Seria melhor que ela fosse lançada no mar com uma pedra de moinho amarrada no pescoço, do que levar um desses pequeninos a pecar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25655, 'nvi', 'Lucas', 'lc', 17, 3, 'Tomem cuidado. "Se o seu irmão pecar, repreenda-o e, se ele se arrepender, perdoe-lhe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25656, 'nvi', 'Lucas', 'lc', 17, 4, 'Se pecar contra você sete vezes no dia, e sete vezes voltar a você e disser: ‘Estou arrependido’, perdoe-lhe".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25657, 'nvi', 'Lucas', 'lc', 17, 5, 'Os apóstolos disseram ao Senhor: "Aumenta a nossa fé! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25658, 'nvi', 'Lucas', 'lc', 17, 6, 'Ele respondeu: "Se vocês tiverem fé do tamanho de uma semente de mostarda, poderão dizer a esta amoreira: ‘Arranque-se e plante-se no mar’, e ela lhes obedecerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25659, 'nvi', 'Lucas', 'lc', 17, 7, '"Qual de vocês que, tendo um servo que esteja arando ou cuidando das ovelhas, lhe dirá, quando ele chegar do campo: ‘Venha agora e sente-se para comer’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25660, 'nvi', 'Lucas', 'lc', 17, 8, 'Pelo contrário, não dirá: ‘Prepare o meu jantar, apronte-se e sirva-me enquanto como e bebo; depois disso você pode comer e beber’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25661, 'nvi', 'Lucas', 'lc', 17, 9, 'Será que ele agradecerá ao servo por ter feito o que lhe foi ordenado?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25662, 'nvi', 'Lucas', 'lc', 17, 10, 'Assim também vocês, quando tiverem feito tudo o que lhes for ordenado, devem dizer: ‘Somos servos inúteis; apenas cumprimos o nosso dever’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25663, 'nvi', 'Lucas', 'lc', 17, 11, 'A caminho de Jerusalém, Jesus passou pela divisa entre Samaria e Galiléia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25664, 'nvi', 'Lucas', 'lc', 17, 12, 'Ao entrar num povoado, dez leprosos dirigiram-se a ele. Ficaram a certa distância');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25665, 'nvi', 'Lucas', 'lc', 17, 13, 'e gritaram em alta voz: "Jesus, Mestre, tem piedade de nós! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25666, 'nvi', 'Lucas', 'lc', 17, 14, 'Ao vê-los, ele disse: "Vão mostrar-se aos sacerdotes". Enquanto eles iam, foram purificados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25667, 'nvi', 'Lucas', 'lc', 17, 15, 'Um deles, quando viu que estava curado, voltou, louvando a Deus em alta voz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25668, 'nvi', 'Lucas', 'lc', 17, 16, 'Prostrou-se aos pés de Jesus e lhe agradeceu. Este era samaritano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25669, 'nvi', 'Lucas', 'lc', 17, 17, 'Jesus perguntou: "Não foram purificados todos os dez? Onde estão os outros nove?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25670, 'nvi', 'Lucas', 'lc', 17, 18, 'Não se achou nenhum que voltasse e desse louvor a Deus, a não ser este estrangeiro? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25671, 'nvi', 'Lucas', 'lc', 17, 19, 'Então ele lhe disse: "Levante-se e vá; a sua fé o salvou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25672, 'nvi', 'Lucas', 'lc', 17, 20, 'Certa vez, tendo sido interrogado pelos fariseus sobre quando viria o Reino de Deus, Jesus respondeu: "O Reino de Deus não vem de modo visível,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25673, 'nvi', 'Lucas', 'lc', 17, 21, 'nem se dirá: ‘Aqui está ele’, ou ‘Lá está’; porque o Reino de Deus está entre vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25674, 'nvi', 'Lucas', 'lc', 17, 22, 'Depois disse aos seus discípulos: "Chegará o tempo em que vocês desejarão ver um dos dias do Filho do homem, mas não verão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25675, 'nvi', 'Lucas', 'lc', 17, 23, 'Dirão a vocês: ‘Lá está ele! ’ ou ‘Aqui está! ’ Não se apressem em segui-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25676, 'nvi', 'Lucas', 'lc', 17, 24, 'Pois o Filho do homem no seu dia será como o relâmpago cujo brilho vai de uma extremidade à outra do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25677, 'nvi', 'Lucas', 'lc', 17, 25, 'Mas antes é necessário que ele sofra muito e seja rejeitado por esta geração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25678, 'nvi', 'Lucas', 'lc', 17, 26, '"Assim como foi nos dias de Noé, também será nos dias do Filho do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25679, 'nvi', 'Lucas', 'lc', 17, 27, 'O povo vivia comendo, bebendo, casando-se e sendo dado em casamento, até o dia em que Noé entrou na arca. Então veio o dilúvio e os destruiu a todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25680, 'nvi', 'Lucas', 'lc', 17, 28, '"Aconteceu a mesma coisa nos dias de Ló. O povo estava comendo e bebendo, comprando e vendendo, plantando e construindo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25681, 'nvi', 'Lucas', 'lc', 17, 29, 'Mas no dia em que Ló saiu de Sodoma, choveu fogo e enxofre do céu e os destruiu a todos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25682, 'nvi', 'Lucas', 'lc', 17, 30, '"Acontecerá exatamente assim no dia em que o Filho do homem for revelado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25683, 'nvi', 'Lucas', 'lc', 17, 31, 'Naquele dia, quem estiver no telhado de sua casa, não deve desça para apanhar os seus bens dentro de casa. Semelhantemente, quem estiver no campo, não deve voltar atrás por coisa alguma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25684, 'nvi', 'Lucas', 'lc', 17, 32, 'Lembrem-se da mulher de Ló!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25685, 'nvi', 'Lucas', 'lc', 17, 33, 'Quem tentar conservar a sua vida a perderá, e quem perder a sua vida a preservará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25686, 'nvi', 'Lucas', 'lc', 17, 34, 'Eu lhes digo: naquela noite duas pessoas estarão numa cama; uma será tirada e a outra deixada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25687, 'nvi', 'Lucas', 'lc', 17, 35, 'Duas mulheres estarão moendo trigo juntas; uma será tirada e a outra deixada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25688, 'nvi', 'Lucas', 'lc', 17, 36, 'Duas pessoas estarão no campo; uma será tirada e a outra deixada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25689, 'nvi', 'Lucas', 'lc', 17, 37, '"Onde, Senhor? ", perguntaram eles. Ele respondeu: "Onde houver um cadáver, ali se ajuntarão os abutres".');
