INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25733, 'nvi', 'Lucas', 'lc', 19, 1, 'Jesus entrou em Jericó, e atravessava a cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25734, 'nvi', 'Lucas', 'lc', 19, 2, 'Havia ali um homem rico chamado Zaqueu, chefe dos publicanos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25735, 'nvi', 'Lucas', 'lc', 19, 3, 'Ele queria ver quem era Jesus, mas, sendo de pequena estatura, não o conseguia, por causa da multidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25736, 'nvi', 'Lucas', 'lc', 19, 4, 'Assim, correu adiante e subiu numa figueira brava para vê-lo, pois Jesus ia passar por ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25737, 'nvi', 'Lucas', 'lc', 19, 5, 'Quando Jesus chegou àquele lugar, olhou para cima e lhe disse: "Zaqueu, desça depressa. Quero ficar em sua casa hoje".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25738, 'nvi', 'Lucas', 'lc', 19, 6, 'Então ele desceu rapidamente e o recebeu com alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25739, 'nvi', 'Lucas', 'lc', 19, 7, 'Todo o povo viu isso e começou a se queixar: "Ele se hospedou na casa de um ‘pecador’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25740, 'nvi', 'Lucas', 'lc', 19, 8, 'Mas Zaqueu levantou-se e disse ao Senhor: "Olha, Senhor! Estou dando a metade dos meus bens aos pobres; e se de alguém extorqui alguma coisa, devolverei quatro vezes mais".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25741, 'nvi', 'Lucas', 'lc', 19, 9, 'Jesus lhe disse: "Hoje houve salvação nesta casa! Porque este homem também é filho de Abraão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25742, 'nvi', 'Lucas', 'lc', 19, 10, 'Pois o Filho do homem veio buscar e salvar o que estava perdido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25743, 'nvi', 'Lucas', 'lc', 19, 11, 'Estando eles a ouvi-lo, Jesus passou a contar-lhes uma parábola, porque estava perto de Jerusalém e o povo pensava que o Reino de Deus ia se manifestar de imediato.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25744, 'nvi', 'Lucas', 'lc', 19, 12, 'Ele disse: "Um homem de nobre nascimento foi para uma terra distante para ser coroado rei e depois voltar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25745, 'nvi', 'Lucas', 'lc', 19, 13, 'Então, chamou dez dos seus servos e lhes deu dez minas. Disse ele: ‘Façam esse dinheiro render até à minha volta’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25746, 'nvi', 'Lucas', 'lc', 19, 14, '"Mas os seus súditos o odiavam e depois enviaram uma delegação para lhe dizer: ‘Não queremos que este homem seja nosso rei’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25747, 'nvi', 'Lucas', 'lc', 19, 15, '"Contudo, foi feito rei e voltou. Então mandou chamar os servos a quem dera o dinheiro, a fim de saber quanto tinham lucrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25748, 'nvi', 'Lucas', 'lc', 19, 16, '"O primeiro veio e disse: ‘Senhor, a tua mina rendeu outras dez’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25749, 'nvi', 'Lucas', 'lc', 19, 17, '" ‘Muito bem, meu bom servo! ’, respondeu o seu senhor. ‘Por ter sido confiável no pouco, governe sobre dez cidades’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25750, 'nvi', 'Lucas', 'lc', 19, 18, '"O segundo veio e disse: ‘Senhor, a tua mina rendeu cinco vezes mais’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25751, 'nvi', 'Lucas', 'lc', 19, 19, '"O seu senhor respondeu: ‘Também você, encarregue-se de cinco cidades’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25752, 'nvi', 'Lucas', 'lc', 19, 20, '"Então veio outro servo e disse: ‘Senhor, aqui está a tua mina; eu a conservei guardada num pedaço de pano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25753, 'nvi', 'Lucas', 'lc', 19, 21, 'Tive medo, porque és um homem severo. Tiras o que não puseste e colhes o que não semeaste’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25754, 'nvi', 'Lucas', 'lc', 19, 22, '"O seu senhor respondeu: ‘Eu o julgarei pelas suas próprias palavras, servo mau! Você sabia que sou homem severo, que tiro o que não pus e colho o que não semeei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25755, 'nvi', 'Lucas', 'lc', 19, 23, 'Então, por que não confiou o meu dinheiro ao banco? Assim, quando eu voltasse o receberia com os juros’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25756, 'nvi', 'Lucas', 'lc', 19, 24, '"E disse aos que estavam ali: ‘Tomem dele a sua mina e dêem-na ao que tem dez’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25757, 'nvi', 'Lucas', 'lc', 19, 25, '" ‘Senhor’, disseram, ‘ele já tem dez! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25758, 'nvi', 'Lucas', 'lc', 19, 26, '"Ele respondeu: ‘Eu lhes digo que a quem tem, mais será dado, mas a quem não tem, até o que tiver lhe será tirado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25759, 'nvi', 'Lucas', 'lc', 19, 27, 'E aqueles inimigos meus, que não queriam que eu reinasse sobre eles, tragam-nos aqui e matem-nos na minha frente! ’ "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25760, 'nvi', 'Lucas', 'lc', 19, 28, 'Depois de dizer isso, Jesus foi adiante, subindo para Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25761, 'nvi', 'Lucas', 'lc', 19, 29, 'Ao aproximar-se de Betfagé e de Betânia, no monte chamado das Oliveiras, enviou dois dos seus discípulos, dizendo-lhes:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25762, 'nvi', 'Lucas', 'lc', 19, 30, '"Vão ao povoado que está adiante e, ao entrarem, encontrarão um jumentinho amarrado, no qual ninguém jamais montou. Desamarrem-no e tragam-no aqui.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25763, 'nvi', 'Lucas', 'lc', 19, 31, 'Se alguém lhes perguntar: ‘Por que o estão desamarrando? ’ digam-lhe: ‘O Senhor precisa dele’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25764, 'nvi', 'Lucas', 'lc', 19, 32, 'Os que tinham sido enviados foram e encontraram o animal exatamente como ele lhes tinha dito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25765, 'nvi', 'Lucas', 'lc', 19, 33, 'Quando estavam desamarrando o jumentinho, os seus donos lhes perguntaram: "Por que vocês estão desamarrando o jumentinho? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25766, 'nvi', 'Lucas', 'lc', 19, 34, 'Eles responderam: "O Senhor precisa dele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25767, 'nvi', 'Lucas', 'lc', 19, 35, 'Levaram-no a Jesus, lançaram seus mantos sobre o jumentinho e fizeram que Jesus montasse nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25768, 'nvi', 'Lucas', 'lc', 19, 36, 'Enquanto ele prosseguia, o povo estendia os seus mantos pelo caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25769, 'nvi', 'Lucas', 'lc', 19, 37, 'Quando ele já estava perto da descida do monte das Oliveiras, toda a multidão dos discípulos começou a louvar a Deus alegremente, em alta voz, por todos os milagres que tinham visto. Exclamavam:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25770, 'nvi', 'Lucas', 'lc', 19, 38, '"Bendito é o rei que vem em nome do Senhor! " "Paz no céu e glória nas alturas! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25771, 'nvi', 'Lucas', 'lc', 19, 39, 'Alguns dos fariseus que estavam no meio da multidão disseram a Jesus: "Mestre, repreende os teus discípulos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25772, 'nvi', 'Lucas', 'lc', 19, 40, '"Eu lhes digo", respondeu ele, "se eles se calarem, as pedras clamarão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25773, 'nvi', 'Lucas', 'lc', 19, 41, 'Quando se aproximou e viu a cidade, Jesus chorou sobre ela');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25774, 'nvi', 'Lucas', 'lc', 19, 42, 'e disse: "Se você compreendesse neste dia, sim, você também, o que traz a paz! Mas agora isso está oculto aos seus olhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25775, 'nvi', 'Lucas', 'lc', 19, 43, 'Virão dias em que os seus inimigos construirão trincheiras contra você, e a rodearão e a cercarão de todos os lados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25776, 'nvi', 'Lucas', 'lc', 19, 44, 'Também a lançarão por terra, você e os seus filhos. Não deixarão pedra sobre pedra, porque você não reconheceu o tempo em que Deus a visitaria".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25777, 'nvi', 'Lucas', 'lc', 19, 45, 'Então ele entrou no templo e começou a expulsar os que estavam vendendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25778, 'nvi', 'Lucas', 'lc', 19, 46, 'Disse-lhes: "Está escrito: ‘A minha casa será casa de oração’; mas vocês fizeram dela ‘um covil de ladrões’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25779, 'nvi', 'Lucas', 'lc', 19, 47, 'Todos os dias ele ensinava no templo. Mas os chefes dos sacerdotes, os mestres da lei e os líderes do povo procuravam matá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25780, 'nvi', 'Lucas', 'lc', 19, 48, 'Todavia, não conseguiam encontrar uma forma de fazê-lo, porque todo o povo estava fascinado pelas suas palavras.');
