INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24975, 'nvi', 'Lucas', 'lc', 2, 1, 'Naqueles dias César Augusto publicou um decreto ordenando o recenseamento de todo o império romano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24976, 'nvi', 'Lucas', 'lc', 2, 2, 'Este foi o primeiro recenseamento feito quando Quirino era governador da Síria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24977, 'nvi', 'Lucas', 'lc', 2, 3, 'E todos iam para a sua cidade natal, a fim de alistar-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24978, 'nvi', 'Lucas', 'lc', 2, 4, 'Assim, José também foi da cidade de Nazaré da Galiléia para a Judéia, para Belém, cidade de Davi, porque pertencia à casa e à linhagem de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24979, 'nvi', 'Lucas', 'lc', 2, 5, 'Ele foi a fim de alistar-se, com Maria, que lhe estava prometida em casamento e esperava um filho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24980, 'nvi', 'Lucas', 'lc', 2, 6, 'Enquanto estavam lá, chegou o tempo de nascer o bebê,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24981, 'nvi', 'Lucas', 'lc', 2, 7, 'e ela deu à luz o seu primogênito. Envolveu-o em panos e o colocou numa manjedoura, porque não havia lugar para eles na hospedaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24982, 'nvi', 'Lucas', 'lc', 2, 8, 'Havia pastores que estavam nos campos próximos e durante a noite tomavam conta dos seus rebanhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24983, 'nvi', 'Lucas', 'lc', 2, 9, 'E aconteceu que um anjo do Senhor apareceu-lhes e a glória do Senhor resplandeceu ao redor deles; e ficaram aterrorizados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24984, 'nvi', 'Lucas', 'lc', 2, 10, 'Mas o anjo lhes disse: "Não tenham medo. Estou lhes trazendo boas novas de grande alegria, que são para todo o povo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24985, 'nvi', 'Lucas', 'lc', 2, 11, 'Hoje, na cidade de Davi, lhes nasceu o Salvador que é Cristo, o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24986, 'nvi', 'Lucas', 'lc', 2, 12, 'Isto lhes servirá de sinal: encontrarão o bebê envolto em panos e deitado numa manjedoura".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24987, 'nvi', 'Lucas', 'lc', 2, 13, 'De repente, uma grande multidão do exército celestial apareceu com o anjo, louvando a Deus e dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24988, 'nvi', 'Lucas', 'lc', 2, 14, '"Glória a Deus nas alturas, e paz na terra aos homens aos quais ele concede o seu favor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24989, 'nvi', 'Lucas', 'lc', 2, 15, 'Quando os anjos os deixaram e foram para o céu, os pastores disseram uns aos outros: "Vamos a Belém, e vejamos isso que aconteceu, e que o Senhor nos deu a conhecer".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24990, 'nvi', 'Lucas', 'lc', 2, 16, 'Então correram para lá e encontraram Maria e José, e o bebê deitado na manjedoura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24991, 'nvi', 'Lucas', 'lc', 2, 17, 'Depois de o verem, contaram a todos o que lhes fora dito a respeito daquele menino,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24992, 'nvi', 'Lucas', 'lc', 2, 18, 'e todos os que ouviram o que os pastores diziam ficaram admirados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24993, 'nvi', 'Lucas', 'lc', 2, 19, 'Maria, porém, guardava todas essas coisas e sobre elas refletia em seu coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24994, 'nvi', 'Lucas', 'lc', 2, 20, 'Os pastores voltaram glorificando e louvando a Deus por tudo o que tinham visto e ouvido, como lhes fora dito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24995, 'nvi', 'Lucas', 'lc', 2, 21, 'Completando-se os oito dias para a circuncisão do menino, foi-lhe posto o nome de Jesus, o qual lhe tinha sido dado pelo anjo antes de ele nascer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24996, 'nvi', 'Lucas', 'lc', 2, 22, 'Completando-se o tempo da purificação deles, de acordo com a Lei de Moisés, José e Maria o levaram a Jerusalém para apresentá-lo ao Senhor');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24997, 'nvi', 'Lucas', 'lc', 2, 23, '( como está escrito na Lei do Senhor: "Todo primogênito do sexo masculino será consagrado ao Senhor" )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24998, 'nvi', 'Lucas', 'lc', 2, 24, 'e para oferecer um sacrifício, de acordo com o que diz a Lei do Senhor: "duas rolinhas ou dois pombinhos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (24999, 'nvi', 'Lucas', 'lc', 2, 25, 'Havia em Jerusalém um homem chamado Simeão, que era justo e piedoso, e que esperava a consolação de Israel; e o Espírito Santo estava sobre ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25000, 'nvi', 'Lucas', 'lc', 2, 26, 'Fora-lhe revelado pelo Espírito Santo que ele não morreria antes de ver o Cristo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25001, 'nvi', 'Lucas', 'lc', 2, 27, 'Movido pelo Espírito, ele foi ao templo. Quando os pais trouxeram o menino Jesus para lhe fazer conforme requeria o costume da lei,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25002, 'nvi', 'Lucas', 'lc', 2, 28, 'Simeão o tomou nos braços e louvou a Deus, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25003, 'nvi', 'Lucas', 'lc', 2, 29, '"Ó Soberano, como prometeste, agora podes despedir em paz o teu servo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25004, 'nvi', 'Lucas', 'lc', 2, 30, 'Pois os meus olhos já viram a tua salvação,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25005, 'nvi', 'Lucas', 'lc', 2, 31, 'que preparaste à vista de todos os povos:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25006, 'nvi', 'Lucas', 'lc', 2, 32, 'luz para revelação aos gentios e para a glória de Israel, teu povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25007, 'nvi', 'Lucas', 'lc', 2, 33, 'O pai e a mãe do menino estavam admirados com o que fora dito a respeito dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25008, 'nvi', 'Lucas', 'lc', 2, 34, 'E Simeão os abençoou e disse a Maria, mãe de Jesus: "Este menino está destinado a causar a queda e o soerguimento de muitos em Israel, e a ser um sinal de contradição,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25009, 'nvi', 'Lucas', 'lc', 2, 35, 'de modo que o pensamento de muitos corações será revelado. Quanto a você, uma espada atravessará a sua alma".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25010, 'nvi', 'Lucas', 'lc', 2, 36, 'Estava ali a profetisa Ana, filha de Fanuel, da tribo de Aser. Era muito idosa; havia vivido com seu marido sete anos depois de se casar');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25011, 'nvi', 'Lucas', 'lc', 2, 37, 'e então permanecera viúva até a idade de oitenta e quatro anos. Nunca deixava o templo: adorava a Deus jejuando e orando dia e noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25012, 'nvi', 'Lucas', 'lc', 2, 38, 'Tendo chegado ali naquele exato momento, deu graças a Deus e falava a respeito do menino a todos os que esperavam a redenção de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25013, 'nvi', 'Lucas', 'lc', 2, 39, 'Depois de terem feito tudo o que era exigido pela Lei do Senhor, voltaram para a sua própria cidade, Nazaré, na Galiléia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25014, 'nvi', 'Lucas', 'lc', 2, 40, 'O menino crescia e se fortalecia, enchendo-se de sabedoria; e a graça de Deus estava sobre ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25015, 'nvi', 'Lucas', 'lc', 2, 41, 'Todos os anos seus pais iam a Jerusalém para a festa da Páscoa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25016, 'nvi', 'Lucas', 'lc', 2, 42, 'Quando ele completou doze anos de idade, eles subiram à festa, conforme o costume.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25017, 'nvi', 'Lucas', 'lc', 2, 43, 'Terminada a festa, voltando seus pais para casa, o menino Jesus ficou em Jerusalém, sem que eles percebessem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25018, 'nvi', 'Lucas', 'lc', 2, 44, 'Pensando que ele estava entre os companheiros de viagem, caminharam o dia todo. Então começaram a procurá-lo entre os seus parentes e conhecidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25019, 'nvi', 'Lucas', 'lc', 2, 45, 'Não o encontrando, voltaram a Jerusalém para procurá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25020, 'nvi', 'Lucas', 'lc', 2, 46, 'Depois de três dias o encontraram no templo, sentado entre os mestres, ouvindo-os e fazendo-lhes perguntas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25021, 'nvi', 'Lucas', 'lc', 2, 47, 'Todos os que o ouviam ficavam maravilhados com o seu entendimento e com as suas respostas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25022, 'nvi', 'Lucas', 'lc', 2, 48, 'Quando seus pais o viram, ficaram perplexos. Sua mãe lhe disse: "Filho, por que você nos fez isto? Seu pai e eu estávamos aflitos, à sua procura".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25023, 'nvi', 'Lucas', 'lc', 2, 49, 'Ele perguntou: "Por que vocês estavam me procurando? Não sabiam que eu devia estar na casa de meu Pai? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25024, 'nvi', 'Lucas', 'lc', 2, 50, 'Mas eles não compreenderam o que lhes dizia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25025, 'nvi', 'Lucas', 'lc', 2, 51, 'Então foi com eles para Nazaré, e era-lhes obediente. Sua mãe, porém, guardava todas essas coisas em seu coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25026, 'nvi', 'Lucas', 'lc', 2, 52, 'Jesus ia crescendo em sabedoria, estatura e graça diante de Deus e dos homens.');
