INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25781, 'nvi', 'Lucas', 'lc', 20, 1, 'Certo dia, quando Jesus estava ensinando o povo no templo e pregando as boas novas, chegaram-se a ele os chefes dos sacerdotes, juntamente com os mestres da lei e os líderes religiosos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25782, 'nvi', 'Lucas', 'lc', 20, 2, 'e lhe perguntaram: "Com que autoridade estás fazendo estas coisas? Quem te deu esta autoridade? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25783, 'nvi', 'Lucas', 'lc', 20, 3, 'Ele respondeu: "Eu também lhes farei uma pergunta: Digam-me:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25784, 'nvi', 'Lucas', 'lc', 20, 4, 'O batismo de João era do céu, ou dos homens? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25785, 'nvi', 'Lucas', 'lc', 20, 5, 'Eles discutiam entre si, dizendo: "Se dissermos: ‘do céu’, ele perguntará: ‘Então por que vocês não creram nele? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25786, 'nvi', 'Lucas', 'lc', 20, 6, 'Mas se dissermos: ‘dos homens’, todo o povo nos apedrejará, porque convencidos estão de que João era um profeta".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25787, 'nvi', 'Lucas', 'lc', 20, 7, 'Assim, responderam: "Não sabemos de onde era".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25788, 'nvi', 'Lucas', 'lc', 20, 8, 'Disse então Jesus: "Tampouco lhes direi com que autoridade estou fazendo estas coisas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25789, 'nvi', 'Lucas', 'lc', 20, 9, 'Então Jesus passou a contar ao povo esta parábola: "Certo homem plantou uma vinha, arrendou-a a alguns lavradores e ausentou-se por longo tempo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25790, 'nvi', 'Lucas', 'lc', 20, 10, 'Na época da colheita, ele enviou um servo aos lavradores, para que lhe entregassem parte do fruto da vinha. Mas os lavradores o espancaram e o mandaram embora de mãos vazias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25791, 'nvi', 'Lucas', 'lc', 20, 11, 'Ele mandou outro servo, mas a esse também espancaram e o trataram de maneira humilhante, mandando-o embora de mãos vazias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25792, 'nvi', 'Lucas', 'lc', 20, 12, 'Enviou ainda um terceiro, e eles o feriram e o expulsaram da vinha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25793, 'nvi', 'Lucas', 'lc', 20, 13, '"Então o proprietário da vinha disse: ‘Que farei? Mandarei meu filho amado; quem sabe o respeitarão’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25794, 'nvi', 'Lucas', 'lc', 20, 14, '"Mas quando os lavradores o viram, combinaram entre si dizendo: ‘Este é o herdeiro. Vamos matá-lo, e a herança será nossa’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25795, 'nvi', 'Lucas', 'lc', 20, 15, 'Assim, lançaram-no fora da vinha e o mataram. "O que lhes fará então o dono da vinha?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25796, 'nvi', 'Lucas', 'lc', 20, 16, 'Virá, matará aqueles lavradores e dará a vinha a outros". Quando o povo ouviu isso, disse: "Que isso nunca aconteça! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25797, 'nvi', 'Lucas', 'lc', 20, 17, 'Jesus olhou fixamente para eles e perguntou: "Então, qual é o significado do que está escrito? ‘A pedra que os construtores rejeitaram tornou-se a pedra angular’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25798, 'nvi', 'Lucas', 'lc', 20, 18, 'Todo o que cair sobre esta pedra será despedaçado, e aquele sobre quem ela cair será reduzido a pó".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25799, 'nvi', 'Lucas', 'lc', 20, 19, 'Os mestres da lei e os chefes dos sacerdotes procuravam uma forma de prendê-lo imediatamente, pois perceberam que era contra eles que ele havia contado essa parábola. Todavia tinham medo do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25800, 'nvi', 'Lucas', 'lc', 20, 20, 'Pondo-se a vigiá-lo, eles mandaram espiões, que se fingiam justos, para apanhar Jesus em alguma coisa que ele dissesse, de forma que o pudessem entregar ao poder e à autoridade do governador.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25801, 'nvi', 'Lucas', 'lc', 20, 21, 'Assim, os espiões lhe perguntaram: "Mestre, sabemos que falas e ensinas o que é correto, e que não mostras parcialidade, mas ensinas o caminho de Deus conforme a verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25802, 'nvi', 'Lucas', 'lc', 20, 22, 'É certo pagar imposto a César ou não? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25803, 'nvi', 'Lucas', 'lc', 20, 23, 'Ele percebeu a astúcia deles e lhes disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25804, 'nvi', 'Lucas', 'lc', 20, 24, '"Mostrem-me um denário. De quem é a imagem e a inscrição que há nele? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25805, 'nvi', 'Lucas', 'lc', 20, 25, '"De César", responderam eles. Ele lhes disse: "Portanto, dêem a César o que é de César, e a Deus o que é de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25806, 'nvi', 'Lucas', 'lc', 20, 26, 'E não conseguiram apanhá-lo em nenhuma palavra diante do povo. E, admirados com a sua resposta, ficaram em silêncio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25807, 'nvi', 'Lucas', 'lc', 20, 27, 'Alguns dos saduceus, que dizem que não há ressurreição, aproximaram-se de Jesus com a seguinte questão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25808, 'nvi', 'Lucas', 'lc', 20, 28, '"Mestre", disseram eles, "Moisés nos deixou escrito que, se o irmão de um homem morrer e deixar mulher sem filhos, este deverá casar-se com a viúva e ter filhos para seu irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25809, 'nvi', 'Lucas', 'lc', 20, 29, 'Havia sete irmãos. O primeiro casou-se e morreu sem deixar filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25810, 'nvi', 'Lucas', 'lc', 20, 30, 'O segundo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25811, 'nvi', 'Lucas', 'lc', 20, 31, 'e o terceiro e depois também os outros casaram-se com ela; e morreram os sete sucessivamente, sem deixar filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25812, 'nvi', 'Lucas', 'lc', 20, 32, 'Finalmente morreu também a mulher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25813, 'nvi', 'Lucas', 'lc', 20, 33, 'Na ressurreição, de quem ela será esposa, visto que os sete foram casados com ela? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25814, 'nvi', 'Lucas', 'lc', 20, 34, 'Jesus respondeu: "Os filhos desta era casam-se e são dados em casamento,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25815, 'nvi', 'Lucas', 'lc', 20, 35, 'mas os que forem considerados dignos de tomar parte na era que há de vir e na ressurreição dos mortos não se casarão nem serão dados em casamento,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25816, 'nvi', 'Lucas', 'lc', 20, 36, 'e não podem mais morrer, pois são como os anjos. São filhos de Deus, visto que são filhos da ressurreição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25817, 'nvi', 'Lucas', 'lc', 20, 37, 'E que os mortos ressuscitam, já Moisés mostrou, no relato da sarça, quando ao Senhor ele chama ‘Deus de Abraão, Deus de Isaque e Deus de Jacó’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25818, 'nvi', 'Lucas', 'lc', 20, 38, 'Ele não é Deus de mortos, mas de vivos, pois para ele todos vivem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25819, 'nvi', 'Lucas', 'lc', 20, 39, 'Alguns dos mestres da lei disseram: "Respondeste bem, Mestre! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25820, 'nvi', 'Lucas', 'lc', 20, 40, 'E ninguém mais ousava fazer-lhe perguntas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25821, 'nvi', 'Lucas', 'lc', 20, 41, 'Então Jesus lhes perguntou: "Como dizem que o Cristo é Filho de Davi?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25822, 'nvi', 'Lucas', 'lc', 20, 42, '"O próprio Davi afirma no Livro dos Salmos: ‘O Senhor disse ao meu Senhor: "Senta-te à minha direita');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25823, 'nvi', 'Lucas', 'lc', 20, 43, 'até que eu ponha os teus inimigos como estrado para os teus pés" ’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25824, 'nvi', 'Lucas', 'lc', 20, 44, 'Portanto Davi o chama ‘Senhor’. Então, como é que ele pode ser seu filho? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25825, 'nvi', 'Lucas', 'lc', 20, 45, 'Estando todo o povo a ouvi-lo, Jesus disse aos seus discípulos:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25826, 'nvi', 'Lucas', 'lc', 20, 46, '"Cuidado com os mestres da lei. Eles fazem questão de andar com roupas especiais, e gostam muito de receber saudações nas praças e de ocupar os lugares mais importantes nas sinagogas e os lugares de honra nos banquetes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25827, 'nvi', 'Lucas', 'lc', 20, 47, 'Eles devoram as casas das viúvas, e, para disfarçar, fazem longas orações. Esses homens serão punidos com maior rigor! "');
