INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25828, 'nvi', 'Lucas', 'lc', 21, 1, 'Jesus olhou e viu os ricos colocando suas contribuições nas caixas de ofertas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25829, 'nvi', 'Lucas', 'lc', 21, 2, 'Viu também uma viúva pobre colocar duas pequeninas moedas de cobre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25830, 'nvi', 'Lucas', 'lc', 21, 3, 'E disse: "Afirmo-lhes que esta viúva pobre colocou mais do que todos os outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25831, 'nvi', 'Lucas', 'lc', 21, 4, 'Todos esses deram do que lhes sobrava; mas ela, da sua pobreza, deu tudo o que possuía para viver".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25832, 'nvi', 'Lucas', 'lc', 21, 5, 'Alguns dos seus discípulos estavam comentando como o templo era adornado com lindas pedras e dádivas dedicadas a Deus. Mas Jesus disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25833, 'nvi', 'Lucas', 'lc', 21, 6, '"Disso que vocês estão vendo, dias virão em que não ficará pedra sobre pedra; serão todas derrubadas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25834, 'nvi', 'Lucas', 'lc', 21, 7, '"Mestre", perguntaram eles, "quando acontecerão essas coisas? E qual será o sinal de que elas estão prestes a acontecer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25835, 'nvi', 'Lucas', 'lc', 21, 8, 'Ele respondeu: "Cuidado para não serem enganados. Pois muitos virão em meu nome, dizendo: ‘Sou eu! ’ e ‘o tempo está próximo’. Não os sigam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25836, 'nvi', 'Lucas', 'lc', 21, 9, 'Quando ouvirem falar de guerras e rebeliões, não tenham medo. É necessário que primeiro aconteçam essas coisas, mas o fim não virá imediatamente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25837, 'nvi', 'Lucas', 'lc', 21, 10, 'Então lhes disse: "Nação se levantará contra nação, e reino contra reino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25838, 'nvi', 'Lucas', 'lc', 21, 11, 'Haverá grandes terremotos, fomes e pestes em vários lugares, e acontecimentos terríveis e grandes sinais provenientes do céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25839, 'nvi', 'Lucas', 'lc', 21, 12, '"Mas antes de tudo isso, prenderão e perseguirão vocês. Então os entregarão às sinagogas e prisões, e vocês serão levados à presença de reis e governadores, tudo por causa do meu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25840, 'nvi', 'Lucas', 'lc', 21, 13, 'Será para vocês uma oportunidade de dar testemunho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25841, 'nvi', 'Lucas', 'lc', 21, 14, 'Mas convençam-se de uma vez de que não devem preocupar-se com o que dirão para se defender.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25842, 'nvi', 'Lucas', 'lc', 21, 15, 'Pois eu lhes darei palavras e sabedoria a que nenhum dos seus adversários será capaz de resistir ou contradizer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25843, 'nvi', 'Lucas', 'lc', 21, 16, 'Vocês serão traídos até por pais, irmãos, parentes e amigos, e eles entregarão alguns de vocês à morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25844, 'nvi', 'Lucas', 'lc', 21, 17, 'Todos odiarão vocês por causa do meu nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25845, 'nvi', 'Lucas', 'lc', 21, 18, 'Contudo, nenhum fio de cabelo da cabeça de vocês se perderá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25846, 'nvi', 'Lucas', 'lc', 21, 19, 'É perseverando que vocês obterão a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25847, 'nvi', 'Lucas', 'lc', 21, 20, '"Quando virem Jerusalém rodeada de exércitos, vocês saberão que a sua devastação está próxima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25848, 'nvi', 'Lucas', 'lc', 21, 21, 'Então os que estiverem na Judéia fujam para os montes, os que estiverem na cidade saiam, e os que estiverem no campo não entrem na cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25849, 'nvi', 'Lucas', 'lc', 21, 22, 'Pois esses são os dias da vingança, em cumprimento de tudo o que foi escrito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25850, 'nvi', 'Lucas', 'lc', 21, 23, 'Como serão terríveis aqueles dias para as grávidas e para as que estiverem amamentando! Haverá grande aflição na terra e ira contra este povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25851, 'nvi', 'Lucas', 'lc', 21, 24, 'Cairão pela espada e serão levados como prisioneiros para todas as nações. Jerusalém será pisada pelos gentios, até que os tempos deles se cumpram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25852, 'nvi', 'Lucas', 'lc', 21, 25, '"Haverá sinais no sol, na lua e nas estrelas. Na terra, as nações se verão em angústia e perplexidade com o bramido e a agitação do mar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25853, 'nvi', 'Lucas', 'lc', 21, 26, 'Os homens desmaiarão de terror, apreensivos com o que estará sobrevindo ao mundo; e os poderes celestes serão abalados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25854, 'nvi', 'Lucas', 'lc', 21, 27, 'Então se verá o Filho do homem vindo numa nuvem com poder e grande glória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25855, 'nvi', 'Lucas', 'lc', 21, 28, 'Quando começarem a acontecer estas coisas, levantem-se e ergam a cabeça, porque estará próxima a redenção de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25856, 'nvi', 'Lucas', 'lc', 21, 29, 'Ele lhes contou esta parábola: "Observem a figueira e todas as árvores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25857, 'nvi', 'Lucas', 'lc', 21, 30, 'Quando elas brotam, vocês mesmos percebem e sabem que o verão está próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25858, 'nvi', 'Lucas', 'lc', 21, 31, 'Assim também, quando virem estas coisas acontecendo, saibam que o Reino de Deus está próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25859, 'nvi', 'Lucas', 'lc', 21, 32, '"Eu lhes asseguro que não passará esta geração até que todas essas coisas aconteçam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25860, 'nvi', 'Lucas', 'lc', 21, 33, 'O céu e a terra passarão, mas as minhas palavras jamais passarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25861, 'nvi', 'Lucas', 'lc', 21, 34, '"Tenham cuidado, para que os seus corações não fiquem carregados de libertinagem, bebedeira e ansiedades da vida, e aquele dia venha sobre vocês inesperadamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25862, 'nvi', 'Lucas', 'lc', 21, 35, 'Porque ele virá sobre todos os que vivem na face de toda a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25863, 'nvi', 'Lucas', 'lc', 21, 36, 'Estejam sempre atentos e orem para que vocês possam escapar de tudo o que está para acontecer, e estar de pé diante do Filho do homem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25864, 'nvi', 'Lucas', 'lc', 21, 37, 'Jesus passava o dia ensinando no templo; e, ao entardecer, saía para passar a noite no monte chamado das Oliveiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25865, 'nvi', 'Lucas', 'lc', 21, 38, 'Todo o povo ia de manhã cedo ouvi-lo no templo.');
