INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25866, 'nvi', 'Lucas', 'lc', 22, 1, 'Estava se aproximando a festa dos pães sem fermento, chamada Páscoa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25867, 'nvi', 'Lucas', 'lc', 22, 2, 'e os chefes dos sacerdotes e os mestres da lei estavam procurando um meio de matar Jesus, mas tinham medo do povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25868, 'nvi', 'Lucas', 'lc', 22, 3, 'Então Satanás entrou em Judas, chamado Iscariotes, um dos Doze.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25869, 'nvi', 'Lucas', 'lc', 22, 4, 'Judas dirigiu-se aos chefes dos sacerdotes e aos oficiais da guarda do templo e tratou com eles como lhes poderia entregar Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25870, 'nvi', 'Lucas', 'lc', 22, 5, 'A proposta muito os alegrou, e lhe prometeram dinheiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25871, 'nvi', 'Lucas', 'lc', 22, 6, 'Ele consentiu e ficou esperando uma oportunidade para lhes entregar Jesus quando a multidão não estivesse presente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25872, 'nvi', 'Lucas', 'lc', 22, 7, 'Finalmente, chegou o dia dos pães sem fermento, no qual devia ser sacrificado o cordeiro pascal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25873, 'nvi', 'Lucas', 'lc', 22, 8, 'Jesus enviou Pedro e João, dizendo: "Vão preparar a refeição da Páscoa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25874, 'nvi', 'Lucas', 'lc', 22, 9, '"Onde queres que a preparemos? ", perguntaram eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25875, 'nvi', 'Lucas', 'lc', 22, 10, 'Ele respondeu: "Ao entrarem na cidade, vocês encontrarão um homem carregando um pote de água. Sigam-no até a casa em que ele entrar');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25876, 'nvi', 'Lucas', 'lc', 22, 11, 'e digam ao dono da casa: ‘O Mestre pergunta: Onde é o salão de hóspedes no qual poderei comer a Páscoa com os meus discípulos? ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25877, 'nvi', 'Lucas', 'lc', 22, 12, 'Ele lhes mostrará uma ampla sala no andar superior, toda mobiliada. Façam ali os preparativos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25878, 'nvi', 'Lucas', 'lc', 22, 13, 'Eles saíram e encontraram tudo como Jesus lhes tinha dito. Então, prepararam a Páscoa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25879, 'nvi', 'Lucas', 'lc', 22, 14, 'Quando chegou a hora, Jesus e os seus apóstolos reclinaram-se à mesa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25880, 'nvi', 'Lucas', 'lc', 22, 15, 'E disse-lhes: "Desejei ansiosamente comer esta Páscoa com vocês antes de sofrer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25881, 'nvi', 'Lucas', 'lc', 22, 16, 'Pois eu lhes digo: Não comerei dela novamente até que se cumpra no Reino de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25882, 'nvi', 'Lucas', 'lc', 22, 17, 'Recebendo um cálice, ele deu graças e disse: "Tomem isto e partilhem uns com os outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25883, 'nvi', 'Lucas', 'lc', 22, 18, 'Pois eu lhes digo que não beberei outra vez do fruto da videira até que venha o Reino de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25884, 'nvi', 'Lucas', 'lc', 22, 19, 'Tomando o pão, deu graças, partiu-o e o deu aos discípulos, dizendo: "Isto é o meu corpo dado em favor de vocês; façam isto em memória de mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25885, 'nvi', 'Lucas', 'lc', 22, 20, 'Da mesma forma, depois da ceia, tomou o cálice, dizendo: "Este cálice é a nova aliança no meu sangue, derramado em favor de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25886, 'nvi', 'Lucas', 'lc', 22, 21, '"Mas eis que a mão daquele que vai me trair está com a minha sobre a mesa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25887, 'nvi', 'Lucas', 'lc', 22, 22, 'O Filho do homem vai, como foi determinado; mas ai daquele que o trai! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25888, 'nvi', 'Lucas', 'lc', 22, 23, 'Eles começaram a perguntar entre si qual deles iria fazer aquilo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25889, 'nvi', 'Lucas', 'lc', 22, 24, 'Surgiu também uma discussão entre eles, acerca de qual deles era considerado o maior.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25890, 'nvi', 'Lucas', 'lc', 22, 25, 'Jesus lhes disse: "Os reis das nações dominam sobre elas; e os que exercem autoridade sobre elas são chamados benfeitores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25891, 'nvi', 'Lucas', 'lc', 22, 26, 'Mas, vocês não serão assim. Pelo contrário, o maior entre vocês deverá ser como o mais jovem, e aquele que governa como o que serve.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25892, 'nvi', 'Lucas', 'lc', 22, 27, 'Pois quem é maior: o que está à mesa, ou o que serve? Não é o que está à mesa? Mas eu estou entre vocês como quem serve.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25893, 'nvi', 'Lucas', 'lc', 22, 28, 'Vocês são os que têm permanecido ao meu lado durante as minhas provações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25894, 'nvi', 'Lucas', 'lc', 22, 29, 'E eu lhes designo um Reino, assim como meu Pai o designou a mim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25895, 'nvi', 'Lucas', 'lc', 22, 30, 'para que vocês possam comer e beber à minha mesa no meu Reino e sentar-se em tronos, julgando as doze tribos de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25896, 'nvi', 'Lucas', 'lc', 22, 31, '"Simão, Simão, Satanás pediu vocês para peneirá-los como trigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25897, 'nvi', 'Lucas', 'lc', 22, 32, 'Mas eu orei por você, para que a sua fé não desfaleça. E quando você se converter, fortaleça os seus irmãos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25898, 'nvi', 'Lucas', 'lc', 22, 33, 'Mas ele respondeu: "Estou pronto para ir contigo para a prisão e para a morte".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25899, 'nvi', 'Lucas', 'lc', 22, 34, 'Respondeu Jesus: "Eu lhe digo, Pedro, que antes que o galo cante hoje, três vezes você negará que me conhece".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25900, 'nvi', 'Lucas', 'lc', 22, 35, 'Então Jesus lhes perguntou: "Quando eu os enviei sem bolsa, saco de viagem ou sandálias, faltou-lhes alguma coisa? " "Nada", responderam eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25901, 'nvi', 'Lucas', 'lc', 22, 36, 'Ele lhes disse: "Mas agora, se vocês têm bolsa, levem-na, e também o saco de viagem; e se não têm espada, vendam a sua capa e comprem uma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25902, 'nvi', 'Lucas', 'lc', 22, 37, 'Está escrito: ‘E ele foi contado com os transgressores’; e eu lhes digo que isto precisa cumprir-se em mim. Sim, o que está escrito a meu respeito está para se cumprir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25903, 'nvi', 'Lucas', 'lc', 22, 38, 'Os discípulos disseram: "Vê, Senhor, aqui estão duas espadas". "É o suficiente! ", respondeu ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25904, 'nvi', 'Lucas', 'lc', 22, 39, 'Como de costume, Jesus foii para o monte das Oliveiras, e os seus discípulos o seguiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25905, 'nvi', 'Lucas', 'lc', 22, 40, 'Chegando ao lugar, ele lhes disse: "Orem para que vocês não caiam em tentação".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25906, 'nvi', 'Lucas', 'lc', 22, 41, 'Ele se afastou deles a uma pequena distância, ajoelhou-se e começou a orar:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25907, 'nvi', 'Lucas', 'lc', 22, 42, '"Pai, se queres, afasta de mim este cálice; contudo, não seja feita a minha vontade, mas a tua".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25908, 'nvi', 'Lucas', 'lc', 22, 43, 'Apareceu-lhe então um anjo do céu que o fortalecia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25909, 'nvi', 'Lucas', 'lc', 22, 44, 'Estando angustiado, ele orou ainda mais intensamente; e o seu suor era como gotas de sangue que caíam no chão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25910, 'nvi', 'Lucas', 'lc', 22, 45, 'Quando se levantou da oração e voltou aos discípulos, encontrou-os dormindo, dominados pela tristeza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25911, 'nvi', 'Lucas', 'lc', 22, 46, '"Por que estão dormindo? ", perguntou-lhes. "Levantem-se e orem para que vocês não caiam em tentação! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25912, 'nvi', 'Lucas', 'lc', 22, 47, 'Enquanto ele ainda falava, apareceu uma multidão conduzida por Judas, um dos Doze. Este se aproximou de Jesus para saudá-lo com um beijo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25913, 'nvi', 'Lucas', 'lc', 22, 48, 'Mas Jesus lhe perguntou: "Judas, com um beijo você está traindo o Filho do homem? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25914, 'nvi', 'Lucas', 'lc', 22, 49, 'Ao verem o que ia acontecer, os que estavam com Jesus lhe disseram: "Senhor, atacaremos com espadas? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25915, 'nvi', 'Lucas', 'lc', 22, 50, 'E um deles feriu o servo do sumo sacerdote, decepando-lhe a orelha direita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25916, 'nvi', 'Lucas', 'lc', 22, 51, 'Jesus, porém, respondeu: "Basta! " E tocando na orelha do homem, ele o curou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25917, 'nvi', 'Lucas', 'lc', 22, 52, 'Então Jesus disse aos chefes dos sacerdotes, aos oficiais da guarda do templo e aos líderes religiosos que tinham vindo procurá-lo: "Estou eu chefiando alguma rebelião, para que vocês tenham vindo com espadas e varas?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25918, 'nvi', 'Lucas', 'lc', 22, 53, 'Todos os dias eu estava com vocês no templo e vocês não levantaram a mão contra mim. Mas esta é a hora de vocês — quando as trevas reinam".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25919, 'nvi', 'Lucas', 'lc', 22, 54, 'Então, prendendo-o, levaram-no para a casa do sumo sacerdote. Pedro os seguia à distância.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25920, 'nvi', 'Lucas', 'lc', 22, 55, 'Mas, quando acenderam um fogo no meio do pátio e se sentaram ao redor dele, Pedro sentou-se com eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25921, 'nvi', 'Lucas', 'lc', 22, 56, 'Uma criada o viu sentado ali à luz do fogo. Olhou fixamente para ele e disse: "Este homem estava com ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25922, 'nvi', 'Lucas', 'lc', 22, 57, 'Mas ele negou: "Mulher, não o conheço".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25923, 'nvi', 'Lucas', 'lc', 22, 58, 'Pouco depois, um homem o viu e disse: "Você também é um deles". "Homem, não sou! ", respondeu Pedro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25924, 'nvi', 'Lucas', 'lc', 22, 59, 'Cerca de uma hora mais tarde, outro afirmou: "Certamente este homem estava com ele, pois é galileu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25925, 'nvi', 'Lucas', 'lc', 22, 60, 'Pedro respondeu: "Homem, não sei do que você está falando! " Falava ele ainda, quando o galo cantou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25926, 'nvi', 'Lucas', 'lc', 22, 61, 'O Senhor voltou-se e olhou diretamente para Pedro. Então Pedro se lembrou da palavra que o Senhor lhe tinha dito: "Antes que o galo cante hoje, você me negará três vezes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25927, 'nvi', 'Lucas', 'lc', 22, 62, 'Saindo dali, chorou amargamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25928, 'nvi', 'Lucas', 'lc', 22, 63, 'Os homens que estavam detendo Jesus começaram a zombar dele e a bater nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25929, 'nvi', 'Lucas', 'lc', 22, 64, 'Cobriam seus olhos e perguntavam: "Profetize! Quem foi que lhe bateu? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25930, 'nvi', 'Lucas', 'lc', 22, 65, 'E lhe dirigiam muitas outras palavras de insulto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25931, 'nvi', 'Lucas', 'lc', 22, 66, 'Ao romper do dia, reuniu-se o Sinédrio, tanto os chefes dos sacerdotes quanto os mestres da lei, e Jesus foi levado perante eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25932, 'nvi', 'Lucas', 'lc', 22, 67, '"Se você é o Cristo, diga-nos", disseram eles. Jesus respondeu: "Se eu vos disser, não crereis em mim');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25933, 'nvi', 'Lucas', 'lc', 22, 68, 'e, se eu vos perguntar, não me respondereis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25934, 'nvi', 'Lucas', 'lc', 22, 69, 'Mas de agora em diante o Filho do homem estará assentado à direita do Deus Todo-poderoso".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25935, 'nvi', 'Lucas', 'lc', 22, 70, 'Perguntaram-lhe todos: "Então, você é o Filho de Deus? " "Vós estais dizendo que eu sou", respondeu ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25936, 'nvi', 'Lucas', 'lc', 22, 71, 'Eles disseram: "Por que precisamos de mais testemunhas? Acabamos de ouvir dos próprios lábios dele".');
