INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25937, 'nvi', 'Lucas', 'lc', 23, 1, 'Então toda a assembléia levantou-se e o levou a Pilatos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25938, 'nvi', 'Lucas', 'lc', 23, 2, 'E começaram a acusá-lo, dizendo: "Encontramos este homem subvertendo a nossa nação. Ele proíbe o pagamento de imposto a César e se declara ele próprio o Cristo, um rei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25939, 'nvi', 'Lucas', 'lc', 23, 3, 'Pilatos perguntou a Jesus: "Você é o rei dos judeus? " "Tu o dizes", respondeu Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25940, 'nvi', 'Lucas', 'lc', 23, 4, 'Então Pilatos disse aos chefes dos sacerdotes e à multidão: "Não encontro motivo para acusar este homem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25941, 'nvi', 'Lucas', 'lc', 23, 5, 'Mas eles insistiam: "Ele está subvertendo o povo em toda a Judéia com os seus ensinamentos. Começou na Galiléia e chegou até aqui".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25942, 'nvi', 'Lucas', 'lc', 23, 6, 'Ouvindo isso, Pilatos perguntou se Jesus era galileu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25943, 'nvi', 'Lucas', 'lc', 23, 7, 'Quando ficou sabendo que ele era da jurisdição de Herodes, enviou-o a Herodes, que também estava em Jerusalém naqueles dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25944, 'nvi', 'Lucas', 'lc', 23, 8, 'Quando Herodes viu Jesus, ficou muito alegre, porque havia muito tempo queria vê-lo. Pelo que ouvira falar dele, esperava vê-lo realizar algum milagre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25945, 'nvi', 'Lucas', 'lc', 23, 9, 'Interrogou-o com muitas perguntas, mas Jesus não lhe deu resposta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25946, 'nvi', 'Lucas', 'lc', 23, 10, 'Os chefes dos sacerdotes e os mestres da lei estavam ali, acusando-o com veemência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25947, 'nvi', 'Lucas', 'lc', 23, 11, 'Então Herodes e os seus soldados ridicularizaram-no e zombaram dele. Vestindo-o com um manto esplêndido, mandaram-no de volta a Pilatos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25948, 'nvi', 'Lucas', 'lc', 23, 12, 'Herodes e Pilatos, que até ali eram inimigos, naquele dia tornaram-se amigos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25949, 'nvi', 'Lucas', 'lc', 23, 13, 'Pilatos reuniu os chefes dos sacerdotes, as autoridades e o povo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25950, 'nvi', 'Lucas', 'lc', 23, 14, 'dizendo-lhes: "Vocês me trouxeram este homem como alguém que estava incitando o povo à rebelião. Eu o examinei na presença de vocês e não achei nenhuma base para as acusações que fazem contra ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25951, 'nvi', 'Lucas', 'lc', 23, 15, 'Nem Herodes, pois ele o mandou de volta para nós. Como podem ver, ele nada fez que mereça a morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25952, 'nvi', 'Lucas', 'lc', 23, 16, 'Portanto, eu o castigarei e depois o soltarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25953, 'nvi', 'Lucas', 'lc', 23, 17, 'Ele era obrigado a soltar-lhes um preso durante a festa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25954, 'nvi', 'Lucas', 'lc', 23, 18, 'A uma só voz eles gritaram: "Acaba com ele! Solta-nos Barrabás! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25955, 'nvi', 'Lucas', 'lc', 23, 19, '( Barrabás havia sido lançado na prisão por causa de uma insurreição na cidade e por assassinato. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25956, 'nvi', 'Lucas', 'lc', 23, 20, 'Desejando soltar a Jesus, Pilatos dirigiu-se a eles novamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25957, 'nvi', 'Lucas', 'lc', 23, 21, 'Mas eles continuaram gritando: "Crucifica-o! Crucifica-o! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25958, 'nvi', 'Lucas', 'lc', 23, 22, 'Pela terceira vez ele lhes falou: "Por quê? Que crime este homem cometeu? Não encontrei nele nada digno de morte. Vou mandar castigá-lo e depois o soltarei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25959, 'nvi', 'Lucas', 'lc', 23, 23, 'Eles, porém, pediam insistentemente, com fortes gritos, que ele fosse crucificado; e a gritaria prevaleceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25960, 'nvi', 'Lucas', 'lc', 23, 24, 'Então Pilatos decidiu fazer a vontade deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25961, 'nvi', 'Lucas', 'lc', 23, 25, 'Libertou o homem que havia sido lançado na prisão por insurreição e assassinato, aquele que eles haviam pedido, e entregou Jesus à vontade deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25962, 'nvi', 'Lucas', 'lc', 23, 26, 'Enquanto o levavam, agarraram Simão de Cirene, que estava chegando do campo, e lhe colocaram a cruz às costas, fazendo-o carregá-la atrás de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25963, 'nvi', 'Lucas', 'lc', 23, 27, 'Um grande número de pessoas o seguia, inclusive mulheres que lamentavam e choravam por ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25964, 'nvi', 'Lucas', 'lc', 23, 28, 'Jesus voltou-se e disse-lhes: "Filhas de Jerusalém, não chorem por mim; chorem por vocês mesmas e por seus filhos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25965, 'nvi', 'Lucas', 'lc', 23, 29, 'Pois chegará a hora em que vocês dirão: ‘Felizes as estéreis, os ventres que nunca geraram e os seios que nunca amamentaram! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25966, 'nvi', 'Lucas', 'lc', 23, 30, '"Então dirão às montanhas: ‘Caiam sobre nós! ’ e às colinas: ‘Cubram-nos! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25967, 'nvi', 'Lucas', 'lc', 23, 31, 'Pois, se fazem isto com a árvore verde, o que acontecerá quando ela estiver seca? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25968, 'nvi', 'Lucas', 'lc', 23, 32, 'Dois outros homens, ambos criminosos, também foram levados com ele, para serem executados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25969, 'nvi', 'Lucas', 'lc', 23, 33, 'Quando chegaram ao lugar chamado Caveira, ali o crucificaram com os criminosos, um à sua direita e o outro à sua esquerda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25970, 'nvi', 'Lucas', 'lc', 23, 34, 'Jesus disse: "Pai, perdoa-lhes, pois não sabem o que estão fazendo". Então eles dividiram as roupas dele, tirando sortes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25971, 'nvi', 'Lucas', 'lc', 23, 35, 'O povo ficou observando, e as autoridades o ridicularizavam. "Salvou os outros", diziam; "salve-se a si mesmo, se é o Cristo de Deus, o Escolhido".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25972, 'nvi', 'Lucas', 'lc', 23, 36, 'Os soldados, aproximando-se, também zombavam dele. Oferecendo-lhe vinagre,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25973, 'nvi', 'Lucas', 'lc', 23, 37, 'diziam: "Se você é o rei dos judeus, salve-se a si mesmo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25974, 'nvi', 'Lucas', 'lc', 23, 38, 'Havia uma inscrição acima dele, que dizia: ESTE É O REI DOS JUDEUS.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25975, 'nvi', 'Lucas', 'lc', 23, 39, 'Um dos criminosos que ali estavam dependurados lançava-lhe insultos: "Você não é o Cristo? Salve-se a si mesmo e a nós! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25976, 'nvi', 'Lucas', 'lc', 23, 40, 'Mas o outro criminoso o repreendeu, dizendo: "Você não teme a Deus, nem estando sob a mesma sentença?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25977, 'nvi', 'Lucas', 'lc', 23, 41, 'Nós estamos sendo punidos com justiça, porque estamos recebendo o que os nossos atos merecem. Mas este homem não cometeu nenhum mal".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25978, 'nvi', 'Lucas', 'lc', 23, 42, 'Então ele disse: "Jesus, lembra-te de mim quando entrares no teu Reino".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25979, 'nvi', 'Lucas', 'lc', 23, 43, 'Jesus lhe respondeu: "Eu lhe garanto: Hoje você estará comigo no paraíso".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25980, 'nvi', 'Lucas', 'lc', 23, 44, 'Já era quase meio dia, e trevas cobriram toda a terra até às três horas da tarde;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25981, 'nvi', 'Lucas', 'lc', 23, 45, 'o sol deixara de brilhar. E o véu do santuário rasgou-se ao meio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25982, 'nvi', 'Lucas', 'lc', 23, 46, 'Jesus bradou em alta voz: "Pai, nas tuas mãos entrego o meu espírito". Tendo dito isso, expirou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25983, 'nvi', 'Lucas', 'lc', 23, 47, 'O centurião, vendo o que havia acontecido, louvou a Deus, dizendo: "Certamente este homem era justo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25984, 'nvi', 'Lucas', 'lc', 23, 48, 'E todo o povo que se havia juntado para presenciar o que estava acontecendo, ao ver isso, começou a bater no peito e a afastar-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25985, 'nvi', 'Lucas', 'lc', 23, 49, 'Mas todos os que o conheciam, inclusive as mulheres que o haviam seguido desde a Galiléia, ficaram de longe, observando essas coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25986, 'nvi', 'Lucas', 'lc', 23, 50, 'Havia um homem chamado José, membro do Conselho, homem bom e justo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25987, 'nvi', 'Lucas', 'lc', 23, 51, 'que não tinha consentido na decisão e no procedimento dos outros. Ele era da cidade de Arimatéia, na Judéia, e esperava o Reino de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25988, 'nvi', 'Lucas', 'lc', 23, 52, 'Dirigindo-se a Pilatos, pediu o corpo de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25989, 'nvi', 'Lucas', 'lc', 23, 53, 'Então, desceu-o, envolveu-o num lençol de linho e o colocou num sepulcro cavado na rocha, no qual ninguém ainda fora colocado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25990, 'nvi', 'Lucas', 'lc', 23, 54, 'Era o Dia da Preparação, e estava para começar o sábado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25991, 'nvi', 'Lucas', 'lc', 23, 55, 'As mulheres que haviam acompanhado Jesus desde a Galiléia, seguiram José e viram o sepulcro, e como o corpo de Jesus fora colocado nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25992, 'nvi', 'Lucas', 'lc', 23, 56, 'Então, foram para casa e prepararam perfumes e especiarias aromáticas. E descansaram no sábado, em obediência ao mandamento.');
