INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25993, 'nvi', 'Lucas', 'lc', 24, 1, 'No primeiro dia da semana, de manhã bem cedo, as mulheres tomaram as especiarias aromáticas que haviam preparado e foram ao sepulcro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25994, 'nvi', 'Lucas', 'lc', 24, 2, 'Encontraram removida a pedra do sepulcro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25995, 'nvi', 'Lucas', 'lc', 24, 3, 'mas, quando entraram, não encontraram o corpo do Senhor Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25996, 'nvi', 'Lucas', 'lc', 24, 4, 'Ficaram perplexas, sem saber o que fazer. De repente dois homens com roupas que brilhavam como a luz do sol colocaram-se ao lado delas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25997, 'nvi', 'Lucas', 'lc', 24, 5, 'Amedrontadas, as mulheres baixaram o rosto para o chão, e os homens lhes disseram: "Por que vocês estão procurando entre os mortos aquele que vive?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25998, 'nvi', 'Lucas', 'lc', 24, 6, 'Ele não está aqui! Ressuscitou! Lembrem-se do que ele lhes disse, quando ainda estava com vocês na Galiléia:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25999, 'nvi', 'Lucas', 'lc', 24, 7, '‘É necessário que o Filho do homem seja entregue nas mãos de homens pecadores, seja crucificado e ressuscite no terceiro dia’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26000, 'nvi', 'Lucas', 'lc', 24, 8, 'Então se lembraram das suas palavras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26001, 'nvi', 'Lucas', 'lc', 24, 9, 'Quando voltaram do sepulcro, elas contaram todas estas coisas aos Onze e a todos os outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26002, 'nvi', 'Lucas', 'lc', 24, 10, 'As que contaram estas coisas aos apóstolos foram Maria Madalena, Joana e Maria, mãe de Tiago, e as outras que estavam com elas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26003, 'nvi', 'Lucas', 'lc', 24, 11, 'Mas eles não acreditaram nas mulheres; as palavras delas lhes pareciam loucura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26004, 'nvi', 'Lucas', 'lc', 24, 12, 'Pedro, todavia, levantou-se e correu ao sepulcro. Abaixando-se, viu as faixas de linho e mais nada; afastou-se, e voltou admirado com o que acontecera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26005, 'nvi', 'Lucas', 'lc', 24, 13, 'Naquele mesmo dia, dois deles estavam indo para um povoado chamado Emaús, a onze quilômetros de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26006, 'nvi', 'Lucas', 'lc', 24, 14, 'No caminho, conversavam a respeito de tudo o que havia acontecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26007, 'nvi', 'Lucas', 'lc', 24, 15, 'Enquanto conversavam e discutiam, o próprio Jesus se aproximou e começou a caminhar com eles;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26008, 'nvi', 'Lucas', 'lc', 24, 16, 'mas os olhos deles foram impedidos de reconhecê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26009, 'nvi', 'Lucas', 'lc', 24, 17, 'Ele lhes perguntou: "Sobre o que vocês estão discutindo enquanto caminham? " Eles pararam, com os rostos entristecidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26010, 'nvi', 'Lucas', 'lc', 24, 18, 'Um deles, chamado Cleopas, perguntou-lhe: "Você é o único visitante em Jerusalém que não sabe das coisas que ali aconteceram nestes dias? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26011, 'nvi', 'Lucas', 'lc', 24, 19, '"Que coisas? ", perguntou ele. "O que aconteceu com Jesus de Nazaré", responderam eles. "Ele era um profeta, poderoso em palavras e em obras diante de Deus e de todo o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26012, 'nvi', 'Lucas', 'lc', 24, 20, 'Os chefes dos sacerdotes e as nossas autoridades o entregaram para ser condenado à morte, e o crucificaram;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26013, 'nvi', 'Lucas', 'lc', 24, 21, 'e nós esperávamos que era ele que ia trazer a redenção a Israel. E hoje é o terceiro dia desde que tudo isso aconteceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26014, 'nvi', 'Lucas', 'lc', 24, 22, 'Algumas das mulheres entre nós nos deram um susto hoje. Foram de manhã bem cedo ao sepulcro');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26015, 'nvi', 'Lucas', 'lc', 24, 23, 'e não acharam o corpo dele. Voltaram e nos contaram que tinham tido uma visão de anjos, que disseram que ele está vivo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26016, 'nvi', 'Lucas', 'lc', 24, 24, 'Alguns dos nossos companheiros foram ao sepulcro e encontraram tudo exatamente como as mulheres tinham dito, mas não o viram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26017, 'nvi', 'Lucas', 'lc', 24, 25, 'Ele lhes disse: "Como vocês custam a entender e como demoram a crer em tudo o que os profetas falaram!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26018, 'nvi', 'Lucas', 'lc', 24, 26, 'Não devia o Cristo sofrer estas coisas, para entrar na sua glória? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26019, 'nvi', 'Lucas', 'lc', 24, 27, 'E começando por Moisés e todos os profetas, explicou-lhes o que constava a respeito dele em todas as Escrituras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26020, 'nvi', 'Lucas', 'lc', 24, 28, 'Ao se aproximarem do povoado para o qual estavam indo, Jesus fez como quem ia mais adiante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26021, 'nvi', 'Lucas', 'lc', 24, 29, 'Mas eles insistiram muito com ele: "Fique conosco, pois a noite já vem; o dia já está quase findando". Então, ele entrou para ficar com eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26022, 'nvi', 'Lucas', 'lc', 24, 30, 'Quando estava à mesa com eles, tomou o pão, deu graças, partiu-o e o deu a eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26023, 'nvi', 'Lucas', 'lc', 24, 31, 'Então os olhos deles foram abertos e o reconheceram, e ele desapareceu da vista deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26024, 'nvi', 'Lucas', 'lc', 24, 32, 'Perguntaram-se um ao outro: "Não estavam ardendo os nossos corações dentro de nós, enquanto ele nos falava no caminho e nos expunha as Escrituras? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26025, 'nvi', 'Lucas', 'lc', 24, 33, 'Levantaram-se e voltaram imediatamente para Jesuralém. Ali encontraram os Onze e os que estavam com eles reunidos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26026, 'nvi', 'Lucas', 'lc', 24, 34, 'que diziam: "É verdade! O Senhor ressuscitou e apareceu a Simão! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26027, 'nvi', 'Lucas', 'lc', 24, 35, 'Então os dois contaram o que tinha acontecido no caminho, e como Jesus fora reconhecido por eles quando partia o pão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26028, 'nvi', 'Lucas', 'lc', 24, 36, 'Enquanto falavam sobre isso, o próprio Jesus apresentou-se entre eles e lhes disse: "Paz seja com vocês! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26029, 'nvi', 'Lucas', 'lc', 24, 37, 'Eles ficaram assustados e com medo, pensando que estavam vendo um espírito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26030, 'nvi', 'Lucas', 'lc', 24, 38, 'Ele lhes disse: "Por que vocês estão perturbados e por que se levantam dúvidas em seus corações?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26031, 'nvi', 'Lucas', 'lc', 24, 39, 'Vejam as minhas mãos e os meus pés. Sou eu mesmo! Toquem-me e vejam; um espírito não tem carne nem ossos, como vocês estão vendo que eu tenho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26032, 'nvi', 'Lucas', 'lc', 24, 40, 'Tendo dito isso, mostrou-lhes as mãos e os pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26033, 'nvi', 'Lucas', 'lc', 24, 41, 'E por não crerem ainda, tão cheios estavam de alegria e de espanto, ele lhes perguntou: "Vocês têm aqui algo para comer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26034, 'nvi', 'Lucas', 'lc', 24, 42, 'Deram-lhe um pedaço de peixe assado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26035, 'nvi', 'Lucas', 'lc', 24, 43, 'e ele o comeu na presença deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26036, 'nvi', 'Lucas', 'lc', 24, 44, 'E disse-lhes: "Foi isso que eu lhes falei enquanto ainda estava com vocês: Era necessário que se cumprisse tudo o que a meu respeito estava escrito na Lei de Moisés, nos Profetas e nos Salmos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26037, 'nvi', 'Lucas', 'lc', 24, 45, 'Então lhes abriu o entendimento, para que pudessem compreender as Escrituras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26038, 'nvi', 'Lucas', 'lc', 24, 46, 'E lhes disse: "Está escrito que o Cristo haveria de sofrer e ressuscitar dos mortos no terceiro dia,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26039, 'nvi', 'Lucas', 'lc', 24, 47, 'e que em seu nome seria pregado o arrependimento para perdão de pecados a todas as nações, começando por Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26040, 'nvi', 'Lucas', 'lc', 24, 48, 'Vocês são testemunhas destas coisas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26041, 'nvi', 'Lucas', 'lc', 24, 49, 'Eu lhes envio a promessa de meu Pai; mas fiquem na cidade até serem revestidos do poder do alto".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26042, 'nvi', 'Lucas', 'lc', 24, 50, 'Tendo-os levado até as proximidades de Betânia, Jesus levantou as mãos e os abençoou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26043, 'nvi', 'Lucas', 'lc', 24, 51, 'Estando ainda a abençoá-los, ele os deixou e foi elevado ao céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26044, 'nvi', 'Lucas', 'lc', 24, 52, 'Então eles o adoraram e voltaram para Jerusalém com grande alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (26045, 'nvi', 'Lucas', 'lc', 24, 53, 'E permaneciam constantemente no templo, louvando a Deus.');
