INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25027, 'nvi', 'Lucas', 'lc', 3, 1, 'No décimo quinto ano do reinado de Tibério César, quando Pôncio Pilatos era governador da Judéia; Herodes, tetrarca da Galiléia; seu irmão Filipe, tetrarca da Ituréia e Traconites; e Lisânias, tetrarca de Abilene;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25028, 'nvi', 'Lucas', 'lc', 3, 2, 'Anás e Caifás exerciam o sumo sacerdócio. Foi nesse ano que veio a palavra do Senhor a João, filho de Zacarias, no deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25029, 'nvi', 'Lucas', 'lc', 3, 3, 'Ele percorreu toda a região próxima ao Jordão, pregando um batismo de arrependimento para o perdão dos pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25030, 'nvi', 'Lucas', 'lc', 3, 4, 'Como está escrito no livro das palavras de Isaías, o profeta: "Voz do que clama no deserto: ‘Preparem o caminho para o Senhor, façam veredas retas para ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25031, 'nvi', 'Lucas', 'lc', 3, 5, 'Todo vale será aterrado e todas as montanhas e colinas, niveladas. As estradas tortuosas serão endireitadas e os caminhos acidentados, aplanados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25032, 'nvi', 'Lucas', 'lc', 3, 6, 'E toda a humanidade verá a salvação de Deus’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25033, 'nvi', 'Lucas', 'lc', 3, 7, 'João dizia às multidões que saíam para serem batizadas por ele: "Raça de víboras! Quem lhes deu a idéia de fugir da ira que se aproxima?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25034, 'nvi', 'Lucas', 'lc', 3, 8, 'Dêem frutos que mostrem o arrependimento. E não comecem a dizer a si mesmos: ‘Abraão é nosso pai’. Pois eu lhes digo que destas pedras Deus pode fazer surgir filhos a Abraão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25035, 'nvi', 'Lucas', 'lc', 3, 9, 'O machado já está posto à raiz das árvores, e toda árvore que não der bom fruto será cortada e lançada ao fogo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25036, 'nvi', 'Lucas', 'lc', 3, 10, '"O que devemos fazer então? ", perguntavam as multidões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25037, 'nvi', 'Lucas', 'lc', 3, 11, 'João respondia: "Quem tem duas túnicas reparta-as com quem não tem nenhuma; e quem tem comida faça o mesmo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25038, 'nvi', 'Lucas', 'lc', 3, 12, 'Alguns publicanos também vieram para serem batizados. Eles perguntaram: "Mestre, o que devemos fazer? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25039, 'nvi', 'Lucas', 'lc', 3, 13, 'Ele respondeu: "Não cobrem nada além do que lhes foi estipulado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25040, 'nvi', 'Lucas', 'lc', 3, 14, 'Então alguns soldados lhe perguntaram: "E nós, o que devemos fazer? " Ele respondeu: "Não pratiquem extorsão nem acusem ninguém falsamente; contentem-se com o seu salário".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25041, 'nvi', 'Lucas', 'lc', 3, 15, 'O povo estava em grande expectativa, questionando em seus corações se acaso João não seria o Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25042, 'nvi', 'Lucas', 'lc', 3, 16, 'João respondeu a todos: "Eu os batizo com água. Mas virá alguém mais poderoso do que eu, tanto que não sou digno nem de curvar-me e desamarrar as correias das suas sandálias. Ele os batizará com o Espírito Santo e com fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25043, 'nvi', 'Lucas', 'lc', 3, 17, 'Ele traz a pá em sua mão, a fim de limpar sua eira e juntar o trigo em seu celeiro; mas queimará a palha com fogo que nunca se apaga".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25044, 'nvi', 'Lucas', 'lc', 3, 18, 'E com muitas outras palavras João exortava o povo e lhe pregava as boas novas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25045, 'nvi', 'Lucas', 'lc', 3, 19, 'Todavia, quando João repreendeu Herodes, o tetrarca, por causa de Herodias, mulher do próprio irmão de Herodes, e por todas as outras coisas más que ele tinha feito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25046, 'nvi', 'Lucas', 'lc', 3, 20, 'Herodes acrescentou a todas elas a de colocar João na prisão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25047, 'nvi', 'Lucas', 'lc', 3, 21, 'Quando todo o povo estava sendo batizado, também Jesus o foi. E, enquanto ele estava orando, o céu se abriu');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25048, 'nvi', 'Lucas', 'lc', 3, 22, 'e o Espírito Santo desceu sobre ele em forma corpórea, como pomba. Então veio do céu uma voz: "Tu és o meu Filho amado; em ti me agrado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25049, 'nvi', 'Lucas', 'lc', 3, 23, 'Jesus tinha cerca de trinta anos de idade quando começou seu ministério. Ele era, como se pensava, filho de José, filho de Eli,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25050, 'nvi', 'Lucas', 'lc', 3, 24, 'filho de Matate, filho de Levi, filho de Melqui, filho de Janai, filho de José,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25051, 'nvi', 'Lucas', 'lc', 3, 25, 'filho de Matatias, filho de Amós, filho de Naum, filho de Esli, filho de Nagai,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25052, 'nvi', 'Lucas', 'lc', 3, 26, 'filho de Maate, filho de Matatias, filho de Semei, filho de Joseque, filho de Jodá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25053, 'nvi', 'Lucas', 'lc', 3, 27, 'filho de Joanã, filho de Ressa, filho de Zorobabel, filho de Salatiel, filho de Neri,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25054, 'nvi', 'Lucas', 'lc', 3, 28, 'filho de Melqui, filho de Adi, filho de Cosã, filho de Elmadã, filho de Er,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25055, 'nvi', 'Lucas', 'lc', 3, 29, 'filho de Josué, filho de Eliézer, filho de Jorim, filho de Matate, filho de Levi,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25056, 'nvi', 'Lucas', 'lc', 3, 30, 'filho de Simeão, filho de Judá, filho de José, filho de Jonã, filho de Eliaquim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25057, 'nvi', 'Lucas', 'lc', 3, 31, 'filho de Meleá, filho de Mená, filho de Matatá, filho de Natã, filho de Davi,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25058, 'nvi', 'Lucas', 'lc', 3, 32, 'filho de Jessé, filho de Obede, filho de Boaz, filho de Salmom, filho de Naassom,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25059, 'nvi', 'Lucas', 'lc', 3, 33, 'filho de Aminadabe, filho de Ram, filho de Esrom, filho de Perez, filho de Judá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25060, 'nvi', 'Lucas', 'lc', 3, 34, 'filho de Jacó, filho de Isaque, filho de Abraão, filho de Terá, filho de Naor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25061, 'nvi', 'Lucas', 'lc', 3, 35, 'filho de Serugue, filho de Ragaú, filho de Faleque, filho de Éber, filho de Salá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25062, 'nvi', 'Lucas', 'lc', 3, 36, 'filho de Cainã, filho de Arfaxade, filho de Sem, filho de Noé, filho de Lameque,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25063, 'nvi', 'Lucas', 'lc', 3, 37, 'filho de Matusalém, filho de Enoque, filho de Jarede, filho de Maalaleel, filho de Cainã,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25064, 'nvi', 'Lucas', 'lc', 3, 38, 'filho de Enos, filho de Sete, filho de Adão, filho de Deus.');
