INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25065, 'nvi', 'Lucas', 'lc', 4, 1, 'Jesus, cheio do Espírito Santo, voltou do Jordão e foi levado pelo Espírito ao deserto,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25066, 'nvi', 'Lucas', 'lc', 4, 2, 'onde, durante quarenta dias, foi tentado pelo diabo. Não comeu nada durante esses dias e, ao fim deles, teve fome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25067, 'nvi', 'Lucas', 'lc', 4, 3, 'O diabo lhe disse: "Se você é o Filho de Deus, mande a esta pedra que se transforme em pão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25068, 'nvi', 'Lucas', 'lc', 4, 4, 'Jesus respondeu: "Está escrito: ‘Nem só de pão viverá o homem’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25069, 'nvi', 'Lucas', 'lc', 4, 5, 'O diabo o levou a um lugar alto e mostrou-lhe num relance todos os reinos do mundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25070, 'nvi', 'Lucas', 'lc', 4, 6, 'E lhe disse: "Eu lhe darei toda a autoridade sobre eles e todo o seu esplendor, porque me foram dados e posso dá-los a quem eu quiser.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25071, 'nvi', 'Lucas', 'lc', 4, 7, 'Então, se você me adorar, tudo será seu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25072, 'nvi', 'Lucas', 'lc', 4, 8, 'Jesus respondeu: "Está escrito: ‘Adore o Senhor, o seu Deus e só a ele preste culto’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25073, 'nvi', 'Lucas', 'lc', 4, 9, 'O diabo o levou a Jerusalém, colocou-o na parte mais alta do templo e lhe disse: "Se você é o Filho de Deus, jogue-se daqui para baixo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25074, 'nvi', 'Lucas', 'lc', 4, 10, 'Pois está escrito: ‘Ele dará ordens a seus anjos a seu respeito, para lhe guardarem;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25075, 'nvi', 'Lucas', 'lc', 4, 11, 'com as mãos eles os segurarão, para que você não tropece em alguma pedra’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25076, 'nvi', 'Lucas', 'lc', 4, 12, 'Jesus respondeu: "Dito está: ‘Não ponha à prova o Senhor, o seu Deus’".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25077, 'nvi', 'Lucas', 'lc', 4, 13, 'Tendo terminado todas essas tentações, o diabo o deixou até ocasião oportuna.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25078, 'nvi', 'Lucas', 'lc', 4, 14, 'Jesus voltou para a Galiléia no poder do Espírito, e por toda aquela região se espalhou a sua fama.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25079, 'nvi', 'Lucas', 'lc', 4, 15, 'Ensinava nas sinagogas, e todos o elogiavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25080, 'nvi', 'Lucas', 'lc', 4, 16, 'Ele foi a Nazaré, onde havia sido criado, e no dia de sábado entrou na sinagoga, como era seu costume. E levantou-se para ler.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25081, 'nvi', 'Lucas', 'lc', 4, 17, 'Foi-lhe entregue o livro do profeta Isaías. Abriu-o e encontrou o lugar onde está escrito:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25082, 'nvi', 'Lucas', 'lc', 4, 18, '"O Espírito do Senhor está sobre mim, porque ele me ungiu para pregar boas novas aos pobres. Ele me enviou para proclamar liberdade aos presos e recuperação da vista aos cegos, para libertar os oprimidos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25083, 'nvi', 'Lucas', 'lc', 4, 19, 'e proclamar o ano da graça do Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25084, 'nvi', 'Lucas', 'lc', 4, 20, 'Então ele fechou o livro, devolveu-o ao assistente e assentou-se. Na sinagoga todos tinham os olhos fitos nele;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25085, 'nvi', 'Lucas', 'lc', 4, 21, 'e ele começou a dizer-lhes: "Hoje se cumpriu a Escritura que vocês acabaram de ouvir".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25086, 'nvi', 'Lucas', 'lc', 4, 22, 'Todos falavam bem dele, e estavam admirados com as palavras de graça que saíam de seus lábios. Mas perguntavam: "Não é este o filho de José? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25087, 'nvi', 'Lucas', 'lc', 4, 23, 'Jesus lhes disse: "É claro que vocês me citarão este provérbio: ‘Médico, cura-te a ti mesmo! ’ Faze aqui em tua terra o que ouvimos que fizeste em Cafarnaum".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25088, 'nvi', 'Lucas', 'lc', 4, 24, 'Continuou ele: "Digo-lhes a verdade: Nenhum profeta é aceito em sua terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25089, 'nvi', 'Lucas', 'lc', 4, 25, 'Asseguro-lhes que havia muitas viúvas em Israel no tempo de Elias, quando o céu foi fechado por três anos e meio, e houve uma grande fome em toda a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25090, 'nvi', 'Lucas', 'lc', 4, 26, 'Contudo, Elias não foi enviado a nenhuma delas, senão a uma viúva de Sarepta, na região de Sidom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25091, 'nvi', 'Lucas', 'lc', 4, 27, 'Também havia muitos leprosos em Israel no tempo de Eliseu, o profeta; todavia, nenhum deles foi purificado: somente Naamã, o sírio".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25092, 'nvi', 'Lucas', 'lc', 4, 28, 'Todos os que estavam na sinagoga ficaram furiosos quando ouviram isso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25093, 'nvi', 'Lucas', 'lc', 4, 29, 'Levantaram-se, expulsaram-no da cidade e o levaram até ao topo da colina sobre a qual fora construída a cidade, a fim de atirá-lo precipício abaixo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25094, 'nvi', 'Lucas', 'lc', 4, 30, 'Mas Jesus passou por entre eles e retirou-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25095, 'nvi', 'Lucas', 'lc', 4, 31, 'Então ele desceu a Cafarnaum, cidade da Galiléia, e, no sábado, começou a ensinar o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25096, 'nvi', 'Lucas', 'lc', 4, 32, 'Todos ficavam maravilhados com o seu ensino, porque falava com autoridade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25097, 'nvi', 'Lucas', 'lc', 4, 33, 'Na sinagoga havia um homem possesso de um demônio, de um espírito imundo. Ele gritou com toda a força:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25098, 'nvi', 'Lucas', 'lc', 4, 34, '"Ah! que queres conosco, Jesus de Nazaré? Vieste para nos destruir? Sei quem tu és: o Santo de Deus! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25099, 'nvi', 'Lucas', 'lc', 4, 35, 'Jesus o repreendeu, e disse: "Cale-se e saia dele! " Então o demônio jogou o homem no chão diante de todos, e saiu dele sem o ferir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25100, 'nvi', 'Lucas', 'lc', 4, 36, 'Todos ficaram admirados, e diziam uns aos outros: "Que palavra é esta? Até aos espíritos imundos ele dá ordens com autoridade e poder, e eles saem! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25101, 'nvi', 'Lucas', 'lc', 4, 37, 'E a sua fama se espalhava por toda a região circunvizinha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25102, 'nvi', 'Lucas', 'lc', 4, 38, 'Jesus saiu da sinagoga e foi à casa de Simão. A sogra de Simão estava com febre alta, e pediram a Jesus que fizesse algo por ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25103, 'nvi', 'Lucas', 'lc', 4, 39, 'Estando ele em pé junto dela, inclinou-se e repreendeu a febre, que a deixou. Ela se levantou imediatamente e passou a servi-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25104, 'nvi', 'Lucas', 'lc', 4, 40, 'Ao pôr-do-sol, o povo trouxe a Jesus todos os que tinham vários tipos de doenças; e ele os curou, impondo as mãos sobre cada um deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25105, 'nvi', 'Lucas', 'lc', 4, 41, 'Além disso, de muitas pessoas saíam demônios gritando: "Tu és o Filho de Deus! " Ele, porém, os repreendia e não permitia que falassem, porque sabiam que ele era o Cristo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25106, 'nvi', 'Lucas', 'lc', 4, 42, 'Ao romper do dia, Jesus foi para um lugar solitário. As multidões o procuravam, e, quando chegaram até onde ele estava, insistiam que não as deixasse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25107, 'nvi', 'Lucas', 'lc', 4, 43, 'Mas ele disse: "É necessário que eu pregue as boas novas do Reino de Deus noutras cidades também, porque para isso fui enviado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25108, 'nvi', 'Lucas', 'lc', 4, 44, 'E continuava pregando nas sinagogas da Judéia.');
