INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25109, 'nvi', 'Lucas', 'lc', 5, 1, 'Certo dia Jesus estava perto do lago de Genesaré, e uma multidão o comprimia de todos os lados para ouvir a palavra de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25110, 'nvi', 'Lucas', 'lc', 5, 2, 'Viu à beira do lago dois barcos, deixados ali pelos pescadores, que estavam lavando as suas redes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25111, 'nvi', 'Lucas', 'lc', 5, 3, 'Entrou num dos barcos, o que pertencia a Simão, e pediu-lhe que o afastasse um pouco da praia. Então sentou-se, e do barco ensinava o povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25112, 'nvi', 'Lucas', 'lc', 5, 4, 'Tendo acabado de falar, disse a Simão: "Vá para onde as águas são mais fundas", e a todos: "Lancem as redes para a pesca".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25113, 'nvi', 'Lucas', 'lc', 5, 5, 'Simão respondeu: "Mestre, esforçamo-nos a noite inteira e não pegamos nada. Mas, porque és tu quem está dizendo isto, vou lançar as redes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25114, 'nvi', 'Lucas', 'lc', 5, 6, 'Quando o fizeram, pegaram tal quantidade de peixe que as redes começaram a rasgar-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25115, 'nvi', 'Lucas', 'lc', 5, 7, 'Então fizeram sinais a seus companheiros no outro barco, para que viessem ajudá-lo; e eles vieram e encheram ambos os barcos, a ponto de quase começarem a afundar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25116, 'nvi', 'Lucas', 'lc', 5, 8, 'Quando Simão Pedro viu isso, prostrou-se aos pés de Jesus e disse: "Afasta-te de mim, Senhor, porque sou um homem pecador! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25117, 'nvi', 'Lucas', 'lc', 5, 9, 'Pois ele e todos os seus companheiros estavam perplexos com a pesca que haviam feito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25118, 'nvi', 'Lucas', 'lc', 5, 10, 'como também Tiago e João, os filhos de Zebedeu, sócios de Simão. Então Jesus disse a Simão: "Não tenha medo; de agora em diante você será pescador de homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25119, 'nvi', 'Lucas', 'lc', 5, 11, 'Eles então arrastaram seus barcos para a praia, deixaram tudo e o seguiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25120, 'nvi', 'Lucas', 'lc', 5, 12, 'Estando Jesus numa das cidades, passou um homem coberto de lepra. Quando viu a Jesus, prostrou-se com o rosto em terra e rogou-lhe: "Se quiseres, podes purificar-me".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25121, 'nvi', 'Lucas', 'lc', 5, 13, 'Jesus estendeu a mão e tocou nele, dizendo: "Quero. Seja purificado! " E imediatamente a lepra o deixou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25122, 'nvi', 'Lucas', 'lc', 5, 14, 'Então Jesus lhe ordenou: "Não conte isso a ninguém; mas vá mostrar-se ao sacerdote e ofereça pela sua purificação os sacrifícios que Moisés ordenou, para que sirva de testemunho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25123, 'nvi', 'Lucas', 'lc', 5, 15, 'Todavia, as notícias a respeito dele se espalhavam ainda mais, de forma que multidões vinham para ouvi-lo e para serem curadas de suas doenças.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25124, 'nvi', 'Lucas', 'lc', 5, 16, 'Mas Jesus retirava-se para lugares solitários, e orava.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25125, 'nvi', 'Lucas', 'lc', 5, 17, 'Certo dia, quando ele ensinava, estavam sentados ali fariseus e mestres da lei, procedentes de todos os povoados da Galiléia, da Judéia e de Jerusalém. E o poder do Senhor estava com ele para curar os doentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25126, 'nvi', 'Lucas', 'lc', 5, 18, 'Vieram alguns homens trazendo um paralítico numa maca e tentaram fazê-lo entrar na casa, para colocá-lo diante de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25127, 'nvi', 'Lucas', 'lc', 5, 19, 'Não conseguindo fazer isso, por causa da multidão, subiram ao terraço e o baixaram em sua maca, através de uma abertura, até o meio da multidão, bem em frente de Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25128, 'nvi', 'Lucas', 'lc', 5, 20, 'Vendo a fé que eles tinham, Jesus disse: "Homem, os seus pecados estão perdoados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25129, 'nvi', 'Lucas', 'lc', 5, 21, 'Os fariseus e os mestres da lei começaram a pensar: "Quem é esse que blasfema? Quem pode perdoar pecados, a não ser somente Deus? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25130, 'nvi', 'Lucas', 'lc', 5, 22, 'Jesus, sabendo o que eles estavam pensando, perguntou: "Por que vocês estão pensando assim?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25131, 'nvi', 'Lucas', 'lc', 5, 23, 'Que é mais fácil dizer: ‘Os seus pecados estão perdoados’, ou: ‘Levante-se e ande’?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25132, 'nvi', 'Lucas', 'lc', 5, 24, 'Mas, para que vocês saibam que o Filho do homem tem na terra autoridade para perdoar pecados" — disse ao paralítico — "eu lhe digo: levante-se, pegue a sua maca e vá para casa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25133, 'nvi', 'Lucas', 'lc', 5, 25, 'Imediatamente ele se levantou na frente deles, pegou a maca em que estivera deitado e foi para casa louvando a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25134, 'nvi', 'Lucas', 'lc', 5, 26, 'Todos ficaram atônitos e glorificavam a Deus, e, cheios de temor, diziam: "Hoje vimos coisas extraordinárias! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25135, 'nvi', 'Lucas', 'lc', 5, 27, 'Depois disso, Jesus saiu e viu um publicano chamado Levi, sentado na coletoria, e disse-lhe: "Siga-me".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25136, 'nvi', 'Lucas', 'lc', 5, 28, 'Levi levantou-se, deixou tudo e o seguiu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25137, 'nvi', 'Lucas', 'lc', 5, 29, 'Então Levi ofereceu um grande banquete a Jesus em sua casa. Havia muita gente comendo com eles: publicanos e outras pessoas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25138, 'nvi', 'Lucas', 'lc', 5, 30, 'Mas os fariseus e aqueles mestres da lei que eram da mesma facção queixaram-se aos discípulos de Jesus: "Por que vocês comem e bebem com publicanos e ‘pecadores’? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25139, 'nvi', 'Lucas', 'lc', 5, 31, 'Jesus lhes respondeu: "Não são os que têm saúde que precisam de médico, mas sim os doentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25140, 'nvi', 'Lucas', 'lc', 5, 32, 'Eu não vim chamar justos, mas pecadores ao arrependimento".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25141, 'nvi', 'Lucas', 'lc', 5, 33, 'E eles lhe disseram: "Os discípulos de João jejuam e oram freqüentemente, bem como os discípulos dos fariseus; mas os teus vivem comendo e bebendo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25142, 'nvi', 'Lucas', 'lc', 5, 34, 'Jesus respondeu: "Podem vocês fazer os convidados do noivo jejuar enquanto o noivo está com eles?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25143, 'nvi', 'Lucas', 'lc', 5, 35, 'Mas virão dias quando o noivo lhes será tirado; naqueles dias jejuarão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25144, 'nvi', 'Lucas', 'lc', 5, 36, 'Então lhes contou esta parábola: "Ninguém tira remendo de roupa nova e o costura em roupa velha; se o fizer, estragará a roupa nova, além do que o remendo da nova não se ajustará à velha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25145, 'nvi', 'Lucas', 'lc', 5, 37, 'E ninguém põe vinho novo em vasilhas de couro velhas; se o fizer, o vinho novo rebentará as vasilhas, se derramará, e as vasilhas se estragarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25146, 'nvi', 'Lucas', 'lc', 5, 38, 'Pelo contrário, vinho novo deve ser posto em vasilhas de couro novas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25147, 'nvi', 'Lucas', 'lc', 5, 39, 'E ninguém, depois de beber o vinho velho, prefere o novo, pois diz: ‘O vinho velho é melhor! ’ "');
