INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25197, 'nvi', 'Lucas', 'lc', 7, 1, 'Tendo terminado de dizer tudo isso ao povo, Jesus entrou em Cafarnaum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25198, 'nvi', 'Lucas', 'lc', 7, 2, 'Ali estava doente, quase à morte, o servo de um centurião, a quem seu senhor estimava muito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25199, 'nvi', 'Lucas', 'lc', 7, 3, 'Ele ouviu falar de Jesus e enviou-lhe alguns líderes religiosos dos judeus, pedindo-lhe que fosse curar o seu servo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25200, 'nvi', 'Lucas', 'lc', 7, 4, 'Chegando-se a Jesus, suplicaram-lhe com insistência: "Este homem merece que lhe faças isso,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25201, 'nvi', 'Lucas', 'lc', 7, 5, 'porque ama a nossa nação e construiu a nossa sinagoga".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25202, 'nvi', 'Lucas', 'lc', 7, 6, 'Jesus foi com eles. Já estava perto da casa quando o centurião mandou amigos dizerem a Jesus: "Senhor, não te incomodes, pois não mereço receber-te debaixo do meu teto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25203, 'nvi', 'Lucas', 'lc', 7, 7, 'Por isso, nem me considerei digno de ir ao teu encontro. Mas dize uma palavra, e o meu servo será curado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25204, 'nvi', 'Lucas', 'lc', 7, 8, 'Pois eu também sou homem sujeito a autoridade, e com soldados sob o meu comando. Digo a um: ‘Vá’, e ele vai; e a outro: ‘Venha’, e ele vem. Digo a meu servo: ‘Faça isto’, e ele faz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25205, 'nvi', 'Lucas', 'lc', 7, 9, 'Ao ouvir isso, Jesus admirou-se dele e, voltando-se para a multidão que o seguia, disse: "Eu lhes digo que nem em Israel encontrei tamanha fé".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25206, 'nvi', 'Lucas', 'lc', 7, 10, 'Então os homens que haviam sido enviados voltaram para casa e encontraram o servo restabelecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25207, 'nvi', 'Lucas', 'lc', 7, 11, 'Logo depois, Jesus foi a uma cidade chamada Naim, e com ele iam os seus discípulos e uma grande multidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25208, 'nvi', 'Lucas', 'lc', 7, 12, 'Ao se aproximar da porta da cidade, estava saindo o enterro do filho único de uma viúva; e uma grande multidão da cidade estava com ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25209, 'nvi', 'Lucas', 'lc', 7, 13, 'Ao vê-la, o Senhor se compadeceu dela e disse: "Não chore".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25210, 'nvi', 'Lucas', 'lc', 7, 14, 'Depois, aproximou-se e tocou no caixão, e os que o carregavam pararam. Jesus disse: "Jovem, eu lhe digo, levante-se! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25211, 'nvi', 'Lucas', 'lc', 7, 15, 'Ele se levantou, sentou-se e começou a conversar, e Jesus o entregou à sua mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25212, 'nvi', 'Lucas', 'lc', 7, 16, 'Todos ficaram cheios de temor e louvavam a Deus. "Um grande profeta se levantou entre nós", diziam eles. "Deus interveio em favor do seu povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25213, 'nvi', 'Lucas', 'lc', 7, 17, 'Essas notícias sobre Jesus espalharam-se por toda a Judéia e regiões circunvizinhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25214, 'nvi', 'Lucas', 'lc', 7, 18, 'Os discípulos de João contaram-lhe todas essas coisas. Chamando dois deles,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25215, 'nvi', 'Lucas', 'lc', 7, 19, 'enviou-os ao Senhor para perguntarem: "És tu aquele que haveria de vir ou devemos esperar algum outro? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25216, 'nvi', 'Lucas', 'lc', 7, 20, 'Dirigindo-se a Jesus, aqueles homens disseram: "João Batista nos enviou para te perguntarmos: ‘És tu aquele que haveria de vir ou devemos esperar algum outro? ’ "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25217, 'nvi', 'Lucas', 'lc', 7, 21, 'Naquele momento Jesus curou muitos que tinham males, doenças graves e espíritos malignos, e concedeu visão a muitos que eram cegos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25218, 'nvi', 'Lucas', 'lc', 7, 22, 'Então ele respondeu aos mensageiros: "Voltem e anunciem a João o que vocês viram e ouviram: os cegos vêem, os aleijados andam, os leprosos são purificados, os surdos ouvem, os mortos são ressuscitados e as boas novas são pregadas aos pobres;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25219, 'nvi', 'Lucas', 'lc', 7, 23, 'e feliz é aquele que não se escandaliza por minha causa".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25220, 'nvi', 'Lucas', 'lc', 7, 24, 'Depois que os mensageiros de João foram embora, Jesus começou a falar à multidão a respeito de João: "O que vocês foram ver no deserto? Um caniço agitado pelo vento?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25221, 'nvi', 'Lucas', 'lc', 7, 25, 'Ou, o que foram ver? Um homem vestido de roupas finas? Ora, os que vestem roupas esplêndidas e se entregam ao luxo estão nos palácios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25222, 'nvi', 'Lucas', 'lc', 7, 26, 'Afinal, o que foram ver? Um profeta? Sim, eu lhes digo, e mais que profeta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25223, 'nvi', 'Lucas', 'lc', 7, 27, 'Este é aquele a respeito de quem está escrito: ‘Enviarei o meu mensageiro à tua frente; ele preparará o teu caminho diante de ti’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25224, 'nvi', 'Lucas', 'lc', 7, 28, 'Eu lhes digo que entre os que nasceram de mulher não há ninguém maior do que João; todavia, o menor no Reino de Deus é maior do que ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25225, 'nvi', 'Lucas', 'lc', 7, 29, 'Todo o povo, até os publicanos, ouvindo as palavras de Jesus, reconheceram que o caminho de Deus era justo, sendo batizados por João.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25226, 'nvi', 'Lucas', 'lc', 7, 30, 'Mas os fariseus e os peritos na lei rejeitaram o propósito de Deus para eles, não sendo batizados por João.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25227, 'nvi', 'Lucas', 'lc', 7, 31, '"A que posso, pois, comparar os homens desta geração? ", prosseguiu Jesus. "Com que se parecem?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25228, 'nvi', 'Lucas', 'lc', 7, 32, 'São como crianças que ficam sentadas na praça e gritam umas às outras: ‘Nós lhes tocamos flauta, mas vocês não dançaram; cantamos um lamento, mas vocês não choraram’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25229, 'nvi', 'Lucas', 'lc', 7, 33, 'Pois veio João Batista, que jejua e não bebe vinho, e vocês dizem: ‘Ele tem demônio’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25230, 'nvi', 'Lucas', 'lc', 7, 34, 'Veio o Filho do homem, comendo e bebendo, e vocês dizem: ‘Aí está um comilão e beberrão, amigo de publicanos e "pecadores" ’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25231, 'nvi', 'Lucas', 'lc', 7, 35, 'Mas a sabedoria é comprovada por todos os seus discípulos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25232, 'nvi', 'Lucas', 'lc', 7, 36, 'Convidado por um dos fariseus para jantar, Jesus foi à casa dele e reclinou-se à mesa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25233, 'nvi', 'Lucas', 'lc', 7, 37, 'Ao saber que Jesus estava comendo na casa do fariseu, certa mulher daquela cidade, uma ‘pecadora’, trouxe um frasco de alabastro com perfume,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25234, 'nvi', 'Lucas', 'lc', 7, 38, 'e se colocou atrás de Jesus, a seus pés. Chorando, começou a molhar-lhe os pés com as suas lágrimas. Depois os enxugou com seus cabelos, beijou-os e os ungiu com o perfume.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25235, 'nvi', 'Lucas', 'lc', 7, 39, 'Ao ver isso, o fariseu que o havia convidado disse a si mesmo: "Se este homem fosse profeta, saberia quem nele está tocando e que tipo de mulher ela é: uma ‘pecadora’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25236, 'nvi', 'Lucas', 'lc', 7, 40, 'Respondeu-lhe Jesus: "Simão, tenho algo a lhe dizer". "Dize, Mestre", disse ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25237, 'nvi', 'Lucas', 'lc', 7, 41, '"Dois homens deviam a certo credor. Um lhe devia quinhentos denários e o outro, cinqüenta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25238, 'nvi', 'Lucas', 'lc', 7, 42, 'Nenhum dos dois tinha com que lhe pagar, por isso perdoou a dívida a ambos. Qual deles o amará mais? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25239, 'nvi', 'Lucas', 'lc', 7, 43, 'Simão respondeu: "Suponho que aquele a quem foi perdoada a dívida maior". "Você julgou bem", disse Jesus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25240, 'nvi', 'Lucas', 'lc', 7, 44, 'Em seguida, virou-se para a mulher e disse a Simão: "Vê esta mulher? Entrei em sua casa, mas você não me deu água para lavar os pés; ela, porém, molhou os meus pés com as suas lágrimas e os enxugou com os seus cabelos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25241, 'nvi', 'Lucas', 'lc', 7, 45, 'Você não me saudou com um beijo, mas esta mulher, desde que entrei aqui, não parou de beijar os meus pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25242, 'nvi', 'Lucas', 'lc', 7, 46, 'Você não ungiu a minha cabeça com óleo, mas ela derramou perfume nos meus pés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25243, 'nvi', 'Lucas', 'lc', 7, 47, 'Portanto, eu lhe digo, os muitos pecados dela lhe foram perdoados, pelo que ela amou muito. Mas aquele a quem pouco foi perdoado, pouco ama".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25244, 'nvi', 'Lucas', 'lc', 7, 48, 'Então Jesus disse a ela: "Seus pecados estão perdoados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25245, 'nvi', 'Lucas', 'lc', 7, 49, 'Os outros convidados começaram a perguntar: "Quem é este que até perdoa pecados? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25246, 'nvi', 'Lucas', 'lc', 7, 50, 'Jesus disse à mulher: "Sua fé a salvou; vá em paz".');
