INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25247, 'nvi', 'Lucas', 'lc', 8, 1, 'Depois disso Jesus ia passando pelas cidades e povoados proclamando as boas novas do Reino de Deus. Os Doze estavam com ele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25248, 'nvi', 'Lucas', 'lc', 8, 2, 'e também algumas mulheres que haviam sido curadas de espíritos malignos e doenças: Maria, chamada Madalena, de quem haviam saído sete demônios;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25249, 'nvi', 'Lucas', 'lc', 8, 3, 'Joana, mulher de Cuza, administrador da casa de Herodes; Susana e muitas outras. Essas mulheres ajudavam a sustentá-los com os seus bens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25250, 'nvi', 'Lucas', 'lc', 8, 4, 'Reunindo-se uma grande multidão e vindo a Jesus gente de várias cidades, ele contou esta parábola:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25251, 'nvi', 'Lucas', 'lc', 8, 5, '"O semeador saiu a semear. Enquanto lançava a semente, parte dela caiu à beira do caminho; foi pisada, e as aves do céu a comeram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25252, 'nvi', 'Lucas', 'lc', 8, 6, 'Parte dela caiu sobre pedras e, quando germinou, as plantas secaram, porque não havia umidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25253, 'nvi', 'Lucas', 'lc', 8, 7, 'Outra parte caiu entre espinhos, que cresceram com ela e sufocaram as plantas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25254, 'nvi', 'Lucas', 'lc', 8, 8, 'Outra ainda caiu em boa terra. Cresceu e deu boa colheita, a cem por um". Tendo dito isso, exclamou: "Aquele que tem ouvidos para ouvir, ouça! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25255, 'nvi', 'Lucas', 'lc', 8, 9, 'Seus discípulos perguntaram-lhe o que significava aquela parábola.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25256, 'nvi', 'Lucas', 'lc', 8, 10, 'Ele disse: "A vocês foi dado o conhecimento dos mistérios do Reino de Deus, mas aos outros falo por parábolas, para que ‘vendo, não vejam; e ouvindo, não entendam’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25257, 'nvi', 'Lucas', 'lc', 8, 11, '"Este é o significado da parábola: A semente é a palavra de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25258, 'nvi', 'Lucas', 'lc', 8, 12, 'As que caíram à beira do caminho são os que ouvem, e então vem o diabo e tira a palavra dos seus corações, para que não creiam e não sejam salvos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25259, 'nvi', 'Lucas', 'lc', 8, 13, 'As que caíram sobre as pedras são os que recebem a palavra com alegria quando a ouvem, mas não têm raiz. Crêem durante algum tempo, mas desistem na hora da provação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25260, 'nvi', 'Lucas', 'lc', 8, 14, 'As que caíram entre espinhos são os que ouvem, mas, ao seguirem seu caminho, são sufocados pelas preocupações, pelas riquezas e pelos prazeres desta vida, e não amadurecem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25261, 'nvi', 'Lucas', 'lc', 8, 15, 'Mas as que caíram em boa terra são os que, com coração bom e generoso, ouvem a palavra, a retêm e dão fruto, com perseverança".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25262, 'nvi', 'Lucas', 'lc', 8, 16, '"Ninguém acende uma candeia e a esconde num jarro ou a coloca debaixo de uma cama. Pelo contrário, coloca-a num lugar apropriado, de modo que os que entram possam ver a luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25263, 'nvi', 'Lucas', 'lc', 8, 17, 'Porque não há nada oculto que não venha a ser revelado, e nada escondido que não venha a ser conhecido e trazido à luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25264, 'nvi', 'Lucas', 'lc', 8, 18, 'Portanto, considerem atentamente como vocês estão ouvindo. A quem tiver, mais lhe será dado; de quem não tiver, até o que pensa que tem lhe será tirado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25265, 'nvi', 'Lucas', 'lc', 8, 19, 'A mãe e os irmãos de Jesus foram vê-lo, mas não conseguiam aproximar-se dele, por causa da multidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25266, 'nvi', 'Lucas', 'lc', 8, 20, 'Alguém lhe disse: "Tua mãe e teus irmãos estão lá fora e querem ver-te".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25267, 'nvi', 'Lucas', 'lc', 8, 21, 'Ele lhe respondeu: "Minha mãe e meus irmãos são aqueles que ouvem a palavra de Deus e a praticam".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25268, 'nvi', 'Lucas', 'lc', 8, 22, 'Certo dia Jesus disse aos seus discípulos: "Vamos para o outro lado do lago". Eles entraram num barco e partiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25269, 'nvi', 'Lucas', 'lc', 8, 23, 'Enquanto navegavam, ele adormeceu. Abateu-se sobre o lago um forte vendaval, de modo que o barco estava sendo inundado, e eles corriam grande perigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25270, 'nvi', 'Lucas', 'lc', 8, 24, 'Os discípulos foram acordá-lo, clamando: "Mestre, Mestre, vamos morrer! " Ele se levantou e repreendeu o vento e a violência das águas; tudo se acalmou e ficou tranqüilo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25271, 'nvi', 'Lucas', 'lc', 8, 25, '"Onde está a sua fé? ", perguntou ele aos seus discípulos. Amedrontados e admirados, eles perguntaram uns aos outros: "Quem é este que até aos ventos e às águas dá ordens, e eles lhe obedecem? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25272, 'nvi', 'Lucas', 'lc', 8, 26, 'Navegaram para a região dos gerasenos que fica do outro lado do lago, frente à Galiléia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25273, 'nvi', 'Lucas', 'lc', 8, 27, 'Quando Jesus pisou em terra, foi ao encontro dele um endemoninhado daquela cidade. Fazia muito tempo que aquele homem não usava roupas, nem vivia em casa alguma, mas nos sepulcros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25274, 'nvi', 'Lucas', 'lc', 8, 28, 'Quando viu Jesus, gritou, prostrou-se aos seus pés e disse em alta voz: "Que queres comigo, Jesus, Filho do Deus Altíssimo? Rogo-te que não me atormentes! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25275, 'nvi', 'Lucas', 'lc', 8, 29, 'Pois Jesus havia ordenado que o espírito imundo saísse daquele homem. Muitas vezes ele tinha se apoderado dele. Mesmo com os pés e as mãos acorrentados e entregue aos cuidados de guardas, quebrava as correntes, e era levado pelo demônio a lugares solitários.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25276, 'nvi', 'Lucas', 'lc', 8, 30, 'Jesus lhe perguntou: "Qual é o seu nome? " "Legião", respondeu ele; porque muitos demônios haviam entrado nele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25277, 'nvi', 'Lucas', 'lc', 8, 31, 'E imploravam-lhe que não os mandasse para o abismo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25278, 'nvi', 'Lucas', 'lc', 8, 32, 'Uma grande manada de porcos estava pastando naquela colina. Os demônios imploraram a Jesus que lhes permitisse entrar neles, e Jesus lhes deu permissão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25279, 'nvi', 'Lucas', 'lc', 8, 33, 'Saindo do homem, os demônios entraram nos porcos, e toda a manada atirou-se precipício abaixo em direção ao lago e se afogou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25280, 'nvi', 'Lucas', 'lc', 8, 34, 'Vendo o que acontecera, os que cuidavam dos porcos fugiram e contaram esses fatos, na cidade e nos campos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25281, 'nvi', 'Lucas', 'lc', 8, 35, 'e o povo foi ver o que havia acontecido. Quando se aproximaram de Jesus, viram que o homem de quem haviam saído os demônios estava assentado aos pés de Jesus, vestido e em perfeito juízo, e ficaram com medo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25282, 'nvi', 'Lucas', 'lc', 8, 36, 'Os que o tinham visto contaram ao povo como o endemoninhado fora curado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25283, 'nvi', 'Lucas', 'lc', 8, 37, 'Então, todo o povo da região dos gerasenos suplicou a Jesus que se retirasse, porque estavam dominados pelo medo. Ele entrou no barco e regressou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25284, 'nvi', 'Lucas', 'lc', 8, 38, 'O homem de quem haviam saído os demônios suplicava-lhe que o deixasse ir com ele; mas Jesus o mandou embora, dizendo:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25285, 'nvi', 'Lucas', 'lc', 8, 39, '"Volte para casa e conte o quanto Deus lhe fez". Assim, o homem se foi e anunciou a toda a cidade o quanto Jesus tinha feito por ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25286, 'nvi', 'Lucas', 'lc', 8, 40, 'Quando Jesus voltou, uma multidão o recebeu, pois todos o esperavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25287, 'nvi', 'Lucas', 'lc', 8, 41, 'Então um homem chamado Jairo, dirigente da sinagoga, veio e prostrou-se aos pés de Jesus, implorando-lhe que fosse à sua casa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25288, 'nvi', 'Lucas', 'lc', 8, 42, 'porque sua única filha, de cerca de doze anos, estava à morte. Estando Jesus a caminho, a multidão o comprimia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25289, 'nvi', 'Lucas', 'lc', 8, 43, 'E estava ali certa mulher que havia doze anos vinha sofrendo de uma hemorragia e gastara tudo o que tinha com os médicos; mas ninguém pudera curá-la.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25290, 'nvi', 'Lucas', 'lc', 8, 44, 'Ela chegou por trás dele, tocou na borda de seu manto, e imediatamente cessou sua hemorragia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25291, 'nvi', 'Lucas', 'lc', 8, 45, '"Quem tocou em mim? ", perguntou Jesus. Como todos negassem, Pedro disse: "Mestre, a multidão se aglomera e te comprime".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25292, 'nvi', 'Lucas', 'lc', 8, 46, 'Mas Jesus disse: "Alguém tocou em mim; eu sei que de mim saiu poder".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25293, 'nvi', 'Lucas', 'lc', 8, 47, 'Então a mulher, vendo que não conseguiria passar despercebida, veio tremendo e prostrou-se aos seus pés. Na presença de todo o povo contou por que tinha tocado nele e como fora instantaneamente curada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25294, 'nvi', 'Lucas', 'lc', 8, 48, 'Então ele lhe disse: "Filha, a sua fé a curou! Vá em paz".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25295, 'nvi', 'Lucas', 'lc', 8, 49, 'Enquanto Jesus ainda estava falando, chegou alguém da casa de Jairo, o dirigente da sinagoga, e disse: "Sua filha morreu. Não incomode mais o Mestre".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25296, 'nvi', 'Lucas', 'lc', 8, 50, 'Ouvindo isso, Jesus disse a Jairo: "Não tenha medo; tão-somente creia, e ela será curada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25297, 'nvi', 'Lucas', 'lc', 8, 51, 'Quando chegou à casa de Jairo, não deixou ninguém entrar com ele, exceto Pedro, João, Tiago e o pai e a mãe da criança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25298, 'nvi', 'Lucas', 'lc', 8, 52, 'Enquanto isso, todo o povo estava se lamentando e chorando por ela. "Não chorem", disse Jesus. "Ela não está morta, mas dorme".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25299, 'nvi', 'Lucas', 'lc', 8, 53, 'Todos começaram a rir dele, pois sabiam que ela estava morta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25300, 'nvi', 'Lucas', 'lc', 8, 54, 'Mas ele a tomou pela mão e disse: "Menina, levante-se! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25301, 'nvi', 'Lucas', 'lc', 8, 55, 'O espírito dela voltou, e ela se levantou imediatamente. Então Jesus lhes ordenou que lhe dessem de comer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25302, 'nvi', 'Lucas', 'lc', 8, 56, 'Os pais dela ficaram maravilhados, mas ele lhes ordenou que não contassem a ninguém o que tinha acontecido.');
