INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25303, 'nvi', 'Lucas', 'lc', 9, 1, 'Reunindo os Doze, Jesus deu-lhes poder e autoridade para expulsar todos os demônios e curar doenças,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25304, 'nvi', 'Lucas', 'lc', 9, 2, 'e os enviou a pregar o Reino de Deus e a curar os enfermos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25305, 'nvi', 'Lucas', 'lc', 9, 3, 'E disse-lhes: "Não levem nada pelo caminho: nem bordão, nem saco de viagem, nem pão, nem dinheiro, nem túnica extra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25306, 'nvi', 'Lucas', 'lc', 9, 4, 'Na casa em que vocês entrarem, fiquem ali até partirem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25307, 'nvi', 'Lucas', 'lc', 9, 5, 'Se não os receberem, sacudam a poeira dos seus pés quando saírem daquela cidade, como testemunho contra eles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25308, 'nvi', 'Lucas', 'lc', 9, 6, 'Assim, eles saíram e foram pelos povoados, pregando o evangelho e fazendo curas por toda parte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25309, 'nvi', 'Lucas', 'lc', 9, 7, 'Herodes, o tetrarca, ouviu falar de tudo o que estava acontecendo e ficou perplexo, porque algumas pessoas estavam dizendo que João tinha ressuscitado dos mortos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25310, 'nvi', 'Lucas', 'lc', 9, 8, 'outros, que Elias tinha aparecido; e ainda outros, que um dos profetas do passado tinha voltado à vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25311, 'nvi', 'Lucas', 'lc', 9, 9, 'Mas Herodes disse: "João, eu decapitei! Quem, pois, é este de quem ouço essas coisas? " E procurava vê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25312, 'nvi', 'Lucas', 'lc', 9, 10, 'Ao voltarem, os apóstolos relataram a Jesus o que tinham feito. Então ele os tomou consigo, e retiraram-se para uma cidade chamada Betsaida;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25313, 'nvi', 'Lucas', 'lc', 9, 11, 'mas as multidões ficaram sabendo, e o seguiram. Ele as acolheu, e falava-lhes acerca do Reino de Deus, e curava os que precisavam de cura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25314, 'nvi', 'Lucas', 'lc', 9, 12, 'Ao fim da tarde os Doze aproximaram-se dele e disseram: "Manda embora a multidão para que eles possam ir aos campos vizinhos e aos povoados, e encontrem comida e pousada, porque aqui estamos em lugar deserto".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25315, 'nvi', 'Lucas', 'lc', 9, 13, 'Ele, porém, respondeu: "Dêem-lhes vocês algo para comer". Eles disseram: "Temos apenas cinco pães e dois peixes — a menos que compremos alimento para toda esta multidão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25316, 'nvi', 'Lucas', 'lc', 9, 14, '( E estavam ali cerca de cinco mil homens ). Mas ele disse aos seus discípulos: "Façam-nos sentar-se em grupos de cinqüenta".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25317, 'nvi', 'Lucas', 'lc', 9, 15, 'Os discípulos assim fizeram, e todos se assentaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25318, 'nvi', 'Lucas', 'lc', 9, 16, 'Tomando os cinco pães e os dois peixes, e olhando para o céu, deu graças e os partiu. Em seguida, entregou-os aos discípulos para que os servissem ao povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25319, 'nvi', 'Lucas', 'lc', 9, 17, 'Todos comeram e ficaram satisfeitos, e os discípulos recolheram doze cestos cheios de pedaços que sobraram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25320, 'nvi', 'Lucas', 'lc', 9, 18, 'Certa vez Jesus estava orando em particular, e com ele estavam os seus discípulos; então lhes perguntou: "Quem as multidões dizem que eu sou? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25321, 'nvi', 'Lucas', 'lc', 9, 19, 'Eles responderam: "Alguns dizem que és João Batista; outros, Elias; e, ainda outros, que és um dos profetas do passado que ressuscitou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25322, 'nvi', 'Lucas', 'lc', 9, 20, '"E vocês, o que dizem? ", perguntou. "Quem vocês dizem que eu sou? " Pedro respondeu: "O Cristo de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25323, 'nvi', 'Lucas', 'lc', 9, 21, 'Jesus os advertiu severamente que não contassem isso a ninguém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25324, 'nvi', 'Lucas', 'lc', 9, 22, 'E disse: "É necessário que o Filho do homem sofra muitas coisas e seja rejeitado pelos líderes religiosos, pelos chefes dos sacerdotes e pelos mestres da lei, seja morto e ressuscite no terceiro dia".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25325, 'nvi', 'Lucas', 'lc', 9, 23, 'Jesus dizia a todos: "Se alguém quiser acompanhar-me, negue-se a si mesmo, tome diariamente a sua cruz e siga-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25326, 'nvi', 'Lucas', 'lc', 9, 24, 'Pois quem quiser salvar a sua vida a perderá; mas quem perder a vida por minha causa, este a salvará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25327, 'nvi', 'Lucas', 'lc', 9, 25, 'Pois que adianta ao homem ganhar o mundo inteiro, e perder-se ou destruir a si mesmo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25328, 'nvi', 'Lucas', 'lc', 9, 26, 'Se alguém se envergonhar de mim e das minhas palavras, o Filho do homem se envergonhará dele, quando vier em sua glória e na glória do Pai e dos santos anjos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25329, 'nvi', 'Lucas', 'lc', 9, 27, 'Garanto-lhes que alguns que aqui se acham de modo nenhum experimentarão a morte antes de verem o Reino de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25330, 'nvi', 'Lucas', 'lc', 9, 28, 'Aproximadamente oito dias depois de dizer essas coisas, Jesus tomou consigo a Pedro, João e Tiago e subiu a um monte para orar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25331, 'nvi', 'Lucas', 'lc', 9, 29, 'Enquanto orava, a aparência de seu rosto se transformou, e suas roupas ficaram alvas e resplandecentes como o brilho de um relâmpago.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25332, 'nvi', 'Lucas', 'lc', 9, 30, 'Surgiram dois homens que começaram a conversar com Jesus. Eram Moisés e Elias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25333, 'nvi', 'Lucas', 'lc', 9, 31, 'Apareceram em glorioso esplendor, e falavam sobre a partida de Jesus, que estava para se cumprir em Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25334, 'nvi', 'Lucas', 'lc', 9, 32, 'Pedro e os seus companheiros estavam dominados pelo sono; acordando subitamente, viram a glória de Jesus e os dois homens que estavam com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25335, 'nvi', 'Lucas', 'lc', 9, 33, 'Quando estes estavam se retirando, Pedro disse a Jesus: "Mestre, é bom estarmos aqui. Façamos três tendas: uma para ti, uma para Moisés e uma para Elias". ( Ele não sabia o que estava dizendo. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25336, 'nvi', 'Lucas', 'lc', 9, 34, 'Enquanto ele estava falando, uma nuvem apareceu e os envolveu, e eles ficaram com medo ao entrarem na nuvem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25337, 'nvi', 'Lucas', 'lc', 9, 35, 'Dela saiu uma voz que dizia: "Este é o meu Filho, o Escolhido; ouçam a ele! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25338, 'nvi', 'Lucas', 'lc', 9, 36, 'Tendo-se ouvido a voz, Jesus ficou só. Os discípulos guardaram isto somente para si; naqueles dias, não contaram a ninguém nada do que tinham visto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25339, 'nvi', 'Lucas', 'lc', 9, 37, 'No dia seguinte, quando desceram do monte, uma grande multidão veio ao encontro dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25340, 'nvi', 'Lucas', 'lc', 9, 38, 'Um homem da multidão bradou: "Mestre, rogo-te que dês atenção ao meu filho, pois é o único que tenho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25341, 'nvi', 'Lucas', 'lc', 9, 39, 'Um espírito o domina; de repente ele grita; lança-o em convulsões e o faz espumar; quase nunca o abandona, e o está destruindo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25342, 'nvi', 'Lucas', 'lc', 9, 40, 'Roguei aos teus discípulos que o expulsassem, mas eles não conseguiram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25343, 'nvi', 'Lucas', 'lc', 9, 41, 'Respondeu Jesus: "Ó geração incrédula e perversa, até quando estarei com vocês e terei que suportá-los? Traga-me aqui o seu filho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25344, 'nvi', 'Lucas', 'lc', 9, 42, 'Quando o menino vinha vindo, o demônio o lançou por terra, em convulsão. Mas Jesus repreendeu o espírito imundo, curou o menino e o entregou de volta a seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25345, 'nvi', 'Lucas', 'lc', 9, 43, 'E todos ficaram atônitos ante a grandeza de Deus. Estando todos maravilhados com tudo o que Jesus fazia, ele disse aos seus discípulos:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25346, 'nvi', 'Lucas', 'lc', 9, 44, '"Ouçam atentamente o que vou lhes dizer: o Filho do homem será traído e entregue nas mãos dos homens".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25347, 'nvi', 'Lucas', 'lc', 9, 45, 'Mas eles não entendiam o que isso significava; era-lhes encoberto, para que não o entendessem. E tinham receio de perguntar-lhe a respeito dessa palavra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25348, 'nvi', 'Lucas', 'lc', 9, 46, 'Começou uma discussão entre os discípulos, acerca de qual deles seria o maior.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25349, 'nvi', 'Lucas', 'lc', 9, 47, 'Jesus, conhecendo os seus pensamentos, tomou uma criança e a colocou em pé, a seu lado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25350, 'nvi', 'Lucas', 'lc', 9, 48, 'Então lhes disse: "Quem recebe esta criança em meu nome, está me recebendo; e quem me recebe, está recebendo aquele que me enviou. Pois aquele que entre vocês for o menor, este será o maior".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25351, 'nvi', 'Lucas', 'lc', 9, 49, 'Disse João: "Mestre, vimos um homem expulsando demônios em teu nome e procuramos impedi-lo, porque ele não era um dos nossos. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25352, 'nvi', 'Lucas', 'lc', 9, 50, '"Não o impeçam", disse Jesus, "pois quem não é contra vocês, é a favor de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25353, 'nvi', 'Lucas', 'lc', 9, 51, 'Aproximando-se o tempo em que seria elevado ao céu, Jesus partiu resolutamente em direção a Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25354, 'nvi', 'Lucas', 'lc', 9, 52, 'E enviou mensageiros à sua frente. Indo estes, entraram num povoado samaritano para lhe fazer os preparativos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25355, 'nvi', 'Lucas', 'lc', 9, 53, 'mas o povo dali não o recebeu porque se notava em seu semblante que ele ia para Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25356, 'nvi', 'Lucas', 'lc', 9, 54, 'Ao verem isso, os discípulos Tiago e João perguntaram: "Senhor, queres que façamos cair fogo do céu para destruí-los? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25357, 'nvi', 'Lucas', 'lc', 9, 55, 'Mas Jesus, voltando-se, os repreendeu, dizendo: "Vocês não sabem de que espécie de espírito são, pois o Filho do homem não veio para destruir a vida dos homens, mas para salvá-los";');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25358, 'nvi', 'Lucas', 'lc', 9, 56, 'e foram para outro povoado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25359, 'nvi', 'Lucas', 'lc', 9, 57, 'Quando andavam pelo caminho, um homem lhe disse: "Eu te seguirei por onde quer que fores".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25360, 'nvi', 'Lucas', 'lc', 9, 58, 'Jesus respondeu: "As raposas têm suas tocas e as aves do céu têm seus ninhos, mas o Filho do homem não tem onde repousar a cabeça".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25361, 'nvi', 'Lucas', 'lc', 9, 59, 'A outro disse: "Siga-me". Mas o homem respondeu: "Senhor, deixa-me ir primeiro sepultar meu pai".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25362, 'nvi', 'Lucas', 'lc', 9, 60, 'Jesus lhe disse: "Deixe que os mortos sepultem os seus próprios mortos; você, porém, vá e proclame o Reino de Deus".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25363, 'nvi', 'Lucas', 'lc', 9, 61, 'Ainda outro disse: "Vou seguir-te, Senhor, mas deixa-me primeiro voltar e me despedir da minha família".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (25364, 'nvi', 'Lucas', 'lc', 9, 62, 'Jesus respondeu: "Ninguém que põe a mão no arado e olha para trás é apto para o Reino de Deus".');
