INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20422, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 1, 'Como o ouro perdeu o brilho! Como o ouro fino ficou embaçado! As pedras sagradas estão espalhadas pelas esquinas de todas as ruas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20423, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 2, 'Como os preciosos filhos de Sião, que antes valiam seu peso em ouro, hoje são considerados como vasos de barro, obra das mãos de um oleiro!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20424, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 3, 'Até os chacais oferecem o peito para amamentar os seus filhotes, mas o meu povo não tem mais coração; é como as avestruzes do deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20425, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 4, 'De tanta sede, a língua dos bebês gruda no céu da boca; as crianças imploram pelo pão, mas ninguém as atende.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20426, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 5, 'Aqueles que costumavam comer comidas finas passam necessidades nas ruas. Aqueles que se adornavam de púrpura hoje estão prostrados sobre montes de cinza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20427, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 6, 'A punição do meu povo é maior que a de Sodoma, que foi destruída num instante sem que ninguém a socorresse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20428, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 7, 'Seus príncipes eram mais brilhantes que a neve e mais brancos do que o leite, tinham a pele mais rosada que rubis; sua aparência lembrava safiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20429, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 8, 'Mas agora estão mais negros do que o carvão; não são reconhecidos nas ruas. Sua pele enrugou-se sobre os seus ossos; parecem agora madeira seca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20430, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 9, 'Os que foram mortos pela espada estão melhor do que os que morrem de fome, os quais, torturados pela fome, definham pela falta de produção das lavouras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20431, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 10, 'Com as próprias mãos, mulheres bondosas cozinharam os próprios filhos, que se tornaram a sua comida quando o meu povo foi destruído.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20432, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 11, 'O Senhor deu vazão total à sua ira; derramou a sua grande fúria. Ele acendeu em Sião um fogo que consumiu os seus alicerces.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20433, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 12, 'Os reis da terra e os povos de todo o mundo, não acreditavam que os inimigos e os adversários pudessem entrar pelas portas de Jerusalém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20434, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 13, 'Dentro da cidade foi derramado o sangue dos justos por causa do pecado dos seus profetas e das maldades dos seus sacerdotes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20435, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 14, 'Hoje eles tateiam pelas ruas como cegos, e tão sujos de sangue estão, que ninguém ousa tocar em suas vestes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20436, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 15, '"Vocês estão imundos! ", o povo grita para eles. "Afastem-se! Não nos toquem! " Quando eles fogem e ficam vagando, os povos das outras nações dizem: "Aqui eles não podem habitar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20437, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 16, 'O próprio Senhor os espalhou; ele já não cuida deles. Ninguém honra os sacerdotes nem respeita os líderes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20438, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 17, 'Nossos olhos estão cansados de buscar ajuda em vão; de nossas torres buscávamos uma nação que não podia salvar-nos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20439, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 18, 'Cada passo nosso era vigiado, de forma que não podíamos caminhar por nossas ruas. Nosso fim estava próximo, nossos dias estavam contados; o nosso fim já havia chegado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20440, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 19, 'Nossos perseguidores eram mais velozes do que águias nos céus; perseguiam-nos por sobre as montanhas, ficavam de tocaia contra nós no deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20441, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 20, 'O ungido do Senhor, o próprio fôlego da nossa vida, foi capturado em suas armadilhas. E nós que pensávamos que sob a sua sombra viveríamos entre as nações!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20442, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 21, 'Alegre-se e exulte, ó terra de Edom, você que vive na terra de Uz. Mas a você também será servido o cálice: você será embriagada e as suas roupas serão arrancadas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (20443, 'nvi', 'Lamentações de Jeremias', 'lm', 4, 22, 'Ó cidade de Sião, o seu castigo terminará; o Senhor não prolongará o seu exílio. Mas, ó terra de Edom, ele punirá o seu pecado e porá à mostra a sua perversidade.');
