INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2978, 'nvi', 'Levítico', 'lv', 10, 1, 'Nadabe e Abiú, filhos de Arão, pegaram cada um o seu incensário, nos quais acenderam fogo, acrescentaram incenso, e trouxeram fogo profano perante o Senhor, sem que tivessem sido autorizados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2979, 'nvi', 'Levítico', 'lv', 10, 2, 'Então saiu fogo da presença do Senhor e os consumiu. Morreram perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2980, 'nvi', 'Levítico', 'lv', 10, 3, 'Moisés então disse a Arão: "Foi isto que o Senhor disse: ‘Aos que de mim se aproximam santo me mostrarei; à vista de todo o povo glorificado serei’ ". Arão, porém, ficou em silêncio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2981, 'nvi', 'Levítico', 'lv', 10, 4, 'Então Moisés chamou Misael e Elzafã, filhos de Uziel, tio de Arão, e lhes disse: "Venham cá; tirem os seus primos da frente do santuário e levem-nos para fora do acampamento".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2982, 'nvi', 'Levítico', 'lv', 10, 5, 'Eles foram e os puxaram pelas túnicas, para fora do acampamento, conforme Moisés tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2983, 'nvi', 'Levítico', 'lv', 10, 6, 'Então Moisés disse a Arão e a seus filhos Eleazar e Itamar: "Não andem descabelados, nem rasguem as roupas em sinal de luto, senão vocês morrerão e a ira do Senhor cairá sobre toda a comunidade. Mas os seus parentes, e toda a nação de Israel, poderão chorar por aqueles que o Senhor destruiu pelo fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2984, 'nvi', 'Levítico', 'lv', 10, 7, 'Não saiam da entrada da Tenda do Encontro, senão vocês morrerão, porquanto o óleo da unção do Senhor está sobre vocês". E eles fizeram conforme Moisés tinha ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2985, 'nvi', 'Levítico', 'lv', 10, 8, 'Depois o Senhor disse a Arão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2986, 'nvi', 'Levítico', 'lv', 10, 9, '"Você e seus filhos não devem beber vinho nem outra bebida fermentada antes de entrar na Tenda do Encontro, senão vocês morrerão. É um decreto perpétuo para as suas gerações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2987, 'nvi', 'Levítico', 'lv', 10, 10, 'Vocês têm que fazer separação entre o santo e o profano, entre o puro e o impuro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2988, 'nvi', 'Levítico', 'lv', 10, 11, 'e ensinar aos israelitas todos os decretos que o Senhor lhes deu por meio de Moisés".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2989, 'nvi', 'Levítico', 'lv', 10, 12, 'Então Moisés disse a Arão e aos seus filhos que ficaram vivos, Eleazar e Itamar: "Peguem a oferta de cereal que sobrou das ofertas dedicadas ao Senhor, preparadas no fogo, e comam-na sem fermento junto ao altar, pois é santíssima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2990, 'nvi', 'Levítico', 'lv', 10, 13, 'Comam-na em lugar sagrado, porquanto é a porção que lhes cabe por decreto, a você e a seus filhos, das ofertas dedicadas ao Senhor, preparadas no fogo; pois assim me foi ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2991, 'nvi', 'Levítico', 'lv', 10, 14, 'O peito ritualmente movido e a coxa ofertada, você, seus filhos e suas filhas poderão comer num lugar cerimonialmente puro; foi dado a você e a seus filhos como porção das ofertas de comunhão dos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2992, 'nvi', 'Levítico', 'lv', 10, 15, 'A coxa ofertada e o peito ritualmente movido devem ser trazidos junto com as porções de gordura das ofertas preparadas no fogo, para serem movidos perante o Senhor como gesto ritual de apresentação. Esta será a porção por decreto perpétuo para você e seus descendentes, conforme o Senhor tinha ordenado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2993, 'nvi', 'Levítico', 'lv', 10, 16, 'Quando Moisés procurou por toda parte o bode da oferta pelo pecado e soube que já fora queimado, irou-se contra Eleazar e Itamar, os filhos de Arão que ficaram vivos, e perguntou:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2994, 'nvi', 'Levítico', 'lv', 10, 17, '"Por que vocês não comeram a carne da oferta pelo pecado no Lugar Santo? É santíssima; foi-lhes dada para retirar a culpa da comunidade e fazer propiciação por ela perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2995, 'nvi', 'Levítico', 'lv', 10, 18, 'Como o sangue do animal não foi levado para dentro do Lugar Santo, vocês deviam tê-lo comido no Lugar Santo, conforme ordenei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2996, 'nvi', 'Levítico', 'lv', 10, 19, 'Arão respondeu a Moisés: "Hoje eles ofereceram o seu sacrifício pelo pecado e o seu holocausto perante o Senhor; mesmo assim coisas como essas aconteceram comigo. Será que teria agradado ao Senhor se eu tivesse comido a oferta pelo pecado hoje? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2997, 'nvi', 'Levítico', 'lv', 10, 20, 'Quando Moisés ouviu isso, ficou satisfeito.');
