INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2998, 'nvi', 'Levítico', 'lv', 11, 1, 'Disse o Senhor a Moisés e a Arão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (2999, 'nvi', 'Levítico', 'lv', 11, 2, '"Digam aos israelitas: De todos os animais que vivem na terra, estes são os que vocês poderão comer:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3000, 'nvi', 'Levítico', 'lv', 11, 3, 'Qualquer animal que tem casco fendido e dividido em duas unhas, e que rumina.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3001, 'nvi', 'Levítico', 'lv', 11, 4, '"Vocês não poderão comer aqueles que só ruminam nem os que só têm o casco fendido. O camelo, embora rumine, não tem casco fendido; considerem-no impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3002, 'nvi', 'Levítico', 'lv', 11, 5, 'O coelho, embora rumine, não tem casco fendido; é impuro para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3003, 'nvi', 'Levítico', 'lv', 11, 6, 'A lebre, embora rumine, não tem casco fendido; considerem-na impura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3004, 'nvi', 'Levítico', 'lv', 11, 7, 'E o porco, embora tenha casco fendido e dividido em duas unhas, não rumina; considerem-no impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3005, 'nvi', 'Levítico', 'lv', 11, 8, 'Vocês não comerão a carne desses animais nem tocarão em seus cadáveres; considerem-nos impuros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3006, 'nvi', 'Levítico', 'lv', 11, 9, '"De todas as criaturas que vivem nas águas do mar e dos rios, vocês poderão comer todas as que possuem barbatanas e escamas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3007, 'nvi', 'Levítico', 'lv', 11, 10, 'Mas todas as criaturas que vivem nos mares ou nos rios, que não possuem barbatanas e escamas, quer dentre todas as pequenas criaturas que povoam as águas quer dentre todos os outros animais das águas, serão proibidas para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3008, 'nvi', 'Levítico', 'lv', 11, 11, 'Por isso, não poderão comer sua carne e considerarão impuros os seus cadáveres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3009, 'nvi', 'Levítico', 'lv', 11, 12, 'Tudo o que vive na água e não possui barbatanas e escamas será proibido para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3010, 'nvi', 'Levítico', 'lv', 11, 13, '"Estas são as aves que vocês considerarão impuras, das quais não poderão comer porque são proibidas: a águia, o urubu, a águia-marinha,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3011, 'nvi', 'Levítico', 'lv', 11, 14, 'o milhafre, o falcão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3012, 'nvi', 'Levítico', 'lv', 11, 15, 'qualquer espécie de corvo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3013, 'nvi', 'Levítico', 'lv', 11, 16, 'a coruja-de-chifre, a coruja-de-orelha-pequena, a coruja-orelhuda, qualquer espécie de gavião,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3014, 'nvi', 'Levítico', 'lv', 11, 17, 'o mocho, a coruja-pescadora e o corujão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3015, 'nvi', 'Levítico', 'lv', 11, 18, 'a coruja-branca, a coruja-do-deserto, o abutre,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3016, 'nvi', 'Levítico', 'lv', 11, 19, 'a cegonha, qualquer tipo de garça, a poupa e o morcego.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3017, 'nvi', 'Levítico', 'lv', 11, 20, '"Todas as pequenas criaturas que enxameiam, que têm asas, mas que se movem pelo chão serão proibidas para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3018, 'nvi', 'Levítico', 'lv', 11, 21, 'Dentre estas, porém, vocês poderão comer aquelas que têm pernas articuladas para saltar no chão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3019, 'nvi', 'Levítico', 'lv', 11, 22, 'Dessas vocês poderão comer os diversos tipos de gafanhotos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3020, 'nvi', 'Levítico', 'lv', 11, 23, 'Mas considerarão impuras todas as outras criaturas que enxameiam, que têm asas e que se movem pelo chão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3021, 'nvi', 'Levítico', 'lv', 11, 24, '"Por meio deles vocês ficarão impuros; todo aquele que tocar em seus cadáveres estará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3022, 'nvi', 'Levítico', 'lv', 11, 25, 'Todo o que carregar o cadáver de algum deles lavará as suas roupas e estará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3023, 'nvi', 'Levítico', 'lv', 11, 26, '"Todo animal de casco não dividido em duas unhas ou que não rumina é impuro para vocês; quem tocar qualquer um deles ficará impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3024, 'nvi', 'Levítico', 'lv', 11, 27, 'Todos os animais de quatro pés, que andam sobre a planta dos pés são impuros para vocês; todo o que tocar os seus cadáveres ficará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3025, 'nvi', 'Levítico', 'lv', 11, 28, 'Quem carregar o cadáver de algum deles lavará suas roupas, e estará impuro até à tarde. São impuros para vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3026, 'nvi', 'Levítico', 'lv', 11, 29, '"Dos animais que se movem rente ao chão, estes vocês considerarão impuros: a doninha, o rato, qualquer espécie de lagarto grande,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3027, 'nvi', 'Levítico', 'lv', 11, 30, 'a lagartixa, o lagarto-pintado, o lagarto, o lagarto da areia e o camaleão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3028, 'nvi', 'Levítico', 'lv', 11, 31, 'De todos os que se movem rente ao chão, esses vocês considerarão impuros. Quem neles tocar depois de mortos estará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3029, 'nvi', 'Levítico', 'lv', 11, 32, 'E tudo sobre o que um deles cair depois de morto, qualquer que seja o seu uso, ficará impuro, seja objeto feito de madeira, de pano, de couro ou de pano de saco. Deverá ser posto em água e estará impuro até a tarde, e então ficará puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3030, 'nvi', 'Levítico', 'lv', 11, 33, 'Se um deles cair dentro de uma vasilha de barro, tudo o que nela houver ficará impuro, e vocês quebrarão a vasilha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3031, 'nvi', 'Levítico', 'lv', 11, 34, 'Qualquer alimento sobre o qual cair essa água, ficará impuro, e qualquer bebida que estiver dentro da vasilha, ficará impura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3032, 'nvi', 'Levítico', 'lv', 11, 35, 'Tudo aquilo sobre o que o cadáver de um desses animais cair ficará impuro; se for um forno ou um fogão de barro vocês o quebrarão. Estão impuros, e vocês os considerarão como tais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3033, 'nvi', 'Levítico', 'lv', 11, 36, 'Mas, se cair numa fonte ou numa cisterna onde se recolhe água, ela permanece pura; mas quem tocar no cadáver ficará impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3034, 'nvi', 'Levítico', 'lv', 11, 37, 'Se um cadáver cair sobre alguma semente a ser plantada, ela permanece pura;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3035, 'nvi', 'Levítico', 'lv', 11, 38, 'mas se foi derramada água sobre a semente, vocês a considerarão impura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3036, 'nvi', 'Levítico', 'lv', 11, 39, '"Quando morrer um animal que vocês têm permissão para comer, quem tocar no seu cadáver ficará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3037, 'nvi', 'Levítico', 'lv', 11, 40, 'Quem comer da carne do animal morto terá que lavar as suas roupas e ficará impuro até à tarde. Quem carregar o cadáver do animal terá que lavar as suas roupas, e ficará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3038, 'nvi', 'Levítico', 'lv', 11, 41, '"Todo animal que se move rente ao chão lhes será proibido e não poderá ser comido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3039, 'nvi', 'Levítico', 'lv', 11, 42, 'Vocês não poderão comer animal algum que se move rente ao chão, quer se arraste sobre o ventre quer ande de quatro ou com o auxílio de muitos pés; eles lhes são proibidos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3040, 'nvi', 'Levítico', 'lv', 11, 43, 'Não se contaminem com qualquer desses animais. Não se tornem impuros com eles nem deixem que eles os tornem impuros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3041, 'nvi', 'Levítico', 'lv', 11, 44, 'Pois eu sou o Senhor Deus de vocês; consagrem-se e sejam santos, porque eu sou santo. Não se tornem impuros com qualquer animal que se move rente ao chão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3042, 'nvi', 'Levítico', 'lv', 11, 45, 'Eu sou o Senhor que os tirou da terra do Egito para ser o seu Deus; por isso, sejam santos, porque eu sou santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3043, 'nvi', 'Levítico', 'lv', 11, 46, '"Essa é a regulamentação acerca dos animais, das aves, de todos os seres vivos que se movem na água e de todo animal que se move rente ao chão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3044, 'nvi', 'Levítico', 'lv', 11, 47, 'Vocês farão separação entre o impuro e o puro, entre animais que podem e os que não podem ser comidos".');
