INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3053, 'nvi', 'Levítico', 'lv', 13, 1, 'Disse o Senhor a Moisés e a Arão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3054, 'nvi', 'Levítico', 'lv', 13, 2, '"Quando alguém tiver um inchaço, uma erupção ou uma mancha brilhante na pele que possa ser sinal de lepra, será levado ao sacerdote Arão ou a um dos seus filhos que seja sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3055, 'nvi', 'Levítico', 'lv', 13, 3, 'Este examinará a parte afetada da pele, e, se naquela parte o pêlo tiver se tornado branco e o lugar parecer mais profundo do que a pele, é sinal de lepra. Depois de examiná-lo, o sacerdote o declarará impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3056, 'nvi', 'Levítico', 'lv', 13, 4, 'Se a mancha na pele for branca, mas não parecer mais profunda do que a pele e sobre ela o pêlo não tiver se tornado branco, o sacerdote o porá em isolamento por sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3057, 'nvi', 'Levítico', 'lv', 13, 5, 'No sétimo dia o sacerdote o examinará, e, se verificar que a parte afetada não se alterou nem se espalhou pela pele, o manterá em isolamento por mais sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3058, 'nvi', 'Levítico', 'lv', 13, 6, 'Ao sétimo dia o sacerdote o examinará de novo, e, se a parte afetada diminuiu e não se espalhou pela pele, o sacerdote o declarará puro; é apenas uma erupção. Então ele lavará as suas roupas, e estará puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3059, 'nvi', 'Levítico', 'lv', 13, 7, 'Mas, se depois que se apresentou ao sacerdote para ser declarado puro a erupção se espalhar pela pele, ele terá que se apresentar novamente ao sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3060, 'nvi', 'Levítico', 'lv', 13, 8, 'O sacerdote o examinará, e, se a erupção espalhou-se pela pele, ele o declarará impuro; trata-se de lepra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3061, 'nvi', 'Levítico', 'lv', 13, 9, '"Quando alguém apresentar sinal de lepra, será levado ao sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3062, 'nvi', 'Levítico', 'lv', 13, 10, 'Este o examinará, e, se houver inchaço branco na pele, o qual tornou branco o pêlo, e se houver carne viva no inchaço,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3063, 'nvi', 'Levítico', 'lv', 13, 11, 'é lepra crônica na pele, e o sacerdote o declarará impuro. Não o porá em isolamento, porquanto já está impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3064, 'nvi', 'Levítico', 'lv', 13, 12, '"Se a doença se alastrar por toda a pele e cobrir toda a pele da pessoa infectada, da cabeça aos pés, até onde é possível ao sacerdote verificar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3065, 'nvi', 'Levítico', 'lv', 13, 13, 'este o examinará e, se observar que a lepra cobriu todo o corpo, ele a declarará pura. Visto que tudo ficou branco, ela está pura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3066, 'nvi', 'Levítico', 'lv', 13, 14, 'Mas quando nela aparecer carne viva, ficará impura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3067, 'nvi', 'Levítico', 'lv', 13, 15, 'Quando o sacerdote vir a carne viva, ele a declarará impura. A carne viva é impura; trata-se de lepra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3068, 'nvi', 'Levítico', 'lv', 13, 16, 'Se a carne viva retroceder e a pele se tornar branca, a pessoa voltará ao sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3069, 'nvi', 'Levítico', 'lv', 13, 17, 'Este a examinará, e, se a parte afetada se tornou branca, o sacerdote declarará pura a pessoa infectada, que então estará pura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3070, 'nvi', 'Levítico', 'lv', 13, 18, '"Quando alguém tiver uma ferida purulenta em sua pele e ela sarar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3071, 'nvi', 'Levítico', 'lv', 13, 19, 'e no lugar da ferida aparecer um inchaço branco ou uma mancha avermelhada, ele se apresentará ao sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3072, 'nvi', 'Levítico', 'lv', 13, 20, 'Este examinará o local, e, se parecer mais profundo do que a pele e o pêlo ali tiver se tornado branco, o sacerdote o declarará impuro. É sinal de lepra que se alastrou onde estava a ferida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3073, 'nvi', 'Levítico', 'lv', 13, 21, 'Mas se, quando o sacerdote o examinar não houver nenhum pêlo branco e o lugar não estiver mais profundo do que a pele e tiver diminuído, então o sacerdote o porá em isolamento por sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3074, 'nvi', 'Levítico', 'lv', 13, 22, 'Se de fato estiver se espalhando pela pele, o sacerdote o declarará impuro; é sinal de lepra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3075, 'nvi', 'Levítico', 'lv', 13, 23, 'Mas, se a mancha não tiver se alterado nem se espalhado, é apenas a cicatriz da ferida, e o sacerdote o declarará puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3076, 'nvi', 'Levítico', 'lv', 13, 24, '"Quando alguém tiver uma queimadura na pele, e uma mancha avermelhada ou branca aparecer na carne viva da queimadura,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3077, 'nvi', 'Levítico', 'lv', 13, 25, 'o sacerdote examinará a mancha, e, se o pêlo sobre ela tiver se tornado branco e ela parecer mais profunda do que a pele, é lepra que surgiu na queimadura. O sacerdote o declarará impuro; é sinal de lepra na pele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3078, 'nvi', 'Levítico', 'lv', 13, 26, 'Mas, se o sacerdote examinar a mancha e nela não houver pêlo branco e esta não estiver mais profunda do que a pele e tiver diminuído, então o sacerdote o porá em isolamento por sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3079, 'nvi', 'Levítico', 'lv', 13, 27, 'No sétimo dia o sacerdote o examinará, e, se a mancha estiver se espalhado pela pele, o sacerdote o declarará impuro; é sinal de lepra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3080, 'nvi', 'Levítico', 'lv', 13, 28, 'Se, todavia, a mancha não tiver se alterado nem se espalhado pela pele, mas tiver diminuído, é um inchaço da queimadura, e o sacerdote o declarará puro; é apenas a cicatriz da queimadura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3081, 'nvi', 'Levítico', 'lv', 13, 29, '"Quando um homem ou uma mulher tiver uma ferida na cabeça ou no queixo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3082, 'nvi', 'Levítico', 'lv', 13, 30, 'o sacerdote examinará a ferida, e, se ela parecer mais profunda do que a pele e o pêlo nela for amarelado e fino, o sacerdote declarará impura aquela pessoa; é sarna, isto é, lepra da cabeça ou do queixo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3083, 'nvi', 'Levítico', 'lv', 13, 31, 'Mas, se quando o sacerdote examinar o sinal de sarna, este não parecer mais profundo do que a pele e não houver pêlo escuro nela, então o sacerdote porá a pessoa infectada em isolamento por sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3084, 'nvi', 'Levítico', 'lv', 13, 32, 'No sétimo dia o sacerdote examinará a parte afetada, e, se a sarna não tiver se espalhado e não houver pêlo amarelado nela e não parecer mais profunda do que a pele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3085, 'nvi', 'Levítico', 'lv', 13, 33, 'a pessoa rapará os pêlos, exceto na parte afetada, e o sacerdote a porá em isolamento por mais sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3086, 'nvi', 'Levítico', 'lv', 13, 34, 'No sétimo dia o sacerdote examinará a sarna, e, se não tiver se espalhado mais e não parecer mais profunda do que a pele, o sacerdote declarará pura a pessoa. Esta lavará suas roupas e estará pura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3087, 'nvi', 'Levítico', 'lv', 13, 35, 'Mas, se a sarna se espalhar pela pele depois que a pessoa for declarada pura,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3088, 'nvi', 'Levítico', 'lv', 13, 36, 'o sacerdote a examinará, e, se a sarna tiver se espalhado pela pele, o sacerdote não precisará procurar pêlo amarelado; a pessoa está impura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3089, 'nvi', 'Levítico', 'lv', 13, 37, 'Se, entretanto, verificar que não houve alteração e cresceu pêlo escuro, a sarna está curada. Ela está pura, e o sacerdote a declarará pura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3090, 'nvi', 'Levítico', 'lv', 13, 38, '"Quando um homem ou uma mulher tiver manchas brancas na pele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3091, 'nvi', 'Levítico', 'lv', 13, 39, 'o sacerdote examinará as manchas; se forem brancas e sem brilho, é um eczema que se alastrou; essa pessoa está pura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3092, 'nvi', 'Levítico', 'lv', 13, 40, '"Quando os cabelos de um homem caírem, ele está calvo, todavia puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3093, 'nvi', 'Levítico', 'lv', 13, 41, 'Se lhe caírem os cabelos da frente da cabeça, ele está meio-calvo, porém puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3094, 'nvi', 'Levítico', 'lv', 13, 42, 'Mas, se tiver uma ferida avermelhada na parte calva da frente ou de trás da cabeça, é lepra que se alastra pela calva da frente ou de trás da cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3095, 'nvi', 'Levítico', 'lv', 13, 43, 'O sacerdote o examinará, e, se a ferida inchada na parte da frente ou de trás da calva for avermelhada como a lepra de pele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3096, 'nvi', 'Levítico', 'lv', 13, 44, 'o homem está leproso e impuro. O sacerdote terá que declará-lo impuro devido à ferida na cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3097, 'nvi', 'Levítico', 'lv', 13, 45, '"Quem ficar leproso, apresentando quaisquer desses sintomas, usará roupas rasgadas, andará descabelado, cobrirá a parte inferior do rosto e gritará: ‘Impuro! Impuro! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3098, 'nvi', 'Levítico', 'lv', 13, 46, 'Enquanto tiver a doença, estará impuro. Viverá separado, fora do acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3099, 'nvi', 'Levítico', 'lv', 13, 47, '"Quando aparecer mancha de mofo em alguma roupa, seja roupa de lã, seja de linho,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3100, 'nvi', 'Levítico', 'lv', 13, 48, 'ou em qualquer peça tecida ou entrelaçada de linho ou de lã, ou em algum pedaço ou objeto de couro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3101, 'nvi', 'Levítico', 'lv', 13, 49, 'se a mancha na roupa, ou no pedaço de couro, ou na peça tecida ou entrelaçada, ou em qualquer objeto de couro, for esverdeada ou avermelhada, é mancha de mofo que deverá ser mostrada ao sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3102, 'nvi', 'Levítico', 'lv', 13, 50, 'O sacerdote examinará a mancha e isolará o objeto afetado por sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3103, 'nvi', 'Levítico', 'lv', 13, 51, 'No sétimo dia examinará a mancha, e, se ela tiver se espalhado pela roupa, ou pela peça tecida ou entrelaçada, ou pelo pedaço de couro, qualquer que seja o seu uso, é mofo corrosivo; o objeto está impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3104, 'nvi', 'Levítico', 'lv', 13, 52, 'Ele queimará a roupa, ou a peça tecida ou entrelaçada, ou qualquer objeto de couro que tiver a mancha, pois é mofo corrosivo; o objeto será queimado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3105, 'nvi', 'Levítico', 'lv', 13, 53, '"Mas se, quando o sacerdote o examinar, a mancha não tiver se espalhado pela roupa, ou pela peça tecida ou entrelaçada, ou pelo objeto de couro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3106, 'nvi', 'Levítico', 'lv', 13, 54, 'ordenará que o objeto afetado seja lavado. Então ele o isolará por mais sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3107, 'nvi', 'Levítico', 'lv', 13, 55, 'Depois de lavado o objeto afetado, o sacerdote o examinará, e, se a mancha não tiver alterado sua cor, ainda que não tenha se espalhado, o objeto estará impuro. Queime-o com fogo, quer o mofo corrosivo tenha afetado um lado, quer o outro do objeto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3108, 'nvi', 'Levítico', 'lv', 13, 56, 'Se quando o sacerdote o examinar, a mancha tiver diminuído depois de lavado o objeto, ele cortará a parte afetada da roupa, ou do pedaço de couro, ou da peça tecida ou entrelaçada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3109, 'nvi', 'Levítico', 'lv', 13, 57, 'Mas, se a mancha ainda aparecer na roupa, ou na peça tecida ou entrelaçada, ou no objeto de couro, é mofo que se alastra, e tudo o que tiver o mofo será queimado com fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3110, 'nvi', 'Levítico', 'lv', 13, 58, 'Mas se, depois de lavada, a mancha desaparecer da roupa, ou da peça tecida ou entrelaçada, ou do objeto de couro, será lavado de novo, e então estará puro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3111, 'nvi', 'Levítico', 'lv', 13, 59, 'Essa é a regulamentação acerca da mancha de mofo nas roupas de lã ou de linho, nas peças tecidas ou entrelaçadas, ou nos objetos de couro, para declará-los puros ou impuros.');
