INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3112, 'nvi', 'Levítico', 'lv', 14, 1, 'Disse também o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3113, 'nvi', 'Levítico', 'lv', 14, 2, '"Esta é a regulamentação acerca da purificação de um leproso: ele será levado ao sacerdote,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3114, 'nvi', 'Levítico', 'lv', 14, 3, 'que sairá do acampamento e o examinará. Se a pessoa foi curada da lepra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3115, 'nvi', 'Levítico', 'lv', 14, 4, 'o sacerdote ordenará que duas aves puras, vivas, um pedaço de madeira de cedro, um pano vermelho e um ramo de hissopo sejam trazidos em favor daquele que será purificado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3116, 'nvi', 'Levítico', 'lv', 14, 5, 'Então o sacerdote ordenará que uma das aves seja morta numa vasilha de barro com água da fonte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3117, 'nvi', 'Levítico', 'lv', 14, 6, 'Então pegará a ave viva e a molhará, juntamente com o pedaço de madeira de cedro, com o pano vermelho e com o ramo de hissopo, no sangue da ave morta em água corrente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3118, 'nvi', 'Levítico', 'lv', 14, 7, 'Sete vezes ele aspergirá aquele que está sendo purificado da lepra e o declarará puro. Depois soltará a ave viva em campo aberto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3119, 'nvi', 'Levítico', 'lv', 14, 8, '"Aquele que estiver sendo purificado lavará as suas roupas, rapará todos os seus pêlos e se banhará com água; e assim estará puro. Depois disso poderá entrar no acampamento, mas ficará fora da sua tenda por sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3120, 'nvi', 'Levítico', 'lv', 14, 9, 'No sétimo dia rapará todos os seus pêlos: o cabelo, a barba, as sobrancelhas e o restante dos pêlos. Lavará suas roupas e banhará o corpo com água; então ficará puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3121, 'nvi', 'Levítico', 'lv', 14, 10, '"No oitavo dia pegará dois cordeiros sem defeito e uma cordeira de um ano sem defeito, juntamente com três jarros da melhor farinha amassada com óleo, como oferta de cereal, e uma caneca de óleo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3122, 'nvi', 'Levítico', 'lv', 14, 11, 'O sacerdote que faz a purificação apresentará ao Senhor, à entrada da Tenda do Encontro, tanto aquele que estiver para ser purificado como as suas ofertas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3123, 'nvi', 'Levítico', 'lv', 14, 12, '"Então o sacerdote pegará um dos cordeiros e o sacrificará como oferta pela culpa, juntamente com a caneca de óleo; ele os moverá perante o Senhor como gesto ritual de apresentação e');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3124, 'nvi', 'Levítico', 'lv', 14, 13, 'matará o cordeiro no Lugar Santo, onde são sacrificados a oferta pelo pecado e o holocausto. Como se dá com a oferta pelo pecado, também a oferta pela culpa pertence ao sacerdote; é santíssima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3125, 'nvi', 'Levítico', 'lv', 14, 14, 'O sacerdote porá um pouco do sangue da oferta pela culpa na ponta da orelha direita daquele que será purificado, no polegar da sua mão direita e no polegar do seu pé direito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3126, 'nvi', 'Levítico', 'lv', 14, 15, 'Então o sacerdote pegará um pouco de óleo da caneca e o derramará na palma da sua própria mão esquerda,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3127, 'nvi', 'Levítico', 'lv', 14, 16, 'molhará o dedo direito no óleo que está na palma da mão esquerda, e com o dedo o aspergirá sete vezes perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3128, 'nvi', 'Levítico', 'lv', 14, 17, 'O sacerdote ainda porá um pouco do óleo restante na palma da sua mão, na ponta da orelha direita daquele que está sendo purificado, no polegar da sua mão direita e no polegar do seu pé direito, em cima do sangue da oferta pela culpa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3129, 'nvi', 'Levítico', 'lv', 14, 18, 'O óleo que restar na palma da sua mão, o sacerdote derramará sobre a cabeça daquele que está sendo purificado e fará propiciação por ele perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3130, 'nvi', 'Levítico', 'lv', 14, 19, '"Então o sacerdote sacrificará a oferta pelo pecado e fará propiciação em favor daquele que está sendo purificado da sua impureza. Depois disso, o sacerdote matará o animal do holocausto');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3131, 'nvi', 'Levítico', 'lv', 14, 20, 'e o oferecerá sobre o altar, juntamente com a oferta de cereal; e assim fará propiciação pelo ofertante, o qual estará puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3132, 'nvi', 'Levítico', 'lv', 14, 21, '"Se, todavia, for alguém pobre, sem recursos para isso, pegará um cordeiro como oferta pela culpa para ser movido para fazer propiciação por ele, juntamente com um jarro da melhor farinha, amassada com óleo, como oferta de cereal; uma caneca de óleo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3133, 'nvi', 'Levítico', 'lv', 14, 22, 'e duas rolinhas ou dois pombinhos, os quais ele tenha condições de ofertar, um como oferta pelo pecado e o outro como holocausto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3134, 'nvi', 'Levítico', 'lv', 14, 23, '"No oitavo dia ele os trará ao sacerdote, para a sua purificação, à entrada da Tenda do Encontro, perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3135, 'nvi', 'Levítico', 'lv', 14, 24, 'O sacerdote pegará o cordeiro da oferta pela culpa, juntamente com uma caneca de óleo, e os moverá perante o Senhor como gesto ritual de apresentação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3136, 'nvi', 'Levítico', 'lv', 14, 25, 'Matará o cordeiro da oferta pela culpa e pegará um pouco do sangue e o porá na ponta da orelha direita daquele que está sendo purificado, no polegar da sua mão direita e no polegar do seu pé direito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3137, 'nvi', 'Levítico', 'lv', 14, 26, 'O sacerdote derramará um pouco do óleo na palma da sua mão esquerda,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3138, 'nvi', 'Levítico', 'lv', 14, 27, 'e com o dedo indicador direito aspergirá um pouco do óleo da palma da sua mão esquerda sete vezes perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3139, 'nvi', 'Levítico', 'lv', 14, 28, 'Ele porá o óleo da palma da sua mão nos mesmos lugares em que pôs o sangue da oferta pela culpa: na ponta da orelha direita daquele que está sendo purificado, no polegar da sua mão direita e no polegar do seu pé direito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3140, 'nvi', 'Levítico', 'lv', 14, 29, 'O que restar do óleo na palma da sua mão, o sacerdote derramará sobre a cabeça daquele que está sendo purificado, para fazer propiciação por ele perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3141, 'nvi', 'Levítico', 'lv', 14, 30, 'Depois sacrificará uma das rolinhas ou um dos pombinhos, os quais a pessoa tem condições de ofertar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3142, 'nvi', 'Levítico', 'lv', 14, 31, 'um como oferta pelo pecado e o outro como holocausto, juntamente com a oferta de cereal. Assim o sacerdote fará propiciação perante o Senhor em favor daquele que está sendo purificado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3143, 'nvi', 'Levítico', 'lv', 14, 32, 'Essa é a regulamentação para todo aquele que tem lepra e não tem recursos para fazer a oferta da sua purificação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3144, 'nvi', 'Levítico', 'lv', 14, 33, 'O Senhor disse a Moisés e a Arão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3145, 'nvi', 'Levítico', 'lv', 14, 34, '"Quando vocês entrarem na terra de Canaã, que lhes dou como propriedade, e eu puser mancha de mofo numa casa, na terra que lhes pertence,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3146, 'nvi', 'Levítico', 'lv', 14, 35, 'o dono da casa irá ao sacerdote e dirá: ‘Parece-me que há mancha de mofo em minha casa’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3147, 'nvi', 'Levítico', 'lv', 14, 36, 'Antes de examinar o mofo, o sacerdote ordenará que desocupem a casa para que nada que houver na casa se torne impuro. Depois disso, o sacerdote irá examinar a casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3148, 'nvi', 'Levítico', 'lv', 14, 37, 'Examinará as manchas nas paredes, e, se elas forem esverdeadas ou avermelhadas e parecerem mais profundas do que a superfície da parede,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3149, 'nvi', 'Levítico', 'lv', 14, 38, 'o sacerdote sairá da casa e a deixará fechada por sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3150, 'nvi', 'Levítico', 'lv', 14, 39, 'No sétimo dia voltará para examinar a casa. Se as manchas se houverem espalhado pelas paredes da casa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3151, 'nvi', 'Levítico', 'lv', 14, 40, 'ordenará que as pedras contaminadas pelas manchas sejam retiradas e jogadas num local impuro, fora da cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3152, 'nvi', 'Levítico', 'lv', 14, 41, 'Fará que a casa seja raspada por dentro e que o reboco raspado seja jogado num local impuro, fora da cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3153, 'nvi', 'Levítico', 'lv', 14, 42, 'Depois colocarão outras pedras no lugar das primeiras, e rebocarão a casa com barro novo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3154, 'nvi', 'Levítico', 'lv', 14, 43, '"Se as manchas tornarem a alastrar-se na casa depois de retiradas as pedras e de raspada e rebocada a casa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3155, 'nvi', 'Levítico', 'lv', 14, 44, 'o sacerdote irá examiná-la, e, se as manchas se espalharam pela casa, é mofo corrosivo; a casa está impura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3156, 'nvi', 'Levítico', 'lv', 14, 45, 'Ela terá que ser demolida: as pedras, as madeiras e todo o reboco da casa; tudo será levado para um local impuro, fora da cidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3157, 'nvi', 'Levítico', 'lv', 14, 46, '"Quem entrar na casa enquanto estiver fechada estará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3158, 'nvi', 'Levítico', 'lv', 14, 47, 'Aquele que dormir ou comer na casa terá que lavar as suas roupas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3159, 'nvi', 'Levítico', 'lv', 14, 48, '"Mas, se o sacerdote for examiná-la e as manchas não se houverem espalhado depois de rebocada a casa, declarará pura a casa, pois as manchas de mofo desapareceram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3160, 'nvi', 'Levítico', 'lv', 14, 49, 'Para purificar a casa, ele pegará duas aves, um pedaço de madeira de cedro, um pano vermelho e hissopo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3161, 'nvi', 'Levítico', 'lv', 14, 50, 'Depois matará uma das aves numa vasilha de barro com água da fonte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3162, 'nvi', 'Levítico', 'lv', 14, 51, 'Então pegará o pedaço de madeira de cedro, o hissopo, o pano vermelho e a ave viva, e os molhará no sangue da ave morta e na água da fonte, e aspergirá a casa sete vezes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3163, 'nvi', 'Levítico', 'lv', 14, 52, 'Ele purificará a casa com o sangue da ave, com a água da fonte, com a ave viva, com o pedaço de madeira de cedro, com o hissopo e com o pano vermelho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3164, 'nvi', 'Levítico', 'lv', 14, 53, 'Depois soltará a ave viva em campo aberto, fora da cidade. Assim fará propiciação pela casa, que ficará pura".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3165, 'nvi', 'Levítico', 'lv', 14, 54, 'Essa é a regulamentação acerca de qualquer tipo de lepra, de sarna,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3166, 'nvi', 'Levítico', 'lv', 14, 55, 'de mofo nas roupas ou numa casa');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3167, 'nvi', 'Levítico', 'lv', 14, 56, 'e de inchaço, erupção ou mancha brilhante,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3168, 'nvi', 'Levítico', 'lv', 14, 57, 'para se determinar quando uma coisa é pura ou impura. Essa é a regulamentação acerca de qualquer tipo de lepra e de mofo.');
