INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3169, 'nvi', 'Levítico', 'lv', 15, 1, 'O Senhor disse a Moisés e a Arão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3170, 'nvi', 'Levítico', 'lv', 15, 2, '"Digam o seguinte aos israelitas: Quando um homem tiver um fluxo que sai do corpo, o fluxo é impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3171, 'nvi', 'Levítico', 'lv', 15, 3, 'Ele ficará impuro por causa do seu fluxo, quer continue, quer fique retido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3172, 'nvi', 'Levítico', 'lv', 15, 4, '"A cama em que um homem com fluxo se deitar ficará impura, e qualquer coisa em que se sentar ficará impura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3173, 'nvi', 'Levítico', 'lv', 15, 5, 'Quem tocar na cama dele, lavará as suas roupas e se banhará com água, e ficará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3174, 'nvi', 'Levítico', 'lv', 15, 6, 'Todo aquele que se sentar sobre qualquer coisa na qual esse homem se sentou, lavará suas roupas e se banhará com água, e estará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3175, 'nvi', 'Levítico', 'lv', 15, 7, '"Quem tocar no homem que tiver um fluxo lavará as suas roupas e se banhará com água, e ficará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3176, 'nvi', 'Levítico', 'lv', 15, 8, '"Se o homem cuspir em alguém que está puro, este lavará as suas roupas e se banhará com água, e ficará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3177, 'nvi', 'Levítico', 'lv', 15, 9, 'Tudo aquilo em que o homem se sentar quando montar um animal estará impuro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3178, 'nvi', 'Levítico', 'lv', 15, 10, 'e todo aquele que tocar em qualquer coisa que tenha estado debaixo dele ficará impuro até à tarde; quem pegar essas coisas lavará as suas roupas e se banhará com água, e ficará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3179, 'nvi', 'Levítico', 'lv', 15, 11, '"Qualquer pessoa em quem o homem com fluxo tocar sem lavar as mãos, lavará as suas roupas e se banhará com água, e ficará impura até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3180, 'nvi', 'Levítico', 'lv', 15, 12, '"A vasilha de barro na qual ele tocar será quebrada; se tocar numa vasilha de madeira, ela será lavada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3181, 'nvi', 'Levítico', 'lv', 15, 13, '"Quando um homem sarar de seu fluxo, contará sete dias para a sua purificação; lavará as suas roupas e se banhará em água corrente, e ficará puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3182, 'nvi', 'Levítico', 'lv', 15, 14, 'No oitavo dia pegará duas rolinhas ou dois pombinhos e irá perante o Senhor, à entrada da Tenda do Encontro, e os dará ao sacerdote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3183, 'nvi', 'Levítico', 'lv', 15, 15, 'O sacerdote os sacrificará, um como oferta pelo pecado e o outro como holocausto, e assim fará propiciação perante o Senhor em favor do homem por causa do fluxo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3184, 'nvi', 'Levítico', 'lv', 15, 16, '"Quando de um homem sair o sêmen, banhará todo o seu corpo com água, e ficará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3185, 'nvi', 'Levítico', 'lv', 15, 17, 'Qualquer peça de roupa ou de couro em que houver sêmen será lavada com água, e ficará impura até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3186, 'nvi', 'Levítico', 'lv', 15, 18, '"Quando um homem se deitar com uma mulher e lhe sair o sêmen, ambos terão que se banhar com água, e estarão impuros até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3187, 'nvi', 'Levítico', 'lv', 15, 19, '"Quando uma mulher tiver fluxo de sangue que sai do corpo, a impureza da sua menstruação durará sete dias, e quem nela tocar ficará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3188, 'nvi', 'Levítico', 'lv', 15, 20, '"Tudo sobre o que ela se deitar durante a sua menstruação ficará impuro, e tudo sobre o que ela se sentar ficará impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3189, 'nvi', 'Levítico', 'lv', 15, 21, 'Todo aquele que tocar em sua cama lavará as suas roupas e se banhará com água, e ficará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3190, 'nvi', 'Levítico', 'lv', 15, 22, 'Quem tocar em alguma coisa sobre a qual ela se sentar lavará as suas roupas e se banhará com água, e estará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3191, 'nvi', 'Levítico', 'lv', 15, 23, 'Quer seja a cama, quer seja qualquer coisa sobre a qual ela esteve sentada, quando alguém nisso tocar estará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3192, 'nvi', 'Levítico', 'lv', 15, 24, '"Se um homem se deitar com ela e a menstruação dela nele tocar, estará impuro por sete dias; qualquer cama sobre a qual ele se deitar estará impura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3193, 'nvi', 'Levítico', 'lv', 15, 25, '"Quando uma mulher tiver um fluxo de sangue por muitos dias fora da sua menstruação normal, ou um fluxo que continue além desse período, ela ficará impura enquanto durar o corrimento, como nos dias da sua menstruação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3194, 'nvi', 'Levítico', 'lv', 15, 26, 'Qualquer cama em que ela se deitar enquanto continuar o seu fluxo estará impura, como acontece com a sua cama durante a sua menstruação, e tudo sobre o que ela se sentar estará impuro, como durante a sua menstruação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3195, 'nvi', 'Levítico', 'lv', 15, 27, 'Quem tocar em alguma dessas coisas ficará impuro; lavará as suas roupas e se banhará com água, e ficará impuro até à tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3196, 'nvi', 'Levítico', 'lv', 15, 28, '"Quando sarar do seu fluxo, contará sete dias, e depois disso estará pura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3197, 'nvi', 'Levítico', 'lv', 15, 29, 'No oitavo dia pegará duas rolinhas ou dois pombinhos e os levará ao sacerdote, à entrada da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3198, 'nvi', 'Levítico', 'lv', 15, 30, 'O sacerdote sacrificará um como oferta pelo pecado e o outro como holocausto, e assim fará propiciação em favor dela, perante o Senhor, devido à impureza do seu fluxo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3199, 'nvi', 'Levítico', 'lv', 15, 31, '"Mantenham os israelitas separados das coisas que os tornam impuros, para que não morram por contaminar com sua impureza o meu tabernáculo, que está entre eles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3200, 'nvi', 'Levítico', 'lv', 15, 32, 'Essa é a regulamentação acerca do homem que tem fluxo e daquele de quem sai o sêmen, tornando-se impuro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3201, 'nvi', 'Levítico', 'lv', 15, 33, 'da mulher em sua menstruação, do homem ou da mulher que têm fluxo e do homem que se deita com uma mulher que está impura.');
