INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3202, 'nvi', 'Levítico', 'lv', 16, 1, 'O Senhor falou com Moisés depois que morreram os dois filhos de Arão, por haverem se aproximado do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3203, 'nvi', 'Levítico', 'lv', 16, 2, 'O Senhor disse a Moisés: "Diga a seu irmão Arão que não entre a toda hora no Lugar Santíssimo, atrás do véu, diante da tampa da arca, para que não morra; pois aparecerei na nuvem, acima da tampa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3204, 'nvi', 'Levítico', 'lv', 16, 3, '"Arão deverá entrar no Lugar Santo com um novilho como oferta pelo pecado e com um carneiro como holocausto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3205, 'nvi', 'Levítico', 'lv', 16, 4, 'Ele vestirá a túnica sagrada de linho, com calções também de linho por baixo; porá o cinto de linho na cintura e também o turbante de linho. Essas vestes são sagradas; por isso ele se banhará com água antes de vesti-las.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3206, 'nvi', 'Levítico', 'lv', 16, 5, 'Receberá da comunidade de Israel dois bodes como oferta pelo pecado e um carneiro como holocausto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3207, 'nvi', 'Levítico', 'lv', 16, 6, '"Arão sacrificará o novilho como oferta pelo seu próprio pecado para fazer propiciação por si mesmo e por sua família.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3208, 'nvi', 'Levítico', 'lv', 16, 7, 'Depois pegará os dois bodes e os apresentará ao Senhor, à entrada da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3209, 'nvi', 'Levítico', 'lv', 16, 8, 'E tirará sortes quanto aos dois bodes: uma para o Senhor e a outra para Azazel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3210, 'nvi', 'Levítico', 'lv', 16, 9, 'Arão trará o bode cuja sorte caiu para o Senhor e o sacrificará como oferta pelo pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3211, 'nvi', 'Levítico', 'lv', 16, 10, 'Mas o bode sobre o qual caiu a sorte para Azazel será apresentado vivo ao Senhor para se fazer propiciação e será enviado para Azazel no deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3212, 'nvi', 'Levítico', 'lv', 16, 11, '"Arão trará o novilho como oferta por seu próprio pecado para fazer propiciação por si mesmo e por sua família, e ele o oferecerá como sacrifício pelo seu próprio pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3213, 'nvi', 'Levítico', 'lv', 16, 12, 'Pegará o incensário cheio de brasas do altar que está perante o Senhor e dois punhados de incenso aromático em pó, e os levará para trás do véu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3214, 'nvi', 'Levítico', 'lv', 16, 13, 'Porá o incenso no fogo perante o Senhor, e a fumaça do incenso cobrirá a tampa que está acima das tábuas da aliança, a fim de que não morra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3215, 'nvi', 'Levítico', 'lv', 16, 14, 'Pegará um pouco do sangue do novilho e com o dedo o aspergirá sobre a parte da frente da tampa; depois, com o dedo aspergirá o sangue sete vezes, diante da tampa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3216, 'nvi', 'Levítico', 'lv', 16, 15, '"Então sacrificará o bode da oferta pelo pecado, em favor do povo, e trará o sangue para trás do véu; fará com o sangue o que fez com o sangue do novilho; ele o aspergirá sobre a tampa e na frente dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3217, 'nvi', 'Levítico', 'lv', 16, 16, 'Assim fará propiciação pelo Lugar Santíssimo por causa das impurezas e das rebeliões dos israelitas, quaisquer que tenham sido os seus pecados. Fará o mesmo em favor da Tenda do Encontro, que está entre eles no meio das suas impurezas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3218, 'nvi', 'Levítico', 'lv', 16, 17, 'Ninguém estará na Tenda do Encontro quando ele entrar para fazer propiciação no Lugar Santíssimo, até a saída de Arão, depois que fizer propiciação por si mesmo, por sua família e por toda a assembléia de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3219, 'nvi', 'Levítico', 'lv', 16, 18, '"Depois irá ao altar que está perante o Senhor e pelo altar fará propiciação. Pegará um pouco do sangue do novilho e do sangue do bode e o porá em todas as pontas do altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3220, 'nvi', 'Levítico', 'lv', 16, 19, 'Com o dedo aspergirá o sangue sete vezes sobre o altar para purificá-lo e santificá-lo das impurezas dos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3221, 'nvi', 'Levítico', 'lv', 16, 20, '"Quando Arão terminar de fazer propiciação pelo Lugar Santíssimo, pela Tenda do Encontro e pelo altar, trará para a frente o bode vivo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3222, 'nvi', 'Levítico', 'lv', 16, 21, 'Então colocará as duas mãos sobre a cabeça do bode vivo e confessará todas as iniqüidades e rebeliões dos israelitas, todos os seus pecados, e os porá sobre a cabeça do bode. Em seguida enviará o bode para o deserto aos cuidados de um homem designado para isso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3223, 'nvi', 'Levítico', 'lv', 16, 22, 'O bode levará consigo todas as iniqüidades deles para um lugar solitário. E o homem soltará o bode no deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3224, 'nvi', 'Levítico', 'lv', 16, 23, '"Depois Arão entrará na Tenda do Encontro, tirará as vestes de linho que usou para entrar no Lugar Santíssimo e as deixará ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3225, 'nvi', 'Levítico', 'lv', 16, 24, 'Ele se banhará com água num lugar sagrado e vestirá as suas próprias roupas. Então sairá e sacrificará o holocausto por si mesmo e o holocausto pelo povo, para fazer propiciação por si mesmo e pelo povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3226, 'nvi', 'Levítico', 'lv', 16, 25, 'Também queimará sobre o altar a gordura da oferta pelo pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3227, 'nvi', 'Levítico', 'lv', 16, 26, '"Aquele que soltar o bode para Azazel lavará as suas roupas e se banhará com água, e depois poderá entrar no acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3228, 'nvi', 'Levítico', 'lv', 16, 27, 'O novilho e o bode da oferta pelo pecado, cujo sangue foi trazido ao Lugar Santíssimo para fazer propiciação, serão levados para fora do acampamento; o couro, a carne e o excremento deles serão queimados com fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3229, 'nvi', 'Levítico', 'lv', 16, 28, 'Aquele que os queimar lavará as suas roupas e se banhará com água; depois poderá entrar no acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3230, 'nvi', 'Levítico', 'lv', 16, 29, '"Este é um decreto perpétuo para vocês: No décimo dia do sétimo mês vocês se humilharão a si mesmos e não poderão realizar trabalho algum, nem o natural da terra, nem o estrangeiro residente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3231, 'nvi', 'Levítico', 'lv', 16, 30, 'Porquanto nesse dia se fará propiciação por vocês, para purificá-los. Então, perante o Senhor, vocês estarão puros de todos os seus pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3232, 'nvi', 'Levítico', 'lv', 16, 31, 'Este lhes será um sábado de descanso, quando vocês se humilharão; é um decreto perpétuo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3233, 'nvi', 'Levítico', 'lv', 16, 32, 'O sacerdote que for ungido e ordenado para suceder seu pai como sumo sacerdote fará a propiciação. Porá as vestes sagradas de linho');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3234, 'nvi', 'Levítico', 'lv', 16, 33, 'e fará propiciação pelo Lugar Santíssimo, pela Tenda do Encontro, pelo altar, por todos os sacerdotes e por todo o povo da assembléia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3235, 'nvi', 'Levítico', 'lv', 16, 34, '"Este é um decreto perpétuo para vocês: A propiciação será feita uma vez por ano, por todos os pecados dos israelitas". E tudo foi feito conforme o Senhor tinha ordenado a Moisés.');
