INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3252, 'nvi', 'Levítico', 'lv', 18, 1, 'Disse o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3253, 'nvi', 'Levítico', 'lv', 18, 2, '"Diga o seguinte aos israelitas: Eu sou o Senhor, o Deus de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3254, 'nvi', 'Levítico', 'lv', 18, 3, 'Não procedam como se procede no Egito, onde vocês moraram, nem como se procede na terra de Canaã, para onde os estou levando. Não sigam as suas práticas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3255, 'nvi', 'Levítico', 'lv', 18, 4, 'Pratiquem as minhas ordenanças, obedeçam aos meus decretos e sigam-nos. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3256, 'nvi', 'Levítico', 'lv', 18, 5, 'Obedeçam aos meus decretos e ordenanças, pois o homem que os praticar viverá por eles. Eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3257, 'nvi', 'Levítico', 'lv', 18, 6, '"Ninguém poderá se aproximar de uma parenta próxima para se envolver sexualmente com ela. Eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3258, 'nvi', 'Levítico', 'lv', 18, 7, '"Não desonre o seu pai, envolvendo-se sexualmente com a sua mãe. Ela é sua mãe; não se envolva sexualmente com ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3259, 'nvi', 'Levítico', 'lv', 18, 8, '"Não se envolva sexualmente com a mulher do seu pai; isso desonraria seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3260, 'nvi', 'Levítico', 'lv', 18, 9, '"Não se envolva sexualmente com a sua irmã, filha do seu pai ou da sua mãe, tenha ela nascido na mesma casa ou em outro lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3261, 'nvi', 'Levítico', 'lv', 18, 10, '"Não se envolva sexualmente com a filha do seu filho ou com a filha da sua filha; isso desonraria você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3262, 'nvi', 'Levítico', 'lv', 18, 11, '"Não se envolva sexualmente com a filha da mulher do seu pai, gerada por seu pai; ela é sua irmã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3263, 'nvi', 'Levítico', 'lv', 18, 12, '"Não se envolva sexualmente com a irmã do seu pai; ela é parenta próxima do seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3264, 'nvi', 'Levítico', 'lv', 18, 13, '"Não se envolva sexualmente com a irmã da sua mãe; ela é parenta próxima da sua mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3265, 'nvi', 'Levítico', 'lv', 18, 14, '"Não desonre o irmão do seu pai aproximando-se da sua mulher para com ela se envolver sexualmente; ela é sua tia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3266, 'nvi', 'Levítico', 'lv', 18, 15, '"Não se envolva sexualmente com a sua nora. Ela é mulher do seu filho; não se envolva sexualmente com ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3267, 'nvi', 'Levítico', 'lv', 18, 16, '"Não se envolva sexualmente com a mulher do seu irmão; isso desonraria seu irmão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3268, 'nvi', 'Levítico', 'lv', 18, 17, '"Não se envolva sexualmente com uma mulher e sua filha. Não se envolva sexualmente com a filha do seu filho ou com a filha da sua filha; são parentes próximos. É perversidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3269, 'nvi', 'Levítico', 'lv', 18, 18, '"Não tome por mulher a irmã da sua mulher, tornando-a rival, envolvendo-se sexualmente com ela, estando a sua mulher ainda viva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3270, 'nvi', 'Levítico', 'lv', 18, 19, '"Não se aproxime de uma mulher para se envolver sexualmente com ela quando ela estiver na impureza da sua menstruação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3271, 'nvi', 'Levítico', 'lv', 18, 20, '"Não se deite com a mulher do seu próximo, contaminando-se com ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3272, 'nvi', 'Levítico', 'lv', 18, 21, '"Não entregue os seus filhos para serem sacrificados a Moloque. Não profanem o nome do seu Deus. Eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3273, 'nvi', 'Levítico', 'lv', 18, 22, '"Não se deite com um homem como quem se deita com uma mulher; é repugnante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3274, 'nvi', 'Levítico', 'lv', 18, 23, '"Não tenha relações sexuais com um animal, contaminando-se com ele. Mulher nenhuma se porá diante de um animal para ajuntar-se com ele; é depravação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3275, 'nvi', 'Levítico', 'lv', 18, 24, '"Não se contaminem com nenhuma dessas coisas, porque assim se contaminaram as nações que vou expulsar da presença de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3276, 'nvi', 'Levítico', 'lv', 18, 25, 'Até a terra ficou contaminada; e eu castiguei a sua iniqüidade, e a terra vomitou os seus habitantes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3277, 'nvi', 'Levítico', 'lv', 18, 26, 'Mas vocês obedecerão aos meus decretos e às minhas leis. Nem o natural da terra nem o estrangeiro residente entre vocês farão nenhuma dessas abominações,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3278, 'nvi', 'Levítico', 'lv', 18, 27, 'pois todas estas abominações foram praticadas pelos que habitaram essa terra antes de vocês; por isso a terra ficou contaminada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3279, 'nvi', 'Levítico', 'lv', 18, 28, 'E, se vocês contaminarem a terra, ela os vomitará, como vomitou os povos que ali estavam antes de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3280, 'nvi', 'Levítico', 'lv', 18, 29, '"Todo aquele que fizer alguma destas abominações, aqueles que assim procederem serão eliminados do meio do seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3281, 'nvi', 'Levítico', 'lv', 18, 30, 'Obedeçam aos meus preceitos, e não pratiquem os costumes repugnantes praticados antes de vocês, nem se contaminem com eles. Eu sou o Senhor, o Deus de vocês".');
