INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3282, 'nvi', 'Levítico', 'lv', 19, 1, 'Disse ainda o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3283, 'nvi', 'Levítico', 'lv', 19, 2, '"Diga o seguinte a toda comunidade de Israel: Sejam santos porque eu, o Senhor, o Deus de vocês, sou santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3284, 'nvi', 'Levítico', 'lv', 19, 3, '"Respeite cada um de vocês a sua mãe e o seu pai, e guarde os meus sábados. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3285, 'nvi', 'Levítico', 'lv', 19, 4, '"Não se voltem para os ídolos, nem façam para si deuses de metal. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3286, 'nvi', 'Levítico', 'lv', 19, 5, '"Quando vocês oferecerem um sacrifício de comunhão ao Senhor, ofereçam-no de modo que seja aceito em favor de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3287, 'nvi', 'Levítico', 'lv', 19, 6, 'Terá que ser comido no dia em que o oferecerem, ou no dia seguinte; o que sobrar até ao terceiro dia será queimado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3288, 'nvi', 'Levítico', 'lv', 19, 7, 'Se alguma coisa for comida no terceiro dia, estará estragada e não será aceita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3289, 'nvi', 'Levítico', 'lv', 19, 8, 'Quem comer sofrerá as conseqüências da sua iniqüidade, porque profanou o que é santo ao Senhor; será eliminado do meio do seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3290, 'nvi', 'Levítico', 'lv', 19, 9, '"Quando fizerem a colheita da sua terra, não colham até às extremidades da sua lavoura, nem ajuntem as espigas caídas de sua colheita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3291, 'nvi', 'Levítico', 'lv', 19, 10, 'Não passem duas vezes pela sua vinha, nem apanhem as uvas que tiverem caído. Deixem-nas para o necessitado e para o estrangeiro. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3292, 'nvi', 'Levítico', 'lv', 19, 11, '"Não furtem. "Não mintam. "Não enganem uns aos outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3293, 'nvi', 'Levítico', 'lv', 19, 12, '"Não jurem falsamente pelo meu nome, profanando assim o nome do seu Deus. Eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3294, 'nvi', 'Levítico', 'lv', 19, 13, '"Não oprimam nem roubem o seu próximo. "Não retenham até a manhã do dia seguinte o pagamento de um diarista.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3295, 'nvi', 'Levítico', 'lv', 19, 14, '"Não amaldiçoem o surdo nem ponham pedra de tropeço à frente do cego, mas tema o seu Deus. Eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3296, 'nvi', 'Levítico', 'lv', 19, 15, '"Não cometam injustiça num julgamento; não favoreçam os pobres, nem procurem agradar os grandes, mas julguem o seu próximo com justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3297, 'nvi', 'Levítico', 'lv', 19, 16, '"Não espalhem calúnias entre o seu povo. "Não se levantem contra a vida do seu próximo. Eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3298, 'nvi', 'Levítico', 'lv', 19, 17, '"Não guardem ódio contra o seu irmão no coração; antes repreendam com franqueza o seu próximo para que, por causa dele, não sofram as conseqüências de um pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3299, 'nvi', 'Levítico', 'lv', 19, 18, '"Não procurem vingança, nem guardem rancor contra alguém do seu povo, mas ame cada um o seu próximo como a si mesmo. Eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3300, 'nvi', 'Levítico', 'lv', 19, 19, '"Obedeçam às minhas leis. "Não cruzem diferentes espécies de animais. "Não plantem duas espécies de sementes na sua lavoura. "Não usem roupas feitas com dois tipos de tecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3301, 'nvi', 'Levítico', 'lv', 19, 20, '"Se um homem deitar-se com uma escrava prometida a outro homem, mas que não tenha sido resgatada nem recebido sua liberdade, aplique-se a devida punição. Contudo não serão mortos, porquanto ela não havia sido libertada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3302, 'nvi', 'Levítico', 'lv', 19, 21, 'O homem, porém, trará ao Senhor, à entrada da Tenda do Encontro, um carneiro como oferta pela culpa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3303, 'nvi', 'Levítico', 'lv', 19, 22, 'Com o carneiro da oferta pela culpa o sacerdote fará propiciação por ele perante o Senhor, pelo pecado que cometeu; assim o pecado que ele cometeu será perdoado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3304, 'nvi', 'Levítico', 'lv', 19, 23, '"Quando vocês entrarem na terra e plantarem qualquer tipo de árvore frutífera, considerem proibidas as suas frutas. Durante três anos vocês as considerarão proibidas; não poderão comê-las.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3305, 'nvi', 'Levítico', 'lv', 19, 24, 'No quarto ano todas as suas frutas serão santas; será uma oferta de louvor ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3306, 'nvi', 'Levítico', 'lv', 19, 25, 'No quinto ano, porém, vocês poderão comer as suas frutas. Assim a sua colheita aumentará. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3307, 'nvi', 'Levítico', 'lv', 19, 26, '"Não comam nada com sangue. "Não pratiquem adivinhação nem feitiçaria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3308, 'nvi', 'Levítico', 'lv', 19, 27, '"Não cortem o cabelo dos lados da cabeça, nem aparem as pontas da barba.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3309, 'nvi', 'Levítico', 'lv', 19, 28, '"Não façam cortes em seus corpos por causa dos mortos, nem tatuagem em si mesmos. Eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3310, 'nvi', 'Levítico', 'lv', 19, 29, '"Ninguém desonre a sua filha tornando-a uma prostituta, se não a terra se entregará à prostituição e se encherá de perversidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3311, 'nvi', 'Levítico', 'lv', 19, 30, '"Guardem os meus sábados e reverenciem o meu santuário. Eu sou o Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3312, 'nvi', 'Levítico', 'lv', 19, 31, '"Não recorram aos médiuns, nem busquem os espíritas, pois vocês serão contaminados por eles. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3313, 'nvi', 'Levítico', 'lv', 19, 32, '"Levantem-se na presença dos idosos, honrem os anciãos, tema o seu Deus. Eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3314, 'nvi', 'Levítico', 'lv', 19, 33, '"Quando um estrangeiro viver na terra de vocês, não o maltratem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3315, 'nvi', 'Levítico', 'lv', 19, 34, 'O estrangeiro residente que viver com vocês será tratado como o natural da terra. Amem-no como a si mesmos, pois vocês foram estrangeiros no Egito. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3316, 'nvi', 'Levítico', 'lv', 19, 35, '"Não usem medidas desonestas quando medirem comprimento, peso ou quantidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3317, 'nvi', 'Levítico', 'lv', 19, 36, 'Usem balanças de pesos honestos, tanto para cereais quanto para líquidos. Eu sou o Senhor, o Deus de vocês, que os tirei da terra do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3318, 'nvi', 'Levítico', 'lv', 19, 37, '"Obedeçam a todos os meus decretos e a todas as minhas leis e pratiquem-nos. Eu sou o Senhor".');
