INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3346, 'nvi', 'Levítico', 'lv', 21, 1, 'Disse ainda o Senhor a Moisés: "Diga o seguinte aos sacerdotes, os filhos de Arão: Um sacerdote não poderá tornar-se impuro por causa de alguém do seu povo que venha a morrer,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3347, 'nvi', 'Levítico', 'lv', 21, 2, 'a não ser por um parente próximo, como mãe ou pai, filho ou filha, irmão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3348, 'nvi', 'Levítico', 'lv', 21, 3, 'ou irmã virgem dependente dele por ainda não ter marido; por causa dela, poderá tornar-se impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3349, 'nvi', 'Levítico', 'lv', 21, 4, 'Não poderá tornar-se impuro e contaminar-se por causa de parentes por casamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3350, 'nvi', 'Levítico', 'lv', 21, 5, '"Os sacerdotes não raparão a cabeça, nem apararão as pontas da barba, nem farão cortes no corpo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3351, 'nvi', 'Levítico', 'lv', 21, 6, 'Serão santos ao seu Deus, e não profanarão o nome do seu Deus. Pelo fato de apresentarem ao Senhor as ofertas preparadas no fogo, ofertas de alimento do seu Deus, serão santos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3352, 'nvi', 'Levítico', 'lv', 21, 7, '"Não poderão tomar por mulher uma prostituta, uma moça que tenha perdido a virgindade, ou uma mulher divorciada do seu marido, porque o sacerdote é santo ao seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3353, 'nvi', 'Levítico', 'lv', 21, 8, 'Considerem-no santo, porque ele oferece o alimento do seu Deus. Considerem-no santo, porque eu, o Senhor, que os santifico, sou santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3354, 'nvi', 'Levítico', 'lv', 21, 9, '"Se a filha de um sacerdote se corromper, tornando-se prostituta, desonra seu pai; será queimada com fogo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3355, 'nvi', 'Levítico', 'lv', 21, 10, '"O sumo sacerdote, aquele entre seus irmãos sobre cuja cabeça tiver sido derramado o óleo da unção, e que tiver sido consagrado para usar as vestes sacerdotais, não andará descabelado, nem rasgará as roupas em sinal de luto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3356, 'nvi', 'Levítico', 'lv', 21, 11, 'Não entrará onde houver um cadáver. Não se tornará impuro, nem mesmo por causa do seu pai ou da sua mãe;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3357, 'nvi', 'Levítico', 'lv', 21, 12, 'e não deixará o santuário do seu Deus, nem o profanará, porquanto foi consagrado pelo óleo da unção do seu Deus. Eu sou o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3358, 'nvi', 'Levítico', 'lv', 21, 13, '"A mulher que ele tomar terá que ser virgem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3359, 'nvi', 'Levítico', 'lv', 21, 14, 'Não poderá ser viúva, nem divorciada, nem moça que perdeu a virgindade, nem prostituta, mas terá que ser uma virgem do seu próprio povo,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3360, 'nvi', 'Levítico', 'lv', 21, 15, 'assim ele não profanará a sua descendência entre o seu povo. Eu sou o Senhor, que o santifico".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3361, 'nvi', 'Levítico', 'lv', 21, 16, 'Disse ainda o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3362, 'nvi', 'Levítico', 'lv', 21, 17, '"Diga a Arão: Pelas suas gerações, nenhum dos seus descendentes que tenha algum defeito poderá aproximar-se para trazer ao seu Deus ofertas de alimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3363, 'nvi', 'Levítico', 'lv', 21, 18, 'Nenhum homem que tenha algum defeito poderá aproximar-se: ninguém que seja cego ou aleijado, que tenha o rosto defeituoso ou o corpo deformado;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3364, 'nvi', 'Levítico', 'lv', 21, 19, 'ninguém que tenha o pé ou a mão defeituosos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3365, 'nvi', 'Levítico', 'lv', 21, 20, 'ou que seja corcunda ou anão, ou que tenha qualquer defeito na vista, ou que esteja com feridas purulentas ou com fluxo, ou que tenha testículos defeituosos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3366, 'nvi', 'Levítico', 'lv', 21, 21, 'Nenhum descendente do sacerdote Arão que tenha qualquer defeito poderá aproximar-se para apresentar ao Senhor ofertas preparadas no fogo. Tem defeito; não poderá aproximar-se para trazê-las ao seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3367, 'nvi', 'Levítico', 'lv', 21, 22, 'Poderá comer o alimento santíssimo de seu Deus, e também o alimento santo;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3368, 'nvi', 'Levítico', 'lv', 21, 23, 'contudo, por causa do seu defeito, não se aproximará do véu nem do altar, para que não profane o meu santuário. Eu sou o Senhor, que os santifico".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3369, 'nvi', 'Levítico', 'lv', 21, 24, 'Foi isso que Moisés falou a Arão e a seus filhos e a todos os israelitas.');
